INSERT INTO `news` (`id`, `name`, `description`, `detail_content`, `photo`, `hot`) VALUES
(3, 'KINH NGHIỆM TÌM NHÀ TRỌ QUẬN NAM TỪ LIÊM ĐƠN GIẢN & HIỆU QUẢ', '<p>Tại H&agrave; Nội c&oacute; rất nhiều khu vực t&igrave;m ph&ograve;ng trọ kh&aacute;c nhau, mỗi khu vực, mỗi quận đều c&oacute; một đặc điểm ri&ecirc;ng, c&aacute;c vấn đề kh&aacute;c nhau.</p>\r\n', '<h2><strong>Một số kinh nghiệm t&igrave;m ph&ograve;ng trọ quận Nam Từ Li&ecirc;m - H&agrave; Nội</strong></h2>\r\n\r\n<p>Với diện t&iacute;ch hơn 32km2 v&agrave; 10 phường, th&igrave; ch&uacute;ng ta cũng kh&ocirc;ng thể chọn một căn ph&ograve;ng trọ ở ph&iacute;a Nam quận m&agrave; trong khi đ&oacute; bạn lại học tập v&agrave; l&agrave;m việc ở ph&iacute;a Bắc của quận, việc x&aacute;c định khu vực thu&ecirc; ph&ograve;ng trọ l&agrave; kh&aacute; quan trọng.&nbsp;</p>\r\n\r\n<p>Ph&iacute;a T&acirc;y quận gi&aacute;p với huyện Ho&agrave;i Đức gần trường đại học C&ocirc;ng Nghiệp, đại học Thương Mại,.... c&aacute;c bạn sinh vi&ecirc;n c&oacute; thể thu&ecirc; phong ở Mỹ Đ&igrave;nh cũng kh&aacute; thuận tiện v&igrave; ở đ&acirc;y gần đường ch&iacute;nh v&agrave; giao th&ocirc;ng thuận tiện cho việc đi lại v&agrave; hoạt động của bản th&acirc;n.</p>\r\n\r\n<h3><strong>Với ph&iacute;a Đ&ocirc;ng, quận gi&aacute;p với c&aacute;c quận như Cầu giấy, Thanh Xu&acirc;n</strong></h3>\r\n\r\n<p>Ở đ&acirc;y c&oacute; nền kinh tế ph&aacute;t triển hơn, v&agrave; c&oacute; kh&aacute; nhiều trường đại học hơn khu ph&iacute;a T&acirc;y quận. L&agrave; nơi ph&aacute;t triển rất mạnh, gần trung t&acirc;m th&agrave;nh phố, với khu vực n&agrave;y bạn cũng c&oacute; rất nhiều sự lựa chọn ph&ograve;ng cho bản th&acirc;n. Tuy vậy, tại đ&acirc;y giao th&ocirc;ng hay tắc nghẽn v&agrave;o c&aacute;c giờ cao điểm, c&aacute;c bạn n&ecirc;n lưu &yacute; để chọn được căn ph&ograve;ng trọ ph&ugrave; hợp với bản th&acirc;n m&igrave;nh nh&eacute;.&nbsp;</p>\r\n\r\n<h3><strong>Ph&ograve;ng trọ khu ph&iacute;a Bắc quận cũng c&oacute; thể l&agrave; rất ph&aacute;t triển</strong></h3>\r\n\r\n<p>Tiếp gi&aacute;p với quận Bắc Từ Li&ecirc;m. Gần trung t&acirc;m th&agrave;nh phố nhất trong c&aacute;c ph&iacute;a của quận. Ở đ&acirc;y ph&aacute;t triển rất mạnh, tuy vậy gi&aacute; ph&ograve;ng trọ ở khu vực n&agrave;y cao hơn c&aacute;c khu vực kh&aacute;c, v&igrave; c&agrave;ng gần trung t&acirc;m th&agrave;nh phố gi&aacute; ph&ograve;ng trọ ở th&agrave;nh phố ng&agrave;y c&agrave;ng tăng.</p>\r\n\r\n<p>C&ugrave;ng đ&oacute; cũng kh&ocirc;ng thể tr&aacute;nh được việc giao th&ocirc;ng thường xuy&ecirc;n tắc nghẽn, những ưu điểm ở đ&acirc;y l&agrave; khu vực c&oacute; nền kinh tế ph&aacute;t triển, gần nhiều trường đại học ở c&aacute;c khu vực l&acirc;n cận, c&aacute;c bạn cũng c&oacute; thể tự ph&aacute;t triển bản th&acirc;n với những c&ocirc;ng việc mới, v&igrave; kh&ocirc;ng hề kh&oacute; kiếm việc l&agrave;m ở khu vực n&agrave;y.</p>\r\n\r\n<h3><strong>Cuối c&ugrave;ng l&agrave; ph&iacute;a Nam quận</strong></h3>\r\n\r\n<p>Ở đ&acirc;y tiếp gi&aacute;p với quận H&agrave; Đ&ocirc;ng, ph&ograve;ng trọ ở khu vực n&agrave;y cũng rất nhiều, kh&aacute; tốt v&agrave; đảm bảo với gi&aacute; cả kh&ocirc;ng qu&aacute; đắt, khi t&agrave;i ch&iacute;nh bạn hạn hẹp th&igrave; ph&iacute;a Nam quận cũng l&agrave; một phương &aacute;n lựa chọn kh&aacute; tốt</p>\r\n\r\n<p>Tổng quan ch&uacute;ng ta cũng c&oacute; thể thấy ở c&aacute;c ph&iacute;a quận nh&agrave; trọ cho thu&ecirc; đều kh&ocirc;ng hề &iacute;t, tuy vậy mỗi khu vực đều c&oacute; một đặc điểm ri&ecirc;ng. Ch&iacute;nh v&igrave; vậy bạn cần xem x&eacute;t kỹ c&aacute;c lợi &iacute;ch v&agrave; bất tiện ở mỗi khu vực để thu&ecirc; ph&ograve;ng.</p>\r\n\r\n<p>Khi thu&ecirc; ph&ograve;ng ở quận th&igrave; đầu ti&ecirc;n ai trong ch&uacute;ng ta cũng ch&uacute; &yacute; vấn đề vị tr&iacute;, rằng thu&ecirc; ở ph&iacute;a n&agrave;o, khu vực n&agrave;o đầu ti&ecirc;n, sau đ&oacute; ch&iacute;nh l&agrave; t&agrave;i ch&iacute;nh, c&aacute;c vấn đề phụ ph&iacute;, nội thất căn ph&ograve;ng. Tuy nhi&ecirc;n khi&nbsp;<strong>t&igrave;m ph&ograve;ng ở Nam Từ Li&ecirc;m</strong>, bạn n&ecirc;n ch&uacute; &yacute; vấn đề giao th&ocirc;ng, v&igrave; tắc nghẽn giao th&ocirc;ng c&oacute; thể l&agrave;m chậm trễ nhiều hoạt động, sinh hoạt, l&agrave;m việc v&agrave; học tập của bạn.&nbsp;</p>\r\n\r\n<p>Ở đ&acirc;y bạn cũng kh&ocirc;ng n&ecirc;n thu&ecirc; c&aacute;c căn ph&ograve;ng qu&aacute; gần mặt đường ch&iacute;nh v&igrave; rất bụi, &iacute;t kh&ocirc;ng kh&iacute; sạch, bạn n&ecirc;n ưu ti&ecirc;n thu&ecirc; c&aacute;c căn ph&ograve;ng ở trong ng&otilde; nhưng kh&ocirc;ng qu&aacute; s&acirc;u, để ch&uacute;ng c&oacute; một bầu kh&ocirc;ng kh&iacute; trong m&aacute;t hơn, c&ugrave;ng với đ&oacute; l&agrave; kh&ocirc;ng thể thiếu những nhu cầu thiết yếu của bản th&acirc;n đối với căn ph&ograve;ng như: điều h&ograve;a, n&oacute;ng lạnh, giường tủ,....</p>\r\n\r\n<p>Bạn cần xem x&eacute;t kỹ hợp đồng, tiền cọc v&agrave; c&aacute;c vấn đề về t&agrave;i ch&iacute;nh, giấy tờ một c&aacute;ch r&otilde; r&agrave;ng. Đ&acirc;y l&agrave; vấn đề chung, kh&ocirc;ng phải chỉ&nbsp;<strong>thu&ecirc; ph&ograve;ng ở quận Nam Từ Li&ecirc;m</strong>&nbsp;m&agrave; bạn cần ch&uacute; &yacute; vấn đề n&agrave;y, m&agrave; ở c&aacute;c khu vực kh&aacute;c khi thu&ecirc; ph&ograve;ng trọ c&aacute;c bạn đều phải ch&uacute; &yacute;, để tr&aacute;nh mang nhiều thiệt hại về cho bản th&acirc;n.</p>\r\n\r\n<p>Nếu bạn l&agrave; một con người sống thoải m&aacute;i, hay sống về đ&ecirc;m th&igrave; kh&ocirc;ng n&ecirc;n thu&ecirc; ph&ograve;ng trọ chung chủ hay c&oacute; giờ giới nghi&ecirc;m nh&eacute;, v&igrave; khi đấy mọi hoạt động của bạn đều bị k&igrave;m h&atilde;m bởi thời gian đ&oacute;ng cửa của ph&ograve;ng trọ.</p>\r\n', '16086964421.jpg', 1),
(4, 'KINH NGHIỆM TÌM PHÒNG TRỌ GIÁ 3TR TẠI HÀ NỘI', '<p>B&agrave;i viết n&agrave;y sẽ gi&uacute;p &iacute;ch bạn c&aacute;c vấn đề, c&aacute;ch t&igrave;m v&agrave; một số kinh nghiệm t&igrave;m ph&ograve;ng trọ c&oacute; gi&aacute; 3tr tại H&agrave; Nội.&nbsp;</p>\r\n', '<h2><strong>Chia sẻ Kinh nghiệm t&igrave;m ph&ograve;ng trọ gi&aacute; 3tr tại H&agrave; Nội</strong></h2>\r\n\r\n<p>Khi bắt đầu bước ch&acirc;n v&agrave;o th&agrave;nh phố, ai cũng sẽ c&oacute; cảm gi&aacute;c kh&aacute; hoang mang v&agrave; lo lắng, vấn đề đầu ti&ecirc;n m&agrave; ai trong ch&uacute;ng ta cũng sẽ nghĩ đến đ&oacute; ch&iacute;nh l&agrave; vấn đề ở, nơi tạm tr&uacute; tạm thời.</p>\r\n\r\n<p>Với c&aacute;c bạn sinh vi&ecirc;n mới l&ecirc;n H&agrave; Nội điều n&agrave;y sẽ rất kh&oacute; khăn, c&aacute;c bạn n&ecirc;n nhờ những người th&acirc;n quen, gia đ&igrave;nh, &hellip;. của m&igrave;nh để c&ugrave;ng t&igrave;m kiếm để giảm c&aacute;c vấn đề lạc, mất thời gian, v&agrave; t&igrave;m được ph&ograve;ng trọ nhanh nhất.</p>\r\n\r\n<h3><strong>1.1: X&aacute;c định đ&uacute;ng khu vực t&igrave;m trọ mong muốn</strong></h3>\r\n\r\n<p>Điều n&agrave;y rất quan trọng với ch&uacute;ng ta, v&igrave; thu&ecirc; trọ l&agrave; để gi&uacute;p ch&uacute;ng ta thuận tiện trong việc đi lại để học tập v&agrave; l&agrave;m việc. Ch&iacute;nh v&igrave; vậy, bạn phải chọn một căn ph&ograve;ng trọ c&oacute; vị tr&iacute; địa l&iacute; ph&ugrave; hợp với nhu cầu của m&igrave;nh c&ugrave;ng với t&agrave;i ch&iacute;nh của bản th&acirc;n, sau đ&oacute; ch&iacute;nh l&agrave; bạn n&ecirc;n ưu ti&ecirc;n chọn c&aacute;c căn ph&ograve;ng trọ gần đường ch&iacute;nh, c&aacute;c khu chợ, si&ecirc;u thị,.... để c&oacute; dễ d&agrave;ng sinh hoạt hơn.</p>\r\n\r\n<h3><strong>1.2: T&igrave;m hiểu kỹ về c&aacute;c vấn đề t&agrave;i ch&iacute;nh&nbsp;</strong></h3>\r\n\r\n<p>Bạn cần biết r&otilde; rằng khi&nbsp;<strong>thu&ecirc; ph&ograve;ng trọ với gi&aacute; 3tr</strong>&nbsp;l&agrave; chưa hề c&oacute; c&aacute;c phụ ph&iacute; như điện, nước, vệ sinh,.... c&oacute; thể l&ecirc;n đến tầm 3tr5 đến 3trr7. Khi thu&ecirc; ph&ograve;ng trọ với t&agrave;i ch&iacute;nh l&agrave; 3tr th&igrave; tại th&agrave;nh phố H&agrave; Nội kh&ocirc;ng hề &iacute;t, nhưng nhiều bạn lại kh&ocirc;ng hỏi kỹ c&aacute;c vấn đề phụ ph&iacute;, tiền cọc,... đ&atilde; gi&uacute;p cho chủ nh&agrave; trọ &ldquo;ăn lời&rdquo; rất nhiều.</p>\r\n\r\n<p><em>Nhiều trường hợp đ&atilde; xảy ra, như mất tiền cọc, gi&aacute; điện nước &ldquo;tr&ecirc;n trời&rdquo;,... mang lại nhiều thiệt hại về cho bản th&acirc;n. C&ugrave;ng với đ&oacute; bạn cần xem x&eacute;t kỹ c&aacute;c đồ d&ugrave;ng, thiết bị trong ph&ograve;ng.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với gi&aacute; 3tr th&igrave; một căn ph&ograve;ng c&oacute; điều h&ograve;a, n&oacute;ng lạnh, kệ bếp, thiết bị vệ sinh, vệ sinh kh&eacute;p k&iacute;n.... l&agrave; điều c&oacute; thể ai cũng cần thiết nhưng ch&uacute;ng ta cần xem x&eacute;t n&oacute; c&oacute; bị hỏng h&oacute;c, lỗi, &hellip;. để phản &aacute;nh với chủ nh&agrave; trước khi chuyển v&agrave;o ở, tr&aacute;nh việc khi chuyển v&agrave;o ch&uacute;ng ta lại phải l&agrave; người chi trả cho sửa chữa thiết bị trong ph&ograve;ng m&agrave; bạn chưa hề được sử dụng, c&aacute;i n&agrave;y l&agrave; trường hợp chung kh&ocirc;ng phải do chủ nh&agrave; m&agrave; c&oacute; thể do người thu&ecirc; cũ l&agrave;m hỏng m&agrave; kh&ocirc;ng b&aacute;o với chủ nh&agrave; n&ecirc;n c&aacute;c bạn cần ch&uacute; &yacute; thật kỹ để kh&ocirc;ng bị thiệt hại nhiều về t&agrave;i ch&iacute;nh.</p>\r\n\r\n<h3><strong>1.3: Ch&uacute; &yacute; c&aacute;c vấn đề an ninh, đảm bảo an to&agrave;n</strong></h3>\r\n\r\n<p>Khi thu&ecirc; ph&ograve;ng trọ vấn đề đảm bảo an ninh, an to&agrave;n l&agrave; rất quan trọng nhất l&agrave; đối với c&aacute;c bạn sinh vi&ecirc;n ở hiện nay.</p>\r\n\r\n<p>Ở c&aacute;c khu trọ, c&oacute; rất nhiều trường hợp xấu xảy ra, khi kh&ocirc;ng c&oacute; người th&acirc;n b&ecirc;n cạnh th&igrave; c&aacute;c bạn cần ch&uacute; &yacute; an to&agrave;n cho bản th&acirc;n, c&aacute;c bạn n&ecirc;n chọn c&aacute;c khu trọ c&oacute; an ninh, t&ograve;a nh&agrave; được lắp camera đầy đủ, b&igrave;nh chữa ch&aacute;y, &hellip;.</p>\r\n\r\n<p>Khi xem ph&ograve;ng bạn c&oacute; thể hỏi nh&agrave; người d&acirc;n xung quanh về c&aacute;c vấn đề an ninh, v&agrave; vệ sinh, an to&agrave;n,.... để đảm bảo rằng m&igrave;nh chọn được căn ph&ograve;ng trọ gi&aacute; tốt.</p>\r\n\r\n<h3><strong>1.4: Một số vấn đề cần lưu &yacute; kh&aacute;c</strong></h3>\r\n\r\n<p>Bạn cần xem x&eacute;t kỹ bản hợp đồng trước khi k&yacute; thu&ecirc; ph&ograve;ng, x&aacute;c định r&otilde; tiền điện, nước, vệ sinh,.... để tr&aacute;nh bị tăng gi&aacute; v&agrave; th&ecirc;m nhiều phụ ph&iacute;, c&ugrave;ng với tiền cọc.&nbsp;</p>\r\n\r\n<p>Cần y&ecirc;u cầu chủ nh&agrave; đăng k&yacute; tạm tr&uacute;, tạm vắng. Vấn đề n&agrave;y khi đi thu&ecirc; ph&ograve;ng trong c&oacute; rất nhiều người trong ch&uacute;ng ta bỏ qua m&agrave; kh&ocirc;ng ch&uacute; &yacute;, khi kh&ocirc;ng đăng k&yacute; tạm tr&uacute; tạm vắng th&igrave; bản th&acirc;n bạn c&oacute; thể bị phạt khi bị ph&aacute;t hiện khi bạn l&agrave; người kh&ocirc;ng c&oacute; hộ khẩu ở th&agrave;nh phố H&agrave; Nội.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng n&ecirc;n chọn căn ph&ograve;ng c&oacute; qu&aacute; nhiều đồ d&ugrave;ng sử dụng điện m&agrave; qu&aacute; cũ, v&igrave; n&oacute; sẽ tốn rất nhiều tiền điện cho c&aacute;c thiết bị đ&oacute;.&nbsp;</p>\r\n', '16086970811.jpg', 1),
(5, 'TÌM PHÒNG TRỌ KHU VỰC MỸ ĐÌNH GIÁ TỐT, CHÍNH CHỦ', '<p>Bạn l&agrave; t&acirc;n sinh vi&ecirc;n mới l&ecirc;n H&agrave; Nội? Bạn muốn thu&ecirc; ph&ograve;ng trọ khu vực Mỹ Đ&igrave;nh nhưng kh&ocirc;ng biết t&igrave;m thế n&agrave;o v&agrave; bắt đầu từ đ&acirc;u?</p>\r\n', '<h2><strong>1. X&aacute;c định nhu cầu về ph&ograve;ng v&agrave; mong muốn của bản th&acirc;n</strong></h2>\r\n\r\n<p>Trước khi bắt đầu t&igrave;m ph&ograve;ng trọ, bạn phải hiểu r&otilde; được nhu cầu v&agrave; khả năng chi trả mỗi th&aacute;ng của bản th&acirc;n để t&igrave;m được căn ph&ograve;ng ph&ugrave; hợp v&agrave; tr&aacute;nh mất thời gian.&nbsp;<em>V&iacute; dụ như: Gi&aacute; ph&ograve;ng 1 th&aacute;ng m&igrave;nh c&oacute; thể chi trả l&agrave; bao nhi&ecirc;u? Ph&ograve;ng bạn mong muốn c&oacute; đủ đồ tiện nghi kh&ocirc;ng? Ph&ograve;ng c&oacute; chung chủ kh&ocirc;ng? Ph&iacute; dịch vụ l&agrave; bao nhi&ecirc;u?...</em></p>\r\n\r\n<p>Thứ hai, x&aacute;c định vị tr&iacute; bạn muốn thu&ecirc; ph&ograve;ng. L&agrave; sinh vi&ecirc;n th&igrave; n&ecirc;n thu&ecirc; ph&ograve;ng gần trường đặc biệt l&agrave; sinh vi&ecirc;n năm nhất chưa th&ocirc;ng thuộc đường phố H&agrave; Nội. Tuy nhi&ecirc;n, thường th&igrave; vị tr&iacute;, diện t&iacute;ch v&agrave; loại h&igrave;nh căn ph&ograve;ng cũng quyết định gi&aacute; thu&ecirc; của ph&ograve;ng.</p>\r\n\r\n<h2><strong>2. T&igrave;m ph&ograve;ng trọ khu vực Mỹ Đ&igrave;nh gi&aacute; tốt</strong></h2>\r\n\r\n<p>Hai c&aacute;ch t&igrave;m nh&agrave; phổ biến hiện nay l&agrave;: t&igrave;m online tr&ecirc;n c&aacute;c trang mạng x&atilde; hội v&agrave; t&igrave;m bằng c&aacute;ch khảo s&aacute;t trực tiếp.</p>\r\n\r\n<ul>\r\n	<li>T&igrave;m tr&ecirc;n mạng x&atilde; hội: Bạn c&oacute; thể đăng tin y&ecirc;u cầu t&igrave;m ph&ograve;ng tr&ecirc;n c&aacute;c trang web, group hỗ trợ v&agrave; cho thu&ecirc; ph&ograve;ng trọ hoặc chung cư mini uy t&iacute;n, ch&iacute;nh chủ như: bandon.vn , kenhnhatro.com , phongtot.vn &hellip; Ngo&agrave;i ra, bạn c&oacute; thể đăng tin trong c&aacute;c nh&oacute;m hỗ trợ t&igrave;m ph&ograve;ng c&ocirc;ng khai (VD: Phòng Trọ &amp;&nbsp;<strong>Nhà Trọ Khu Vực Mỹ Đình</strong>, &hellip;) tr&ecirc;n mạng x&atilde; hội Facebook. Chủ nh&agrave; n&agrave;o đ&aacute;p ứng nhu cầu về ph&ograve;ng của bạn sẽ chủ động b&igrave;nh luận hoặc nhắn tin cho bạn.</li>\r\n	<li>T&igrave;m ph&ograve;ng bằng c&aacute;ch khảo s&aacute;t thực tế: Đ&acirc;y l&agrave; c&aacute;ch t&igrave;m nh&agrave; truyền thống m&agrave; rất nhiều người &aacute;p dụng từ xưa tới nay. Bạn chỉ cần đi t&igrave;m xung quanh khu vực bạn muốn t&igrave;m. Nh&agrave; c&oacute; ph&ograve;ng cho thu&ecirc; sẽ treo banner về gi&aacute; cả v&agrave; th&ocirc;ng tin về ph&ograve;ng. Lưu &yacute; nhỏ l&agrave; kh&ocirc;ng n&ecirc;n t&igrave;m trong những ng&otilde; qu&aacute; nhỏ v&agrave; s&acirc;u nh&eacute;. Tuy c&aacute;ch n&agrave;y hơi mất thời gian x&iacute;u nhưng b&ugrave; lại bạn sẽ được xem ph&ograve;ng trực tiếp m&agrave; kh&ocirc;ng mất bất kỳ ch&iacute; ph&iacute; trung gian n&agrave;o.</li>\r\n</ul>\r\n\r\n<p>Với những người đi l&agrave;m c&oacute; thu nhập trung b&igrave;nh c&oacute; thể tham khảo một số căn chung cư mini tiện lợi v&agrave; đầy đủ tiện &iacute;ch hơn. Dưới đ&acirc;y l&agrave; một số&nbsp;<strong>ti&ecirc;u chuẩn chung cư mini khu vực Mỹ Đ&igrave;nh</strong>&nbsp;tại hệ thống Bản Đ&ocirc;n bạn c&oacute; thể tham khảo.</p>\r\n\r\n<ul>\r\n	<li>Diện t&iacute;ch từ 20-50m2 với c&aacute;c loại h&igrave;nh như: ph&ograve;ng 1 ngủ; ph&ograve;ng 1 ngủ, 1 kh&aacute;ch; ph&ograve;ng 1 ngủ, 1 bếp; ph&ograve;ng 1 kh&aacute;ch, 2 ngủ&hellip;</li>\r\n	<li>Điện nước: &Aacute;p dụng gi&aacute; ti&ecirc;u chuẩn nh&agrave; nước ( kh&ocirc;ng thay đổi theo thời gian nhất định )</li>\r\n	<li>Nội thất: Giường, tủ, bếp, điều h&ograve;a, n&oacute;ng lạnh, r&egrave;m cửa, thang m&aacute;y, m&aacute;y giặt&hellip;</li>\r\n	<li>An ninh: Kh&oacute;a v&acirc;n tay bảo mật ra v&agrave;o t&ograve;a nh&agrave;, camera24h, thiết bị PCCC</li>\r\n	<li>Tiện &iacute;ch: Chỗ để xe, đi lại tự do, kh&ocirc;ng chung chủ,&hellip;</li>\r\n	<li>Thủ tục giấy tờ l&agrave;m ho&agrave;n to&agrave;n miễn ph&iacute;.</li>\r\n</ul>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin v&agrave; kinh nghiệm&nbsp;<strong>thu&ecirc; chung cư mini, t&igrave;m ph&ograve;ng trọ tại khu vực Mỹ Đ&igrave;nh</strong>. Để tham khảo th&ecirc;m th&ocirc;ng tin chi tiết về c&aacute;c ph&ograve;ng trọ, chung cư mini nhiều hơn, h&atilde;y truy cập bandon.vn nh&eacute;. Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</p>\r\n', '16086971541.jpg', 1),
(6, 'CÁCH TÍNH HÓA ĐƠN TIỀN ĐIỆN ĐƠN GIẢN & CHÍNH XÁC 2020', '<p>Hiện nay, mọi người khi thu&ecirc; ph&ograve;ng trọ đều kh&ocirc;ng biết c&aacute;ch t&iacute;nh h&oacute;a đơn tiền điện của ph&ograve;ng m&igrave;nh ra sao, m&agrave; gần như chủ nh&agrave; b&aacute;o gi&aacute; v&agrave; ch&uacute;ng ta sẽ đ&oacute;ng, c&oacute; nhiều trường hợp ph&ograve;ng bị t&iacute;nh &ldquo;hớ&rdquo; tiền điện m&agrave; kh&ocirc;ng biết.</p>\r\n', '<p>Theo như bậc điện của Bộ C&ocirc;ng thương quy định th&igrave; gồm c&oacute; 6 bậc, vậy để c&oacute;&nbsp;<a href=\"https://bandon.vn/tin-tuc/cach-tinh-hoa-don-tien-dien\"><strong>c&aacute;ch t&iacute;nh h&oacute;a đơn tiền điện</strong></a>chuẩn x&aacute;c ch&uacute;ng ta cần biết v&agrave; &aacute;p dụng c&ocirc;ng thức như sau:</p>\r\n\r\n<p><strong><em>Bậc 1 cho kWh 0-50: 1.678 đ</em></strong></p>\r\n\r\n<p><strong><em>Bậc 2 cho kWh 51-100: 1.734 đ</em></strong></p>\r\n\r\n<p><strong><em>Bấc 3 cho kWh 101- 200: 2.014 đ</em></strong></p>\r\n\r\n<p><strong><em>Bậc 4 cho kWh 201-300: 2.536 đ</em></strong></p>\r\n\r\n<p><strong><em>Bậc 5 cho kWh 301-400: 2.834 đ</em></strong></p>\r\n\r\n<p><strong><em>Bậc 6 cho kWh 401 trở l&ecirc;n: 2.927 đ</em></strong></p>\r\n\r\n<p><em>Đơn vị: đồng Việt Nam</em></p>\r\n\r\n<p>C&aacute;c bạn chỉ cần t&iacute;nh theo mức số điện ở c&aacute;c bậc tr&ecirc;n rồi cộng với 10% thuế VAT l&agrave; số tiền m&agrave; người d&acirc;n phải trả thuế cho ng&agrave;nh điện th&igrave; c&aacute;c bạn sẽ ra số điện của gia đ&igrave;nh hay ph&ograve;ng trọ của m&igrave;nh đ&atilde; sử dụng.&nbsp;</p>\r\n\r\n<p>V&iacute; dụ cụ thể: Gia đ&igrave;nh anh Minh trong th&aacute;ng 4 vừa qua đ&atilde; sử dụng hết 400 số điện th&igrave; ta sẽ c&oacute; c&aacute;ch t&iacute;nh như sau:</p>\r\n\r\n<p>Bậc 1: 50*1.678= 83.900 đồng<br />\r\nBậc 2: 50*1.734= 86.700 đồng<br />\r\nBậc 3: 100*2.014= 201.400 đồng<br />\r\nBậc 4: 100*2.536= 253.600 đồng<br />\r\nBậc 5: 100*2.834= 283.400 đồng</p>\r\n\r\n<p>=&gt; Cộng tổng lại l&agrave;:&nbsp;<strong>909.000 đồng</strong>&nbsp;</p>\r\n\r\n<p><em>Thuế VAT 10%= 90.900 đồng</em></p>\r\n\r\n<p><strong>Tổng số tiền gia đ&igrave;nh anh Minh phải đ&oacute;ng l&agrave;: 909.000+90.900= 999.900 đồng</strong></p>\r\n\r\n<h2><strong>C&aacute;c Quy định của nh&agrave; nước</strong></h2>\r\n\r\n<p>Theo quyết định của Bộ C&ocirc;ng thương:</p>\r\n\r\n<p>&ldquo;Tại mỗi địa chỉ nh&agrave; cho thu&ecirc;, B&ecirc;n b&aacute;n điện chỉ k&yacute; một hợp đồng mua b&aacute;n điện duy nhất. Chủ nh&agrave; cho thu&ecirc; c&oacute; tr&aacute;ch nhiệm xuất tr&igrave;nh sổ tạm tr&uacute; hoặc chứng từ x&aacute;c nhận tạm tr&uacute; của cơ quan C&ocirc;ng an quản l&yacute; địa b&agrave;n của người thu&ecirc; nh&agrave;&rdquo;.&nbsp;</p>\r\n\r\n<p>Khi&nbsp;cho thu&ecirc; nh&agrave; dưới 12 th&aacute;ng v&agrave; chủ nh&agrave; kh&ocirc;ng thực hiện k&ecirc; khai được đầy đủ số người sử dụng điện th&igrave; &aacute;p dụng gi&aacute; b&aacute;n lẻ điện sinh hoạt của bậc 3: Từ 101 &ndash; 200kWh cho to&agrave;n bộ sản lượng điện đo đếm được tại c&ocirc;ng tơ.</p>\r\n\r\n<p>Trong đ&oacute; 4 người được t&iacute;nh l&agrave; một hộ sử dụng điện để t&iacute;nh số định mức &aacute;p dụng gi&aacute; b&aacute;n lẻ điện sinh hoạt, cụ thể: 01 người được t&iacute;nh l&agrave; 1/4 định mức, 02 người được t&iacute;nh l&agrave; 1/2 định mức, 03 người được t&iacute;nh l&agrave; 3/4 định mức, 04 người được t&iacute;nh l&agrave; 1 định mức.</p>\r\n\r\n<p>Đối với hợp đồng thu&ecirc; ph&ograve;ng trọ 12 th&aacute;ng trở l&ecirc;n&nbsp; v&agrave; c&oacute; đăng k&yacute; tạm tr&uacute; th&igrave; chủ nh&agrave; trực tiếp k&yacute; hợp đồng mua b&aacute;n điện hoặc đại diện b&ecirc;n thu&ecirc; nh&agrave; k&yacute; hợp đồng mua b&aacute;n điện.&nbsp;</p>\r\n\r\n<h3><strong>Một số ch&uacute; &yacute; trong c&aacute;ch t&iacute;nh h&oacute;a đơn tiền điện nh&agrave; trọ</strong></h3>\r\n\r\n<p>H&atilde;y xem lại c&ocirc;ng thức để kh&ocirc;ng bị nhầm c&aacute;c mốc định mức tương ứng với gi&aacute; điện kh&aacute;c nhau.</p>\r\n\r\n<p>C&aacute;c bạn n&ecirc;n để &yacute; xem x&eacute;t r&otilde; h&oacute;a đơn tiền điện của ph&ograve;ng trọ m&igrave;nh để &uacute;p dụng&nbsp;<strong>c&aacute;ch t&iacute;nh h&oacute;a đơn tiền điện</strong>ch&iacute;nh x&aacute;c nhất. Trong trường hợp c&oacute; sự kh&aacute;c biệt qu&aacute; lớn giữa c&aacute;c th&aacute;ng th&igrave; bạn n&ecirc;n li&ecirc;n hệ ngay với chủ nh&agrave; xin h&oacute;a đơn tiền điện của ph&ograve;ng m&igrave;nh để được l&agrave;m r&otilde; qu&aacute; tr&igrave;nh sử dụng v&agrave; ti&ecirc;u hao điện trong th&aacute;ng đ&oacute;.</p>\r\n\r\n<p>N&ecirc;n ghi r&otilde; v&agrave; xem x&eacute;t số tiền điện giữa c&aacute;c th&aacute;ng để c&oacute; thể tự t&iacute;nh to&aacute;n c&ugrave;ng với so s&aacute;nh, bạn c&oacute; thể tự t&iacute;nh to&aacute;n số tiền điện của ph&ograve;ng m&igrave;nh đ&oacute;ng cho chủ nh&agrave; trọ.&nbsp;</p>\r\n\r\n<p><em>&nbsp;| Tham khảo th&ecirc;m:&nbsp;<a href=\"https://bandon.vn/kinh-nghiem/gia-dien-nha-nuoc\" target=\"_blank\">C&aacute;ch t&iacute;nh tiền điện ph&ograve;ng trọ, gi&aacute; điện nh&agrave; trọ mới 2020</a></em></p>\r\n\r\n<p>Việc kiểm tra r&otilde; gi&aacute; tiền điện trước khi k&yacute; hợp đồng thu&ecirc; nh&agrave; l&agrave; một việc l&agrave;m rất cần thiết để đảm bảo quyền lợi đặc biệt l&agrave; b&ecirc;n đi thu&ecirc; ph&ograve;ng như ch&uacute;ng ta, ngo&agrave;i ra khi đi thu&ecirc; c&aacute;c bạn cần kiểm tra kỹ lưỡng c&aacute;c phụ ph&iacute; kh&aacute;c chứ kh&ocirc;ng chỉ mỗi gi&aacute; tiền điện nh&eacute;.</p>\r\n\r\n<p>Như vậy c&aacute;c bạn c&oacute; thể dễ d&agrave;ng t&iacute;nh tiền điện ph&ograve;ng trọ của m&igrave;nh khi đi thu&ecirc; trọ của m&igrave;nh.</p>\r\n', '16086972111.jpg', 1),
(7, ' LƯU Ý KHÔNG THỂ BỎ QUA KHI TÌM PHÒNG TRỌ CAO CẤP TẠI HÀ NỘI', '<p>X&atilde; hội ph&aacute;t triển, n&ecirc;n nhu cầu của con người c&agrave;ng cao. Bạn muốn t&igrave;m ph&ograve;ng trọ cao cấp tại H&agrave; Nội hay một căn hộ mini/ chung cư mini.&nbsp;</p>\r\n', '<h2><strong>&nbsp;- T&igrave;m ph&ograve;ng trọ cao cấp ở H&agrave; Nội cần ch&uacute; &yacute; những g&igrave;?</strong></h2>\r\n\r\n<p><strong>Ph&ograve;ng trọ cao cấp l&agrave; g&igrave;</strong>? - Cũng l&agrave; c&aacute;c căn ph&ograve;ng trọ nhưng đ&atilde; được c&aacute;c chủ nh&agrave; trang bị đủ nội thất, căn ph&ograve;ng mới v&agrave; tho&aacute;ng m&aacute;t, khu vực an ninh tốt,.... Hiện nay gi&aacute; cho thu&ecirc; của một căn ph&ograve;ng trọ cao cấp c&oacute; gia dao động từ 3,5 triệu - 5 triệu/ 1 th&aacute;ng t&ugrave;y v&agrave;o từng khu vực m&agrave; gi&aacute; ph&ograve;ng trọ cao cấp kh&aacute;c nhau.</p>\r\n\r\n<p>V&iacute; dụ như c&aacute;c khu vực, c&aacute;c quận gần nội th&agrave;nh th&igrave; gi&aacute; ph&ograve;ng trọ sẽ cao hơn c&aacute;c ph&ograve;ng trọ cao cấp khu vực xa nội th&agrave;nh. Ch&iacute;nh v&igrave; vậy ch&uacute;ng ta phải dựa v&agrave;o nguồn t&agrave;i ch&iacute;nh của m&igrave;nh để xem x&eacute;t khu vực thu&ecirc; trọ v&agrave; c&aacute;c căn ph&ograve;ng trọ cao cấp.&nbsp;</p>\r\n\r\n<p><strong>Một căn ph&ograve;ng trọ cao cấp</strong>, nếu t&agrave;i ch&iacute;nh của bạn kh&ocirc;ng đủ nhưng mong muốn th&igrave; bạn n&ecirc;n thu&ecirc; một căn ph&ograve;ng trọ cao cấp xa nội th&agrave;nh, những nơi c&oacute; giao th&ocirc;ng thuận tiện cũng sẽ dễ d&agrave;ng di chuyển m&agrave; bạn kh&ocirc;ng cần phải thu&ecirc; một căn ph&ograve;ng gần nội th&agrave;nh m&agrave; đắt đỏ, t&agrave;i ch&iacute;nh kh&ocirc;ng đủ.&nbsp;</p>\r\n\r\n<p><strong>Thu&ecirc; một căn ph&ograve;ng trọ cao cấp th&igrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua việc quan s&aacute;t c&aacute;c khu vực xung quanh như:&nbsp;</strong></p>\r\n\r\n<p><strong>An ninh:</strong>&nbsp;Bạn n&ecirc;n ưu ti&ecirc;n t&igrave;m một căn ph&ograve;ng trọ c&oacute; an ninh đảm bảo, tốt. Tr&aacute;nh những nơi hẻo l&aacute;nh, gần c&aacute;c khu nh&agrave; bỏ hoang,... sẽ c&oacute; khả năng tệ nạn x&atilde; hội rất nhiều.&nbsp;</p>\r\n\r\n<p><strong>Sự thuận tiện:</strong>&nbsp;Ch&uacute;ng ta đi thu&ecirc; ph&ograve;ng trọ để đ&aacute;p ứng nhu cầu thuận tiện của cuộc sống khi xa nh&agrave;. Bạn n&ecirc;n xem x&eacute;t như giao th&ocirc;ng c&oacute; thuận tiện, gần c&aacute;c chợ hay c&aacute;c si&ecirc;u thị, gần khu c&ocirc;ng ty, trường học,...&nbsp;</p>\r\n\r\n<p><strong>Kh&ocirc;ng gian, cảnh quan:</strong>&nbsp;Hiện nay ở nội th&agrave;nh nhiều nơi sẽ trở th&agrave;nh &ldquo;hồ bơi&rdquo; mỗi khi v&agrave;o m&ugrave;a mưa, việc n&agrave;y ảnh hưởng rất lớn đến đời sống sinh hoạt h&agrave;ng ng&agrave;y của ch&uacute;ng ta mỗi khi đến m&ugrave;a mưa, nếu bạn đi thu&ecirc; ph&ograve;ng trọ v&agrave;o m&ugrave;a mưa th&igrave; c&oacute; thể dễ thấy, nhưng nếu bạn thu&ecirc; ph&ograve;ng v&agrave;o m&ugrave;a hanh kh&ocirc; như m&ugrave;a đ&ocirc;ng th&igrave; bạn chỉ c&ograve;n c&aacute;ch l&agrave; n&ecirc;n hỏi người d&acirc;n xung quanh.</p>\r\n\r\n<p>Khi thu&ecirc; ph&ograve;ng bạn cũng n&ecirc;n ch&uacute; &yacute; c&aacute;c khu vực sinh hoạt gần đấy, tr&aacute;nh những căn ph&ograve;ng trọ gần b&atilde;i r&aacute;c, khu c&ocirc;ng nghiệp gần đấy,... sẽ c&oacute; rất nhiều tiếng ồn, kh&ocirc;ng kh&iacute; c&oacute; m&ugrave;i kh&oacute; chịu,... sẽ g&acirc;y cảm gi&aacute;c kh&oacute; chịu khi sinh sống, nếu&nbsp;<strong>thu&ecirc; căn ph&ograve;ng trọ</strong>&nbsp;ở những khu vực như vậy th&igrave; chắc rằng bạn sẽ rất kh&oacute; khăn trong việc chịu động tiếng ồn hay những m&ugrave;i kh&oacute; chịu.&nbsp;</p>\r\n\r\n<p>Sau khi đ&atilde; t&igrave;m hiểu tất những nhu cầu, c&aacute;c yếu tố cần thiết th&igrave; bạn h&atilde;y khoanh lại khu vực c&aacute;c căn ph&ograve;ng trọ cao cấp c&oacute; thể đ&aacute;p ứng được nhu cầu của bạn lại nh&eacute;.&nbsp;</p>\r\n\r\n<h2><strong>2 - Vậy t&igrave;m ph&ograve;ng trọ cao cấp tại H&agrave; Nội như thế n&agrave;o l&agrave; tốt?&nbsp;</strong></h2>\r\n\r\n<p><strong>Ph&ograve;ng trọ cao cấp H&agrave; Nội - kh&ocirc; tho&aacute;ng:</strong>&nbsp;Một căn ph&ograve;ng trọ tho&aacute;ng chứng tỏ căn ph&ograve;ng kh&ocirc;ng c&oacute; ẩm mốc, kh&ocirc;ng bị b&iacute;. Nếu căn ph&ograve;ng trọ kh&ocirc;ng tho&aacute;ng th&igrave; d&ugrave; đẹp đẽ hay sang trọng đến đ&acirc;u th&igrave; kh&ocirc;ng gian sinh sống sẽ kh&aacute; ngột ngạt. chưa kể đến vấn đề căn ph&ograve;ng ẩm mốc g&acirc;y ra hỏng h&oacute;c c&aacute;c đồ vật nhiều.&nbsp;</p>\r\n\r\n<p><strong>Ph&ograve;ng trọ cao cấp H&agrave; Nội - khu để xe an to&agrave;n:</strong>&nbsp;Chắc hẳn ở một nơi xa lạ th&igrave; gần như trong ch&uacute;ng ta ai cũng c&oacute; một chiếc xe ri&ecirc;ng, t&ugrave;y v&agrave;o mỗi người, gia đ&igrave;nh m&agrave; ch&uacute;ng ta c&oacute; nhiều loại xe kh&aacute;c nhau, một căn ph&ograve;ng trọ cao cấp th&igrave; phải đảm bảo rằng phải c&oacute; khu để xe an to&agrave;n, c&oacute; bảo vệ, c&oacute; camera an ninh v&agrave; khu để xe cần bảo mật v&acirc;n tay.&nbsp;</p>\r\n\r\n<p><strong>Ph&ograve;ng trọ cao cấp H&agrave; Nội - nội thất căn ph&ograve;ng:</strong>&nbsp;C&aacute;c căn ph&ograve;ng trọ cao cấp th&igrave; gần như đều đ&atilde; đầy đủ nội thất cho ch&uacute;ng ta, c&ugrave;ng với việc vệ sinh kh&eacute;p k&iacute;n l&agrave; kh&ocirc;ng thể bỏ qua, tuy nhi&ecirc;n bạn n&ecirc;n xem x&eacute;t kỹ v&agrave; r&otilde; xem c&aacute;c đồ vật c&oacute; hỏng h&oacute;c hay k&eacute;m chất lượng cần b&aacute;o lu&ocirc;n cho chủ nh&agrave; để tr&aacute;nh trường hợp c&oacute; thiệt hại về m&igrave;nh, v&igrave; d&ugrave; l&agrave; cao cấp đến mấy bạn cũng kh&ocirc;ng thể tr&aacute;nh những người chủ nh&agrave; kh&oacute; t&iacute;nh về vấn đề n&agrave;y.&nbsp;</p>\r\n\r\n<p>Một căn ph&ograve;ng trọ cao cấp sẽ rất m&aacute;t v&agrave; hướng ph&ograve;ng đẹp. Vấn đề n&agrave;y th&igrave; bạn n&ecirc;n đi xem ph&ograve;ng v&agrave;o buổi trưa để xem x&eacute;t xem căn ph&ograve;ng c&oacute; tho&aacute;ng m&aacute;t v&agrave; đẹp hay kh&ocirc;ng.&nbsp;</p>\r\n\r\n<p><em>Bạn cần xem x&eacute;t kỹ khu nấu ăn, khu phơi đồ để c&oacute; thể dễ d&agrave;ng sinh hoạt hơn.&nbsp;</em></p>\r\n\r\n<p>Cuối c&ugrave;ng khi bạn đ&atilde; x&aacute;c định được căn ph&ograve;ng bạn muốn thu&ecirc; th&igrave;&nbsp; bạn n&ecirc;n ch&uacute; &yacute; việc k&yacute; hợp đồng, n&oacute; sẽ rất quan trọng, ảnh hưởng trực tiếp đến thời gian sinh hoạt của ch&uacute;ng ta. Bạn cần xem x&eacute;t kỹ thời gian, c&aacute;c phụ ph&iacute; như gi&aacute; điện, nước, mạng internet, ph&iacute; gửi xe,...&nbsp;</p>\r\n', '16086972971.jpg', 1),
(8, 'KINH NGHIỆM NUÔI CHÓ MÈO Ở PHÒNG TRỌ, CHUNG CƯ MINI', '<p>Hiện nay, việc nu&ocirc;i ch&oacute; m&egrave;o tại ph&ograve;ng trọ ng&agrave;y c&agrave;ng phổ biến hơn. Ch&iacute;nh v&igrave; vậy ng&agrave;y c&agrave;ng nhiều nh&agrave; trọ mọc l&ecirc;n nhằm đ&aacute;p ứng nhu cầu n&agrave;y của kh&aacute;ch h&agrave;ng.</p>\r\n', '<h2><strong>Nu&ocirc;i ch&oacute; m&egrave;o ở ph&ograve;ng trọ c&oacute; ưu, nhược điểm g&igrave;?</strong></h2>\r\n\r\n<h3><strong>Giảm bớt cảm gi&aacute;c c&ocirc; đơn, buồn tủi của bản th&acirc;n</strong></h3>\r\n\r\n<p>Gần như ch&uacute;ng trong ch&uacute;ng ta ai nu&ocirc;i th&uacute; cưng cũng để c&oacute; th&ecirc;m một &lsquo;người bạn&rsquo; b&ecirc;n cạnh khi c&ocirc; đơn, c&oacute; những &lsquo;người lắng nghe&rsquo; t&acirc;m sự của m&igrave;nh. Với những ch&uacute; ch&oacute;, m&egrave;o th&igrave; điều n&agrave;y lại v&ocirc; c&ugrave;ng hợp l&yacute;.</p>\r\n\r\n<p>Tuy vậy c&aacute;c ch&uacute; ch&oacute; ch&uacute; m&egrave;o rất hay nghịch ngợm vờn, chạy quanh căn ph&ograve;ng sẽ g&acirc;y ra kh&aacute; nhiều tiếng ồn. V&igrave; vậy khi&nbsp;<strong>nu&ocirc;i ch&oacute;, m&egrave;o ở ph&ograve;ng trọ</strong>&nbsp;bạn n&ecirc;n để &yacute; h&agrave;ng x&oacute;m, c&aacute;c ph&ograve;ng trọ b&ecirc;n cạnh hoặc ở tầng dưới nếu họ kh&ocirc;ng th&iacute;ch th&uacute; cưng hay tiếng động th&igrave; bạn n&ecirc;n trải một tấm thảm để giảm bớt những tiếng động đ&ugrave;a nghịch của ch&uacute;ng.</p>\r\n\r\n<p>Cũng như l&agrave; h&agrave;ng x&oacute;m bạn kh&ocirc;ng th&iacute;ch th&uacute; cưng th&igrave; bạn n&ecirc;n n&oacute;i chuyện với họ v&igrave; khi nu&ocirc;i c&aacute;c ch&uacute; ch&oacute;, ch&uacute; m&egrave;o kh&ocirc;ng thể mất được m&ugrave;i h&ocirc;i của ch&uacute;ng d&ugrave; ch&uacute;ng ta tắm rửa cho ch&uacute;ng h&agrave;ng ng&agrave;y.</p>\r\n\r\n<h3><strong>T&iacute;nh to&aacute;n thật kỹ chi ph&iacute; cho ch&uacute; m&egrave;o hay ch&oacute; ở ph&ograve;ng trọ c&ugrave;ng m&igrave;nh</strong></h3>\r\n\r\n<p>V&igrave; kinh ph&iacute; nu&ocirc;i một ch&uacute; ch&oacute;, m&egrave;o cũng cực kỳ tốn k&eacute;m. Bạn cần phải đảm bảo v&agrave; đ&aacute;p ứng được c&aacute;c nhu cầu cơ bản của ch&uacute;ng, cũng như chuẩn bị những t&igrave;nh huống khẩn cấp c&oacute; thể xảy ra.</p>\r\n\r\n<p>Kinh ph&iacute; của c&aacute;c&nbsp;<strong>ch&uacute; ch&oacute;, m&egrave;o</strong>&nbsp;sẽ rất ảnh hưởng đến t&agrave;i ch&iacute;nh của bạn khi&nbsp;<strong>ở trọ</strong>, cho n&ecirc;n bạn cần xem x&eacute;t v&agrave; ghi r&otilde; những chi ti&ecirc;u cần thiết v&agrave; kh&ocirc;ng cần thiết để vừa tiết kiệm v&agrave; c&aacute;c th&uacute; cưng của m&igrave;nh vẫn thoải m&aacute;i.</p>\r\n\r\n<h3><strong>Kh&ocirc;ng n&ecirc;n bỏ qua những điều trị th&uacute; y cần thiết&nbsp;</strong></h3>\r\n\r\n<p>Việc n&agrave;y kh&aacute; quan trọng với những ch&uacute; ch&oacute; m&egrave;o của m&igrave;nh, tuy ch&uacute;ng c&oacute; thể khỏe l&ecirc;n nhanh ch&oacute;ng trong những trường hợp bệnh nhẹ, nhưng ch&uacute;ng cũng c&oacute; thể tử vong ngay khi mắc c&aacute;c bệnh khẩn cấp, v&agrave; cũng được tin vacxin h&agrave;ng th&aacute;ng c&ugrave;ng với kiểm tra định kỳ.</p>\r\n\r\n<h3><strong>Kh&ocirc;ng n&ecirc;n để c&aacute;c đồ d&ugrave;ng dễ vỡ, c&aacute;c dụng cụ sắc nhọn nhiều trong ph&ograve;ng&nbsp;</strong></h3>\r\n\r\n<p>C&aacute;c th&uacute; cưng nhất l&agrave; ch&oacute;, m&egrave;o lu&ocirc;n th&iacute;ch hoạt động mạnh ch&uacute;ng hay nhảy nh&oacute;t v&agrave; đ&ugrave;a nghịch quanh chủ hay c&aacute;c đồ vật trong ph&ograve;ng, n&ecirc;n việc ch&uacute;ng đ&aacute;nh rơi, đổ vỡ c&aacute;c đồ vật l&agrave; điều kh&ocirc;ng thể tr&aacute;nh được, v&igrave; vậy để ch&uacute; &yacute; an to&agrave;n cho th&uacute; cưng th&igrave; đồ d&ugrave;ng trong ph&ograve;ng trọ bạn tr&aacute;nh sử dụng những đồ vật dễ vỡ, hay sắc nhọn để tr&aacute;nh ch&uacute;ng chơi đ&ugrave;a m&agrave; xảy ra một số tai nạn kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n\r\n<p>Việc t&igrave;m nơi cho ch&uacute; ch&oacute; m&egrave;o của m&igrave;nh đi vệ sinh v&agrave; ăn uống cũng kh&aacute; l&agrave; quan trọng. V&igrave; n&oacute; c&oacute; thể ảnh hưởng đến cả ch&uacute;ng ta v&agrave; những h&agrave;ng x&oacute;m b&ecirc;n cạnh. Bạn n&ecirc;n tận dụng những g&oacute;c nhỏ hay những nơi của ph&ograve;ng trọ m&agrave; bạn &iacute;t khi đi qua hay để đồ vật như g&oacute;c tường, một g&oacute;c ban c&ocirc;ng nhỏ,... để tr&aacute;nh m&ugrave;i.&nbsp;</p>\r\n\r\n<p>Hay l&agrave; nơi ch&uacute;ng ăn, nếu bạn nu&ocirc;i cả ch&oacute; v&agrave; m&egrave;o th&igrave; bạn n&ecirc;n để thức ăn của m&egrave;o l&ecirc;n cao m&agrave; những ch&uacute; ch&oacute; kh&ocirc;ng nh&igrave;n thấy được. H&atilde;y t&igrave;m kiếm một số chỗ l&yacute; tưởng để cho c&aacute;c ch&uacute; m&egrave;o của m&igrave;nh c&oacute; một kh&ocirc;ng gian ăn uống thoải m&aacute;i v&agrave; hợp l&iacute; nhất.</p>\r\n\r\n<h3><strong>Bạn n&ecirc;n d&agrave;nh một ch&uacute;t thời gian cho c&aacute;c th&uacute; cưng mỗi ng&agrave;y&nbsp;</strong></h3>\r\n\r\n<p>Những th&uacute; cưng hay n&oacute;i gần gũi hơn l&agrave; c&aacute;c ch&uacute; ch&oacute;, m&egrave;o của bạn cần t&igrave;nh y&ecirc;u v&agrave; sự quan t&acirc;m nhiều từ chủ nh&acirc;n của n&oacute;.</p>\r\n\r\n<p>Khi bạn c&oacute; một ch&uacute; ch&oacute; hay m&egrave;o th&igrave; chắc chắn rằng kh&ocirc;ng gian sống của bạn lu&ocirc;n c&oacute; ch&uacute;ng b&ecirc;n cạnh, d&ugrave; cả một ng&agrave;y bận rộn th&igrave; bạn vẫn n&ecirc;n bỏ một ch&uacute;t thời gian vuốt ve v&agrave; t&acirc;m sự với ch&uacute;ng nh&eacute;.</p>\r\n\r\n<h3><strong>Giữ vệ sinh, tẩy rửa cho ch&oacute;, m&egrave;o mỗi ng&agrave;y&nbsp;</strong></h3>\r\n\r\n<p>Ch&oacute;, m&egrave;o từ bản th&acirc;n ch&uacute;ng th&igrave; gần như l&uacute;c n&agrave;o cũng sẽ bốc m&ugrave;i của ri&ecirc;ng mỗi lo&agrave;i, ch&iacute;nh v&igrave; vậy sẽ rất h&ocirc;i, cũng đ&ugrave;a nghịch chơi đ&ugrave;a nhiều sẽ c&oacute; rất nhiều vi khuẩn ch&iacute;nh v&igrave; vậy để c&oacute; kh&ocirc;ng gian thoải m&aacute;i hết m&ugrave;i từ c&aacute;c ch&uacute; ch&oacute;, m&egrave;o của m&igrave;nh th&igrave; bạn n&ecirc;n tắm rửa cho ch&uacute;ng mỗi ng&agrave;y.</p>\r\n\r\n<p><em>Lưu &yacute; quan trọng đ&oacute; l&agrave; bạn cần &quot;chuẩn bị chỗ ở k&iacute;n đ&aacute;o, ấm &aacute;p cho ch&uacute; m&egrave;o, ch&oacute;&quot;</em></p>\r\n\r\n<p>Ch&uacute;ng ta kh&ocirc;ng phải ai cũng c&oacute; thể bế ch&uacute; m&egrave;o hay ch&oacute; ngủ c&ugrave;ng mỗi ng&agrave;y, ch&iacute;nh v&igrave; vậy t&igrave;m chỗ ở k&iacute;n đ&aacute;o, ấm &aacute;p cho ch&uacute;ng để giữ một sức khỏe tốt nhất cho c&aacute;c th&uacute; cưng của bạn. Sau một thời gian hoạt động vui đ&ugrave;a mệt mỏi th&igrave; ch&uacute;ng c&oacute; thể nghỉ ngơi ngay cả khi bạn kh&ocirc;ng c&oacute; nh&agrave;.&nbsp;</p>\r\n\r\n<h3><strong>N&ecirc;n huấn luyện th&uacute; cưng</strong></h3>\r\n\r\n<p>Khi ở trọ, việc ch&uacute;ng ta theo d&otilde;i th&uacute; cưng những ch&uacute; ch&oacute;, m&egrave;o của m&igrave;nh l&agrave; kh&aacute; kh&oacute; khăn ch&iacute;nh v&igrave; vậy việc huấn luyện những th&uacute; cưng của bạn l&agrave; điều rất cần thiết, gi&uacute;p cho những ch&uacute; ch&oacute;, m&egrave;o c&oacute; thể tự t&igrave;m nơi đi vệ sinh, tự t&igrave;m nơi ngủ, biết thời gian tắm rửa, những nơi kh&ocirc;ng được chơi đ&ugrave;a,... sẽ gi&uacute;p bạn đỡ mệt nhọc trong việc nu&ocirc;i dưỡng ch&uacute;ng ở ph&ograve;ng trọ.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n l&agrave; những&nbsp;<strong>kinh nghiệm nu&ocirc;i ch&oacute; m&egrave;o ở ph&ograve;ng trọ</strong>&nbsp;cần thiết, sẽ gi&uacute;p c&aacute;c bạn phần n&agrave;o đ&oacute; trong việc sinh hoạt tại một nơi ở mới nh&eacute;.</p>\r\n', '16086973731.jpg', 1),
(9, 'BIẾN PHÒNG TRỌ MỐC MEO THÀNH CĂN HỘ STUDIO SIÊU XINH VỚI 7 TRIỆU ĐỒNG', '<p>Biến ph&ograve;ng trọ mốc meo th&agrave;nh căn hộ studio si&ecirc;u xinh với 7 triệu đồng tại gia sống ảo hết c&ocirc;ng suất.</p>\r\n', '<p>D&ugrave; l&agrave; nh&agrave; thu&ecirc; hay ch&iacute;nh chủ, ai m&agrave; chẳng muốn sẽ c&oacute; một nơi ăn chốn ở sạch sẽ, gọn g&agrave;ng v&agrave; xinh đẹp nhỉ? Ri&ecirc;ng với phe con g&aacute;i, c&oacute; background xịn s&ograve; để sống ảo cho nhanh tr&ocirc;i qua những ng&agrave;y ở nh&agrave; nh&agrave;m ch&aacute;n thế n&agrave;y nữa th&igrave; c&agrave;ng tốt.</p>\r\n\r\n<p>Tuy nhi&ecirc;n để c&oacute; thể&nbsp;cải tạo một căn ph&ograve;ng&nbsp;chẳng phải chuyện dễ d&agrave;ng v&igrave; sẽ tốn kha kh&aacute; thời gian, c&ocirc;ng sức v&agrave; cả tiền bạc nữa.&nbsp;C&ocirc; g&aacute;i trẻ tuổi cũng từng gặp những vấn đề y hệt. C&oacute; điều đ&oacute; l&agrave; chuyện trước đ&acirc;y th&ocirc;i bởi hiện tại,&nbsp;<a href=\"https://bandon.vn/thue-phong-tro\"><strong>ph&ograve;ng trọ</strong></a>&nbsp;của c&ocirc; đ&atilde; chẳng kh&aacute;c một chiếc studio be b&eacute;, xinh xinh rồi n&agrave;y.</p>\r\n\r\n<p>C&ocirc; &aacute;i cho biết bản th&acirc;n l&agrave; một người c&oacute; t&iacute;nh kh&aacute; k&igrave;, th&iacute;ch đẹp, th&iacute;ch sạch. Bởi vậy m&agrave; sau chục năm ở nh&agrave; thu&ecirc; nhưng c&ocirc; n&agrave;ng vẫn cố gắng l&agrave;m sao để &quot;em n&oacute;&quot; xinh nhất c&oacute; thể.&nbsp;</p>\r\n\r\n<p>Chia sẻ về căn ph&ograve;ng hiện tại:&nbsp;<em>&quot;H&ocirc;m đi xem nh&agrave; về m&igrave;nh stress lu&ocirc;n, nh&agrave; hư hỏng nặng v&agrave; anh chị thu&ecirc; nh&agrave; trước m&igrave;nh sắp dọn đi n&ecirc;n đồ kh&aacute; bừa bộn.&nbsp;</em><em>Thế l&agrave; nhận nh&agrave; xong, m&igrave;nh&nbsp;mất hơn 1 tuần để tự tay sơn từng bức tường v&agrave; sắp đặt.&nbsp;</em></p>\r\n\r\n<p><em>May c&oacute; &quot;anh bạn th&acirc;n&quot; đa năng biết t&iacute;t tường, sửa la ph&ocirc;ng, thay đ&egrave;n điện, đ&oacute;ng kệ c&aacute;c thứ c&aacute;c thứ phụ gi&uacute;p nhiệt t&igrave;nh. Việc của m&igrave;nh chỉ l&agrave; l&agrave;m sao cho &quot;em n&oacute;&quot; thật đẹp, thật cool m&agrave; tiết kiệm chi ph&iacute;&quot;</em>.</p>\r\n\r\n<p>Một trong những điểm đ&aacute;ng ch&uacute; &yacute; nhất trong c&ocirc;ng cuộc &quot;thay &aacute;o&quot; cho ph&ograve;ng trọ của c&ocirc; n&agrave;ng ch&iacute;nh l&agrave; tiết kiệm chi ph&iacute;. Chỉ với hơn 7 triệu đồng để c&oacute; căn ph&ograve;ng xinh xắn, s&aacute;ng sủa như studio thế n&agrave;y. C&oacute; thời gian n&ecirc;n c&ocirc; n&agrave;ng l&ecirc;n mạng t&igrave;m được nhiều chỗ b&aacute;n đồ rẻ hơn, chịu kh&oacute; đi mua &quot;tận gốc&quot; d&ugrave; xa hơn.</p>\r\n', '1608867844101.jpg', 1),
(10, 'BÓC MẼ 3 CHIÊU TRÒ LỪA ĐẢO CHO THUÊ PHÒNG TRỌ, NHÀ TRỌ GIÁ RẺ', '<p>Ph&ograve;ng trọ gi&aacute; rẻ l&agrave; mối quan t&acirc;m của nhiều sinh vi&ecirc;n, người lao động khi sống v&agrave; học tập tại c&aacute;c th&agrave;nh phố lớn. Lợi dụng nhu cầu đ&oacute;, nhiều m&aacute;nh kh&oacute;e đ&atilde; được kẻ xấu vẽ ra để lừa lọc người thu&ecirc;.</p>\r\n', '<p>Thu&ecirc; ph&ograve;ng trọ, nh&agrave; trọ gi&aacute; rẻ l&agrave; mối quan t&acirc;m của nhiều sinh vi&ecirc;n, người lao động khi sống v&agrave; học tập tại c&aacute;c th&agrave;nh phố lớn. Lợi dụng nhu cầu đ&oacute;, nhiều m&aacute;nh kh&oacute;e đ&atilde; được kẻ xấu vẽ ra để lừa lọc người thu&ecirc;.</p>\r\n\r\n<h2><strong>Chi&ecirc;u tr&ograve; lừa đảo cho thu&ecirc; ph&ograve;ng trọ gi&aacute; rẻ</strong></h2>\r\n\r\n<h3><strong>1 - Cho thu&ecirc; ph&ograve;ng trọ &ldquo;ảo&rdquo;</strong></h3>\r\n\r\n<p><em>Dấu hiệu nhận biết:</em></p>\r\n\r\n<ul>\r\n	<li>Th&ocirc;ng tin cho thu&ecirc; mơ hồ, sơ x&agrave;i v&agrave; kh&ocirc;ng cụ thể, v&iacute; dụ: &ldquo;Cho thu&ecirc; nh&agrave; trọ gi&aacute; rẻ, mới x&acirc;y, li&ecirc;n hệ theo số điện thoại abcxyz&rdquo; m&agrave; kh&ocirc;ng c&oacute; gi&aacute; cả cụ thể hay địa chỉ r&otilde; r&agrave;ng. Khi li&ecirc;n lạc với số điện thoại n&agrave;y, nhiều khả năng kh&aacute;ch thu&ecirc; sẽ gặp phải &ldquo;c&ograve; ph&ograve;ng&rdquo; thay v&igrave; chủ nh&agrave; trọ.</li>\r\n	<li>&quot;Trống ph&ograve;ng n&agrave;o th&igrave; giao ph&ograve;ng đ&oacute;&quot;, nghĩa l&agrave; ph&ograve;ng&nbsp;kh&aacute;ch đến xem c&oacute; thể kh&aacute;c ho&agrave;n to&agrave;n với ph&ograve;ng l&uacute;c nhận b&agrave;n giao.</li>\r\n	<li>Cho thu&ecirc; ph&ograve;ng trọ nhưng lại gắn m&aacute;c chung cư mini, chất lượng kh&ocirc;ng l&yacute; tưởng như quảng c&aacute;o, chỗ để xe chật chội, tiện &iacute;ch chung qu&aacute; tải, kh&ocirc;ng giới hạn số người ở một ph&ograve;ng m&agrave; thu tiền theo đầu người&hellip;</li>\r\n</ul>\r\n\r\n<p><em>C&aacute;ch ph&ograve;ng tr&aacute;nh:</em></p>\r\n\r\n<p>Cẩn thận với những th&ocirc;ng tin ch&agrave;o mời thu&ecirc; nh&agrave; tr&ocirc;i nổi tr&ecirc;n internet hay mạng x&atilde; hội. N&ecirc;n chọn lọc những tin đăng cho thu&ecirc; nh&agrave; c&oacute; đầy đủ th&ocirc;ng tin, địa chỉ r&otilde; r&agrave;ng, số điện thoại li&ecirc;n hệ ch&iacute;nh chủ.</p>\r\n\r\n<p>Để t&igrave;m được chỗ thu&ecirc; tốt, n&ecirc;n tham khảo &yacute; kiến người d&acirc;n xung quanh khu vực nh&agrave; trọ, n&ecirc;n đi xem ph&ograve;ng với người th&acirc;n hoặc bạn b&egrave; để c&oacute; đ&aacute;nh gi&aacute; kh&aacute;ch quan hơn hoặc đề ph&ograve;ng gặp kẻ xấu.</p>\r\n\r\n<h3><strong>2 - Lừa đảo chiếm đoạt tiền đặt cọc</strong></h3>\r\n\r\n<p><em>Dấu hiệu nhận biết:</em></p>\r\n\r\n<ul>\r\n	<li>Chủ nh&agrave; cho thu&ecirc; ph&ograve;ng gi&aacute; rẻ, nhưng lại bắt đặt cọc một khoản lớn, từ 2 &ndash; 3 th&aacute;ng tiền ph&ograve;ng mới cho người thu&ecirc; dọn v&agrave;o ở.</li>\r\n	<li>Một số đối tượng tận dụng những căn ph&ograve;ng trống, vắng chủ, đăng tin rao cho thu&ecirc; gi&aacute; rẻ rồi dẫn kh&aacute;ch đi xem, nhưng lấy l&yacute; do kh&aacute;ch đang thu&ecirc; đi vắng kh&ocirc;ng v&agrave;o xem được. Sau đ&oacute;, y&ecirc;u cầu kh&aacute;ch đặt cọc một khoản tiền nhỏ, thường dưới 500.000 đồng giữ chỗ. Kh&aacute;ch h&agrave;ng thấy tiền đặt cọc kh&ocirc;ng cao, ph&ograve;ng trọ đẹp n&ecirc;n thường đồng &yacute;, đến thời gian hẹn đ&agrave;m ph&aacute;n thu&ecirc; nh&agrave; kh&aacute;ch li&ecirc;n hệ lại th&igrave; số điện thoại kh&ocirc;ng li&ecirc;n hệ được.</li>\r\n	<li>Sau khi nhận tiền đặt cọc, chủ nh&agrave; tăng gi&aacute; hoặc bổ sung th&ecirc;m một loạt ph&iacute; khi thu&ecirc; nh&agrave; khiến kh&aacute;ch h&agrave;ng buộc phải &ldquo;bỏ của chạy lấy người&rdquo;, mất oan tiền cọc.</li>\r\n	<li>Điều khoản hợp đồng với nội dung mập mờ &ldquo;b&ecirc;n cho thu&ecirc; thỏa thuận giữ ph&ograve;ng cho b&ecirc;n thu&ecirc; khi c&oacute; ph&ograve;ng trống&rdquo;. Khi đ&oacute;, chủ nh&agrave; sẽ tự quyết việc giao ph&ograve;ng cho người thu&ecirc; v&agrave;o thời điểm n&agrave;o, bất chấp kh&aacute;ch thu&ecirc; đ&atilde; đặt cọc giữ chỗ. Nếu đến ng&agrave;y hẹn b&ecirc;n thu&ecirc; n&oacute;i chưa c&oacute; ph&ograve;ng trống, kh&aacute;ch vẫn phải đợi, hoặc phải chịu mất lu&ocirc;n tiền cọc.</li>\r\n</ul>\r\n\r\n<p><em>C&aacute;ch ph&ograve;ng tr&aacute;nh:</em></p>\r\n\r\n<p>Nếu tiền thu&ecirc; ph&ograve;ng trọ lớn, người thu&ecirc; cần t&igrave;m hiểu th&ocirc;ng tin để chắc chắn gặp đ&uacute;ng chủ nh&agrave;. Chỉ đặt cọc giữ chỗ khi c&oacute; thỏa thuận bi&ecirc;n nhận r&otilde; r&agrave;ng, tuyệt đối kh&ocirc;ng đặt cọc nếu nghi ngờ c&oacute; dấu hiệu lừa đảo.</p>\r\n\r\n<p>Khi đặt cọc phải y&ecirc;u cầu chủ trọ ghi r&otilde; r&agrave;ng v&agrave; chi tiết những thỏa thuận về tiền ph&ograve;ng, chi ph&iacute; h&agrave;ng th&aacute;ng để l&agrave;m bằng chứng. Lưu &yacute; giấy đặt cọc phải c&oacute; chữ k&yacute;&nbsp;của cả hai b&ecirc;n.</p>\r\n\r\n<h3><strong>&nbsp;3 - &ldquo;</strong><strong>Bẫy</strong><strong>&rdquo;</strong><strong>&nbsp;ở gh&eacute;p ph&ograve;ng</strong></h3>\r\n\r\n<p><em>Dấu hiệu nhận biết:</em></p>\r\n\r\n<ul>\r\n	<li>Lợi dụng t&acirc;m l&yacute; muốn thu&ecirc; ph&ograve;ng trọ tốt m&agrave; kh&ocirc;ng qu&aacute; tốn k&eacute;m, kẻ xấu giả l&agrave;m người cần ở gh&eacute;p rồi ngang nhi&ecirc;n cuỗm t&agrave;i sản khi bạn c&ugrave;ng ph&ograve;ng sơ hở. Những đối tượng n&agrave;y thường lập nick ảo rồi l&ecirc;n mạng x&atilde; hội, v&agrave;o những trang t&igrave;m người ở gh&eacute;p nhắn tin, li&ecirc;n lạc với ai đang c&oacute; nhu cầu.</li>\r\n	<li>Khi được đồng &yacute; ở gh&eacute;p, kẻ xấu sẽ hạn chế đến mức thấp nhất việc để bạn khai th&aacute;c th&ocirc;ng tin về m&igrave;nh. Khi đến ở, họ thường viện ra h&agrave;ng loạt l&yacute; do để tr&igrave; ho&atilde;n, kh&ocirc;ng cung cấp giấy tờ t&ugrave;y th&acirc;n hay l&agrave;m thủ tục đăng k&yacute; tạm tr&uacute;, đồng thời hạn chế việc tiếp x&uacute;c với người c&ugrave;ng thu&ecirc; trọ, kh&ocirc;ng mang theo t&agrave;i sản c&oacute; gi&aacute; trị tới nh&agrave; thu&ecirc;...</li>\r\n</ul>\r\n\r\n<p><em>C&aacute;ch ph&ograve;ng tr&aacute;nh:</em></p>\r\n\r\n<p>T&igrave;m hiểu kỹ về người ở gh&eacute;p trước khi đồng &yacute; cho họ dọn v&agrave;o thu&ecirc; nh&agrave; chung. Những th&ocirc;ng tin cơ bản bạn nhất định phải hỏi v&agrave; x&aacute;c minh như t&ecirc;n tuổi, qu&ecirc; qu&aacute;n, nghề nghiệp, c&aacute;c th&oacute;i quen sinh hoạt kh&aacute;c...</p>\r\n\r\n<p>Ngo&agrave;i ra, khi ai đ&oacute; chuyển đến, bạn h&atilde;y y&ecirc;u cầu người đ&oacute; ra ngay c&ocirc;ng an phường đăng k&yacute; tạm tr&uacute;, tạm vắng. Nếu l&agrave; kẻ xấu chắc chắn sẽ bỏ cuộc v&igrave; kh&ocirc;ng d&aacute;m ra tr&igrave;nh diện trước c&ocirc;ng an, ngược lại, người thực sự c&oacute; nhu cầu ở gh&eacute;p sẽ kh&ocirc;ng ngần ngại thực hiện thủ tục n&agrave;y để đảm bảo quyền lợi cho họ khi thu&ecirc; nh&agrave;.</p>\r\n\r\n<h3><strong>4 - Lưu &yacute; khi thu&ecirc; ph&ograve;ng trọ</strong></h3>\r\n\r\n<p><em>Nhiều trường hợp chủ nh&agrave; đẩy gi&aacute; thu&ecirc; v&ocirc; l&yacute; nhằm trục lợi.</em></p>\r\n\r\n<p>Nhiều trường hợp chủ nh&agrave; ban đầu qu&aacute; nhiệt t&igrave;nh v&agrave; đưa ra mức chi ph&iacute; thu&ecirc; trọ ưu đ&atilde;i hơn hẳn so với những ph&ograve;ng trọ c&ugrave;ng khu vực. Tuy nhi&ecirc;n, khi kh&aacute;ch dọn v&agrave;o ở một thời gian ngắn, chủ nh&agrave; kiếm cớ tăng gi&aacute; cho thu&ecirc; cao hơn thỏa thuận ban đầu. Nếu kh&ocirc;ng chấp nhận, kh&aacute;ch buộc phải chuyển đi v&agrave; mất cọc.</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng cũng phải cẩn thận khi hợp đồng kh&ocirc;ng n&ecirc;u r&otilde; c&aacute;c khoản ph&iacute; h&agrave;ng th&aacute;ng, thậm ch&iacute; kh&ocirc;ng c&oacute; hợp đồng thu&ecirc; nh&agrave;. Trường hợp kh&ocirc;ng c&oacute; hợp đồng thu&ecirc; nh&agrave;, chủ nh&agrave; thường bao biện rằng &ldquo;tốt cho người thu&ecirc;&rdquo;, kh&ocirc;ng c&oacute; hợp đồng muốn chuyển đi bất cứ l&uacute;c n&agrave;o cũng được. Tuy nhi&ecirc;n, điều đ&oacute; đồng nghĩa với việc, nếu chủ nh&agrave; tăng gi&aacute; v&ocirc; l&yacute; th&igrave; kh&aacute;ch h&agrave;ng cũng kh&ocirc;ng c&oacute; căn cứ để phản đối.</p>\r\n\r\n<p><em>C&aacute;ch ph&ograve;ng tr&aacute;nh:</em></p>\r\n\r\n<ul>\r\n	<li>Cần trao đổi kỹ th&ocirc;ng tin với chủ nh&agrave; về gi&aacute; thu&ecirc; ph&ograve;ng v&agrave; c&aacute;c chi ph&iacute; cơ bản, x&aacute;c nhận xem ngo&agrave;i những khoản ph&iacute; n&agrave;y th&igrave; c&ograve;n ph&aacute;t sinh th&ecirc;m bất kỳ khoản ph&iacute; n&agrave;o kh&aacute;c kh&ocirc;ng?</li>\r\n	<li>Lưu &yacute; trong hợp đồng thu&ecirc; nh&agrave; phải ghi chi tiết gi&aacute; thu&ecirc;, &aacute;p dụng gi&aacute; đ&oacute; trong thời gian bao l&acirc;u, đến khi n&agrave;o sẽ tăng gi&aacute; (nếu c&oacute;), tỉ lệ tăng mỗi năm kh&ocirc;ng qu&aacute; bao nhi&ecirc;u phần trăm. B&ecirc;n cạnh đ&oacute;, hợp đồng phải quy định r&otilde; việc thay đổi c&ocirc;ng năng, vật dụng, sơn sửa nh&agrave;... sẽ do ai trả tiền, tr&aacute;nh sau n&agrave;y kh&ocirc;ng ở nữa sẽ bị chủ nh&agrave; lấy cớ trừ tiền cọc.</li>\r\n</ul>\r\n', '1608867853102.jpg', 1);
INSERT INTO `news` (`id`, `name`, `description`, `detail_content`, `photo`, `hot`) VALUES
(11, 'TÂN SINH VIÊN NÊN SỐNG Ở THUÊ PHÒNG TRỌ HAY KÍ TÚC XÁ?', '<p>Các bạn thí sinh sau khi thi Đại học xong sẽ phải ở xa nhà. Vậy c&aacute;c t&acirc;n sinh vi&ecirc;n n&ecirc;n s&ocirc;́ng ở thu&ecirc; phòng trọ hay k&iacute; t&uacute;c x&aacute;?</p>\r\n', '<p>Các bạn thí sinh sau khi thi Đại học xong sẽ phải ở xa nhà. Vậy c&aacute;c t&acirc;n sinh vi&ecirc;n n&ecirc;n s&ocirc;́ng ở thu&ecirc; phòng trọ hay k&iacute; t&uacute;c x&aacute;?</p>\r\n\r\n<p>V&acirc;̣y làm th&ecirc;́ nào đ&ecirc;̉ tìm được m&ocirc;̣t nơi ở t&ocirc;́t. Bài vi&ecirc;́t dưới đ&acirc;y&nbsp;sẽ chia sẻ tất tần tật những kinh nghiệm ở trọ hoặc k&yacute; t&uacute;c x&aacute; dành cho các bạn sinh vi&ecirc;n xa nhà nh&eacute;!&nbsp;&nbsp;</p>\r\n\r\n<h2><strong>Sinh vi&ecirc;n ở k&yacute; t&uacute;c x&aacute;</strong></h2>\r\n\r\n<p>Tùy m&ocirc;̃i trường sẽ có ký túc xá hoặc kh&ocirc;ng có, nhưng n&ecirc;́u trường bạn học có ký túc xá thì dưới đ&acirc;y sẽ là những th&ocirc;ng tin v&ocirc; cùng hữu ích dành cho bạn.</p>\r\n\r\n<h3>Trước ti&ecirc;n, hãy nói đ&ecirc;́n những ưu đi&ecirc;̉m của vi&ecirc;̣c ở ký túc xá</h3>\r\n\r\n<p>1. Đ&ocirc;ng vui. Xung quanh bạn lúc nào cũng có bạn bè cười nói, chi&ecirc;̀u chi&ecirc;̀u lại rủ r&ecirc; nhau đi ăn gỏi cu&ocirc;́n, hủ ti&ecirc;́u gõ, bánh tráng nướng, trà sữa, cà ph&ecirc; thì &lsquo;vui h&ecirc;́t sảy&rsquo;.</p>\r\n\r\n<p>2. Ở ký túc xá, bạn sẽ kh&ocirc;ng phải lo v&ecirc;̀ những t&ecirc;̣ nạn xã h&ocirc;̣i, bởi nơi đ&acirc;y r&acirc;́t an ninh, lu&ocirc;n có chú bảo v&ecirc;̣ d&ecirc;̃ thương gác c&ocirc;̉ng, người ngoài sẽ kh&ocirc;ng th&ecirc;̉ v&ocirc; được trừ sinh vi&ecirc;n có thẻ ký túc xá.</p>\r\n\r\n<p>3. Đi&ecirc;̀u thích nh&acirc;́t là chi phí ở ký túc xá &lsquo;rẻ bèo&rsquo;, khoảng 200-300k/giường th&ocirc;i, các chi phí khác như đi&ecirc;̣n nước wifi thì bạn cũng phải đóng th&ecirc;m nhé, nhưng t&acirc;̀m m&acirc;́y chục ngàn th&ocirc;i. Ti&ecirc;́t ki&ecirc;̣m được r&acirc;́t nhi&ecirc;̀u chi phí sinh hoạt lu&ocirc;n.</p>\r\n\r\n<p>4. Ký túc xá là nơi k&ecirc;́t n&ocirc;́i bạn bè với nhau, d&ecirc;̃ dàng trao đ&ocirc;̉i, giao lưu học t&acirc;̣p, rèn luy&ecirc;̣n cách s&ocirc;́ng t&acirc;̣p th&ecirc;̉.</p>\r\n\r\n<p>5. Các th&ocirc;ng tin ở trường lớp đ&ecirc;́n với bạn r&acirc;́t nhanh qua &lsquo;k&ecirc;nh truy&ecirc;̀n mi&ecirc;̣ng&rsquo; của m&acirc;́y đứa cùng phòng.</p>\r\n\r\n<p>6. S&acirc;n ở ký túc xá r&acirc;́t r&ocirc;̣ng, là m&ocirc;̣t đi&ecirc;̉m c&ocirc;̣ng cho bạn nào thích th&ecirc;̉ thao, chi&ecirc;̀u chi&ecirc;̀u xu&ocirc;́ng đi b&ocirc;̣, chạy b&ocirc;̣, đánh c&acirc;̀u l&ocirc;ng cũng r&acirc;́t thú vị và mát mẻ đ&acirc;́y.</p>\r\n\r\n<p>7. Ký túc xá thường được x&acirc;y ở g&acirc;̀n trường, n&ecirc;n vi&ecirc;̣c đ&ecirc;́n trường sẽ kh&ocirc;ng là v&acirc;́n đ&ecirc;̀ khó khăn. Nhưng đừng v&acirc;̣y mà ngủ nướng th&ecirc;m nhé.</p>\r\n\r\n<h3>Ti&ecirc;́p đ&ecirc;́n là v&ecirc;̀ sự b&acirc;́t ti&ecirc;̣n của ký túc xá</h3>\r\n\r\n<p>1. Vì đ&ocirc;ng người n&ecirc;n nơi đ&acirc;y sẽ r&acirc;́t &ocirc;̀n ào và ch&acirc;̣t ch&ocirc;̣i, khó có kh&ocirc;ng gian ri&ecirc;ng cho bạn học bài, ngủ trưa hoặc làm vi&ecirc;̣c cá nh&acirc;n, và sẽ cảm th&acirc;́y kh&ocirc;ng thoải mái.</p>\r\n\r\n<p>2. Kh&ocirc;ng gian nơi ở sẽ r&acirc;́t nhỏ, m&ocirc;̃i người chỉ vỏn vẹn 1 cái giường, kh&ocirc;ng có tủ đựng qu&acirc;̀n áo. Vì th&ecirc;́, bạn phải đ&ecirc;̉ qu&acirc;̀n áo trong vali và bỏ nó dưới g&acirc;̀m giường cho gọn.</p>\r\n\r\n<p>3. Nhà v&ecirc;̣ sinh thì sẽ tắm l&acirc;̀n lượt, bạn có th&ecirc;̉ b&ocirc;́c thăm tới lượt mình nhé! Và vì v&acirc;̣y cũng phải tìm lúc nào nhà v&ecirc;̣ sinh kh&ocirc;ng có ai thì giặt qu&acirc;̀n áo bằng tay, h&ocirc;m nào lười thì đem ra ti&ecirc;̣m giặt ủi cũng được. T&acirc;̀m 20k/3kg th&ocirc;i.</p>\r\n\r\n<p>4. Ở ký túc xá sẽ là m&ocirc;i trường r&acirc;́t phức tạp. 9 người 10 ý n&ecirc;n có th&ecirc;̉ g&acirc;y xích mích, cãi vã với bạn cùng phòng vì kh&ocirc;ng hi&ecirc;̉u nhau.</p>\r\n\r\n<p>5. Sống tập thể th&igrave; phải h&ograve;a hợp, thay đổi th&oacute;i quen sinh hoạt c&aacute; nh&acirc;n để ph&ugrave; hợp với tập thể. Đ&ocirc;i khi bạn l&agrave; người th&iacute;ch ngủ sớm, nhưng c&aacute;c bạn trong ph&ograve;ng lại thức khuya n&oacute;i cười đ&ugrave;a giỡn, thế l&agrave; l&acirc;u ng&agrave;y bạn lại biến th&agrave;nh &ldquo;c&uacute; đ&ecirc;m&rdquo; l&uacute;c n&agrave;o kh&ocirc;ng hay.</p>\r\n\r\n<p>6. Ký túc xá kh&ocirc;ng cho người ngoài v&ocirc;, vì th&ecirc;́ người th&acirc;n hay b&ocirc;́ mẹ đ&ecirc;́n thăm cũng kh&ocirc;ng có ch&ocirc;̃ nghỉ ngơi dành cho họ.</p>\r\n\r\n<p>7. Kh&ocirc;ng th&ecirc;̉ n&acirc;́u ăn trong ký túc xa, ngày nào cũng phải ăn cơm ti&ecirc;̣m.</p>\r\n\r\n<h2><strong>Sinh vi&ecirc;n ở phòng trọ</strong></h2>\r\n\r\n<p>N&ecirc;́u trường bạn chọn kh&ocirc;ng có ký túc xa, hoặc có nhưng bạn kh&ocirc;ng thích ở chung với nhi&ecirc;̀u người, thì vi&ecirc;̣c tìm phòng trọ là lựa chọn t&ocirc;́i ưu.</p>\r\n\r\n<h3>Phòng trọ sẽ có những ưu đi&ecirc;̉m sau đ&acirc;y</h3>\r\n\r\n<p>1. Phòng trọ sẽ dành cho các bạn sinh vi&ecirc;n y&ecirc;u thích sự thoải mái, kh&ocirc;ng thích sự gò bó, quản lý của các chú bảo v&ecirc;̣ ký túc xá.</p>\r\n\r\n<p>2. Phòng trọ có đ&acirc;̀y đủ ti&ecirc;̣n nghị, có kh&ocirc;ng gian ri&ecirc;ng và r&ocirc;̣ng rãi, bạn có th&ecirc;̉ làm những gì mình thích mà kh&ocirc;ng sợ ai nói nặng nhẹ gì cả.</p>\r\n\r\n<p>3. Đ&acirc;y là lựa chọn hàng đ&acirc;̀u của các bạn sinh vi&ecirc;n thích &lsquo;đi qu&acirc;̉y đ&ecirc;m&rsquo;, đi với bạn bè bu&ocirc;̉i t&ocirc;́i mu&ocirc;̣n mà kh&ocirc;ng ràng bu&ocirc;̣c thời gian. C&oacute; những nơi trọ quy định giờ giấc buổi tối phải về sớm, nhưng đa số l&agrave; kh&ocirc;ng quy định, bạn c&oacute; thể tự do đi du lịch, đi chơi đ&ecirc;m c&ugrave;ng bạn b&egrave; m&agrave; kh&ocirc;ng phải lo lắng.</p>\r\n\r\n<p>4. Kh&ocirc;ng phải dọn dẹp nếu h&ocirc;m đ&oacute; bạn &lsquo;lười&rsquo; v&igrave; bạn kh&ocirc;ng sống trong tập thể v&agrave; kh&ocirc;ng phải tu&acirc;n theo &lsquo;lịch dọn dẹp&rsquo; như ở k&yacute; t&uacute;c x&aacute;.</p>\r\n\r\n<p>5. C&oacute; người th&acirc;n đến thăm, bạn cũng c&oacute; nơi để cho họ nghỉ ngơi, ngủ qua đ&ecirc;m thay v&igrave; phải t&igrave;m 1 nơi trọ kh&aacute;c cho họ.</p>\r\n\r\n<p>6. Bạn cũng c&oacute; thể nấu ăn t&ugrave;y th&iacute;ch, chứ kh&ocirc;ng phải lo về việc đồ ăn b&ecirc;n ngo&agrave;i mất vệ sinh hay phải giấu diếm để nấu ăn như ở k&yacute; t&uacute;c x&aacute;.</p>\r\n\r\n<h3>B&ecirc;n cạnh những ưu đi&ecirc;̉m h&acirc;́p d&acirc;̃n thì cũng có những nhược đi&ecirc;̉m</h3>\r\n\r\n<p>1. V&acirc;́n đ&ecirc;̀ lớn nh&acirc;́t là chi phi. Ở trọ đắt hơn r&acirc;́t nhi&ecirc;̀u so với vi&ecirc;̣c ở ký túc xá. T&acirc;̀m 2 tri&ecirc;̣u - 3 tri&ecirc;̣u / tháng bao g&ocirc;̀m cả đi&ecirc;̣n, nước, wifi, chưa tính ti&ecirc;̀n ăn u&ocirc;́ng, trà sữa các thứ. Vi&ecirc;̣c ở trọ mắc n&ecirc;n bạn có th&ecirc;̉ ở ghép, tìm người quen ở chung với nhau khoảng 2-4 người/ phòng cho &lsquo;đỡ ti&ecirc;̀n&rsquo;.</p>\r\n\r\n<p>2. Phòng trọ sẽ kh&ocirc;ng an ninh như ở ký túc xá, bạn sẽ kh&ocirc;ng bi&ecirc;́t được chủ nhà là người như th&ecirc;́ nào, khu đó có an toàn hay kh&ocirc;ng, những người s&ocirc;́ng cùng khu có t&ocirc;́t kh&ocirc;ng.</p>\r\n\r\n<p>3. Ở trọ phải sắm sửa mọi thứ đ&ocirc;̀ dùng c&acirc;̀n thi&ecirc;́t cho mình. Có những nơi sẽ chỉ có căn phòng tr&ocirc;́ng và cái quạt, bạn phải mua giường, chi&ecirc;́u, g&ocirc;́i đ&acirc;̀y đủ cho mình.</p>\r\n\r\n<p>4. M&ocirc;̃i l&acirc;̀n chuy&ecirc;̉n trọ là m&ocirc;̣t v&acirc;́n đ&ecirc;̀, vì có r&acirc;́t nhi&ecirc;̀u v&acirc;̣t dụng.</p>\r\n\r\n<p>5. Vi&ecirc;̣c tìm trọ cũng r&acirc;́t khó khăn. Bạn sẽ phải m&acirc;́t m&ocirc;̣t ngày nghi&ecirc;n cứu tìm tòi xem nơi ở nào phù hợp và văn minh đ&acirc;́y.</p>\r\n\r\n<p>Tóm lại, n&ecirc;́u bạn thích đ&ocirc;ng vui và chi phí rẻ thì ký túc xá là lựa chọn hoàn hảo. Còn n&ecirc;́u bạn thích kh&ocirc;ng gian thoải mái và khá giả thì ở trọ là lựa chọn t&ocirc;́i ưu. Những chia sẻ tr&ecirc;n hi vọng sẽ góp ph&acirc;̀n giúp ích cho bạn trong vi&ecirc;̣c lựa chọn nơi ở khi bắt đ&acirc;̀u bước vào đời s&ocirc;́ng sinh vi&ecirc;n.</p>\r\n', '1608867861103.jpg', 1),
(12, 'XU HƯỚNG CHỌN PHÒNG TRỌ CỦA SINH VIÊN 2020', '<p>H&agrave;ng năm, một số lượng lớn sinh vi&ecirc;n đổ về c&aacute;c th&agrave;nh phố lớn để học tập v&agrave; l&agrave;m việc. Nhu cầu cần thiết của ở đ&acirc;y l&agrave; nh&agrave; ở, nếu bạn đang c&oacute; &yacute; định kinh doanh thu&ecirc; trọ nhắm tới sinh vi&ecirc;n th&igrave; h&atilde;y tham khảo b&agrave;i viết dưới đ&acirc;y nh&eacute;!</p>\r\n', '<p>H&agrave;ng năm, một số lượng lớn sinh vi&ecirc;n đổ về c&aacute;c th&agrave;nh phố lớn để học tập v&agrave; l&agrave;m việc. Nhu cầu cần thiết của ở đ&acirc;y l&agrave; nh&agrave; ở, nếu bạn đang c&oacute; &yacute; định kinh doanh thu&ecirc; trọ nhắm tới sinh vi&ecirc;n th&igrave; h&atilde;y tham khảo b&agrave;i viết dưới đ&acirc;y nh&eacute;!</p>\r\n\r\n<h2><strong>1. Gi&aacute; &quot;sinh vi&ecirc;n&quot;</strong></h2>\r\n\r\n<p>Sinh vi&ecirc;n đa phần l&agrave; kh&ocirc;ng c&oacute; thu nhập hoặc thu nhập rất thấp, phụ thuộc v&agrave;o bố mẹ l&agrave; nhiều. Ho&agrave;n cảnh gia đ&igrave;nh tuy từng người những hầu hết, sinh vi&ecirc;n đi t&igrave;m nh&agrave; trọ đều kh&ocirc;ng mong muốn một c&aacute;i gi&aacute; qu&aacute; cao. Th&ocirc;ng thường, c&aacute;c bạn sinh vi&ecirc;n sẽ ở theo nh&oacute;m từ 2-3 người để c&ugrave;ng chia tiền nh&agrave; cho rẻ.</p>\r\n\r\n<p>Mức thu&ecirc; c&oacute; thể chấp nhận được l&agrave; từ 1.000.000 đến 2.000.000 đồng một người/ một th&aacute;ng. Tương ứng với số tiền n&agrave;y, những căn ph&ograve;ng trọ sẽ c&oacute; diện t&iacute;ch khoảng từ 13-15 m2 một ph&ograve;ng, ở được từ 2-3 người. Gi&aacute; cho thu&ecirc; nh&agrave; th&igrave; sẽ đi k&egrave;m với gi&aacute; điện, nước.</p>\r\n\r\n<p>Hầu hết, sinh vi&ecirc;n muốn được ở những khu trọ t&iacute;nh điện, nước theo gi&aacute; nh&agrave; nước để kh&ocirc;ng phải chịu th&ecirc;m khoản ph&iacute; n&agrave;o m&agrave; chủ trọ đặt ra.</p>\r\n\r\n<h2><strong>2. Gần trường học</strong></h2>\r\n\r\n<p>Kh&ocirc;ng phải sinh vi&ecirc;n n&agrave;o khi l&ecirc;n th&agrave;nh phố học cũng c&oacute; thể chủ động trong phương tiện cũng như thạo đường x&aacute;. Th&ecirc;m v&agrave;o đ&oacute;, việc phải chịu ph&iacute; thu&ecirc; nh&agrave; hay điện nước h&agrave;ng th&aacute;ng khiến họ kh&ocirc;ng muốn chịu th&ecirc;m ph&iacute; đi lại cho việc đến trường v&igrave; đơn giản, họ c&oacute; quyền lựa chọn nơi ở ph&ugrave; hợp với điều kiện của m&igrave;nh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&oacute; một thực tế l&agrave;, những khu trọ gần trường th&igrave; gi&aacute; cho thu&ecirc; sẽ cao. Ch&iacute;nh v&igrave; thế, nhiều sinh vi&ecirc;n cũng kh&ocirc;ng qu&aacute; coi trọng ti&ecirc;u ch&iacute; n&agrave;y. Một khu trọ kh&ocirc;ng qu&aacute; hẻo l&aacute;nh, dễ d&agrave;ng di chuyển, gần bến xe bus, chợ hay si&ecirc;u thị ng&agrave;y nay được c&aacute;c bạn sinh vi&ecirc;n kh&aacute; quan t&acirc;m thay v&igrave; gần trường như trước đ&acirc;y.</p>\r\n\r\n<p>&nbsp;| Đọc th&ecirc;m:&nbsp;<a href=\"https://bandon.vn/kinh-doanh-nha-tro/luu-y-xay-nha-kinh-doanh-cho-thue-phong-tro\" target=\"_blank\">Lưu &yacute; 5 điều khi x&acirc;y nh&agrave; kinh doanh cho thu&ecirc; ph&ograve;ng trọ</a></p>\r\n\r\n<h2><strong>3. Cơ sở hạ tầng ổn định</strong></h2>\r\n\r\n<p>Sinh vi&ecirc;n l&agrave; một trong những đối tượng c&oacute; nhu cầu sử dụng mạng lớn nhất trong x&atilde; hội. Học tập hay c&ocirc;ng việc đều cần đến internet, cho n&ecirc;n, xu hướng t&igrave;m nh&agrave; trọ của họ hiện nay l&agrave; c&oacute; đường truyền mạng ổn định v&agrave; gi&aacute; cả hợp l&yacute;. C&oacute; thể dễ d&agrave;ng nhận thấy, an ninh cũng rất quan trọng.</p>\r\n\r\n<p>Nhiều vụ mất cắp xảy ra ở c&aacute;c khu trọ của sinh vi&ecirc;n khiến họ lo lắng về nơi ở của m&igrave;nh. Ch&iacute;nh v&igrave; thế, một nơi ở an n&igrave;nh được đảm bảo sẽ l&agrave; lựa chọn h&agrave;ng đầu của sinh vi&ecirc;n thay v&igrave; những khu trọ lộn xộn nhiều kẻ gian.</p>\r\n\r\n<h2><strong>4. Th&aacute;i độ của chủ trọ</strong></h2>\r\n\r\n<p>Điều n&agrave;y thực sự rất quan trọng trong khi sinh vi&ecirc;n đi t&igrave;m kiếm chỗ ở. Nếu bạn đang c&oacute; &yacute; định kinh doanh ng&agrave;nh dịch vụ n&agrave;y th&igrave; h&atilde;y nhớ rằng: h&atilde;y giữ cho m&igrave;nh th&aacute;i độ vui vẻ, h&ograve;a đồng, gi&uacute;p đỡ kh&aacute;ch thu&ecirc; nh&agrave; của m&igrave;nh nh&eacute;.</p>\r\n', '1608867880104.jpg', 0),
(13, 'MẸO SỬ DỤNG BÌNH NÓNG LẠNH AN TOÀN VÀ TIẾT KIỆM ĐIỆN MÙA ĐÔNG', '<p>Đ&atilde; v&agrave;o m&ugrave;a đ&ocirc;ng, nhu cầu sử dụng b&igrave;nh nước n&oacute;ng ng&agrave;y c&agrave;ng tăng cao. Từ việc tắm rửa đến c&aacute;c c&ocirc;ng việc trong nh&agrave; như rửa b&aacute;t, rửa rau, ... đều cần phải sử dụng nước n&oacute;ng để tr&aacute;nh c&aacute;i r&eacute;t. Nhu cầu sử dụng cao đồng nghĩa với việc lượng điện năng ti&ecirc;u thụ lớn. Sau đ&acirc;y l&agrave; phương ph&aacute;p để lu&ocirc;n c&oacute; nước n&oacute;ng sử dụng tiết kiệm điện tối đa.&nbsp;</p>\r\n', '<p>Đ&atilde; v&agrave;o m&ugrave;a đ&ocirc;ng, nhu cầu sử dụng b&igrave;nh nước n&oacute;ng ng&agrave;y c&agrave;ng tăng cao. Từ việc tắm rửa đến c&aacute;c c&ocirc;ng việc trong nh&agrave; như rửa b&aacute;t, rửa rau, ... đều cần phải sử dụng nước n&oacute;ng để tr&aacute;nh c&aacute;i r&eacute;t. Nhu cầu sử dụng cao đồng nghĩa với việc lượng điện năng ti&ecirc;u thụ lớn. Sau đ&acirc;y l&agrave; phương ph&aacute;p để lu&ocirc;n c&oacute; nước n&oacute;ng sử dụng tiết kiệm điện tối đa.&nbsp;</p>\r\n\r\n<h2><strong>Sử dụng b&igrave;nh n&oacute;ng lạnh an to&agrave;n</strong></h2>\r\n\r\n<p>Cẩn thận khi sử dụng nước n&oacute;ng v&agrave;o m&ugrave;a đ&ocirc;ng, đặc biệt trong nh&agrave; c&oacute; trẻ nhỏ. Nhiệt độ nước từ 48 độ trở l&ecirc;n c&oacute; thể g&acirc;y bỏng n&ecirc;n ch&uacute; &yacute; giữ c&aacute;c b&eacute; tr&aacute;nh xa v&ograve;i nước v&agrave;o m&ugrave;a đ&ocirc;ng.</p>\r\n\r\n<p>B&igrave;nh n&oacute;ng lạnh cần được bảo tr&igrave; h&agrave;ng năm để hoạt động tốt. Những b&igrave;nh nước sử dụng l&acirc;u ng&agrave;y c&oacute; nguy cơ r&ograve; rỉ điện cực k&igrave; nguy hiểm. Trước mỗi m&ugrave;a đ&ocirc;ng, gia đ&igrave;nh cần thu&ecirc; thợ kiểm tra hoặc d&ugrave;ng b&uacute;t thử điện chạm v&agrave;o v&ograve;i nước bằng sắt ở gần b&igrave;nh nhất.&nbsp;</p>\r\n\r\n<p>Giữ cho khu vực xung quanh b&igrave;nh đun nước n&oacute;ng sạch sẽ v&agrave; kh&ocirc;ng c&oacute; c&aacute;c&nbsp;vật liệu dễ ch&aacute;y.</p>\r\n\r\n<p>Khi cả gia đ&igrave;nh đi vắng, cần tắt b&igrave;nh n&oacute;ng lạnh để tr&aacute;nh g&acirc;y ra chập ch&aacute;y.</p>\r\n\r\n<p>Tắt b&igrave;nh n&oacute;ng lạnh trước khi sử dụng để tr&aacute;nh c&aacute;c nguy cơ bị điện giật.</p>\r\n\r\n<p><em>&nbsp;| Xem th&ecirc;m:&nbsp;<a href=\"https://bandon.vn/tin-tuc/thuc-hien-1-vai-dieu-de-tiet-kiem-dien-khi-o-nha-tro\" target=\"_blank\">Thực hiện 1 v&agrave;i điều để tiết kiệm điện khi ở nh&agrave; trọ</a></em></p>\r\n\r\n<h2><strong>Sử dụng b&igrave;nh n&oacute;ng lạnh tiết kiệm điện</strong></h2>\r\n\r\n<p>Chọn mua&nbsp;b&igrave;nh n&oacute;ng lạnh c&oacute; thể t&iacute;ch ph&ugrave; hợp với kh&ocirc;ng gian gia đ&igrave;nh.&nbsp;B&igrave;nh c&oacute; dung t&iacute;ch c&agrave;ng lớn th&igrave; khả năng ti&ecirc;u hao điện năng c&agrave;ng nhiều. &nbsp;V&iacute; dụ nếu gia đ&igrave;nh c&oacute; 4 người với 2 ph&ograve;ng tắm th&igrave;&nbsp;chỉ cần lắp loại b&igrave;nh 20 l&iacute;t.</p>\r\n\r\n<p>Lắp b&igrave;nh n&oacute;ng lạnh cố định tr&ecirc;n tường, c&aacute;ch mặt đất từ 1,7 - 2m để tr&aacute;nh thất tho&aacute;t nhiệt, tiết kiệm diện t&iacute;ch&nbsp;cũng như an to&agrave;n cho người trong gia đ&igrave;nh.</p>\r\n\r\n<p>C&agrave;i đặt b&igrave;nh ở khoảng 50 độ C. Cứ mỗi lượng giảm 5 độ C, gia đ&igrave;nh sẽ tiết kiệm được từ 3 - 5% lượng điện sử dụng.&nbsp;</p>\r\n\r\n<p>Bảo tr&igrave; b&igrave;nh n&oacute;ng lạnh thường xuy&ecirc;n gi&uacute;p loại bỏ cặn lắng ở trong b&igrave;nh. Qu&aacute; nhiều cặn lắng trong b&igrave;nh sẽ ảnh hưởng tới chất lượng hoạt động, qua đ&oacute; k&eacute;o d&agrave;i thời gian l&agrave;m n&oacute;ng nước.</p>\r\n\r\n<p>Kh&ocirc;ng n&ecirc;n bật b&igrave;nh n&oacute;ng lạnh cả ng&agrave;y v&igrave; dễ g&acirc;y qu&aacute; tải,&nbsp;giảm tuổi thọ của m&aacute;y m&agrave; c&ograve;n tốn điện. Tốt n&ecirc;n n&ecirc;n bật b&igrave;nh trong khoảng 15-20 ph&uacute;t trước khi sử dụng cho nước n&oacute;ng gi&agrave;. Sau đ&oacute; ngắt điện v&agrave; sử dụng như b&igrave;nh thường.</p>\r\n\r\n<p>Tắm bằng v&ograve;i sen gi&uacute;p tiết kiệm lượng nước v&agrave; điện rất lớn. Nếu sử dụng bồn tắm, diện t&iacute;ch bề mặt lớn khiến qu&aacute; tr&igrave;nh bốc hơi nhanh hơn. V&igrave; vậy, trong suốt qu&aacute; tr&igrave;nh tắm, bạn lại phải bổ sung th&ecirc;m nước n&oacute;ng.</p>\r\n', '1608867888105.jpg', 0),
(14, '10 GỢI Ý GIẢM TIỀN ĐIỆN KHI DÙNG ĐIỀU HÒA TRONG MÙA HÈ', '<p><em>Thời tiết n&oacute;ng bức m&ugrave;a h&egrave; l&agrave; thời điểm điều ho&agrave; toả s&aacute;ng, dễ khiến bạn bật ngửa v&igrave; tiền điện cuối th&aacute;ng. Đ&oacute; l&agrave; chưa để đến chuyện gi&aacute; điện cứ m&atilde;i tăng chưa. Vậy n&ecirc;n 10 gợi &yacute; sau đ&acirc;y sẽ gi&uacute;p bạn sử dụng điều h&ograve;a hiệu quả, tiết kiệm điện v&agrave; ho&aacute; đơn cuối th&aacute;ng cũng nhẹ nh&agrave;ng hơn (d&ugrave; vẫn tăng).</em></p>\r\n', '<p><strong>1. Đặt nhiệt độ cao nhất m&agrave; bạn vẫn cảm thấy thoải m&aacute;i</strong></p>\r\n\r\n<p>Quy tắc hoạt động cơ bản của điều h&ograve;a l&agrave; nếu bạn đặt nhiệt độ c&agrave;ng thấp, điện năng ti&ecirc;u thụ sẽ c&agrave;ng nhiều. Mấu chốt ở chỗ h&atilde;y đặt nhiệt độ cao nhất c&oacute; thể m&agrave; bạn vận cảm thấy thoải m&aacute;i, thay v&igrave; giảm thấp nhất c&oacute; thể v&agrave;... cho&aacute;ng v&igrave; tiền điện cuối th&aacute;ng phải trả. Giảm 1 độ C c&oacute; thể tiết kiệm cho bạn tới 10% tiền điện sử dụng điều h&ograve;a.</p>\r\n\r\n<p><strong>2. Mức ch&ecirc;nh lệch giữa nhiệt độ điều h&ograve;a v&agrave; ngo&agrave;i trời kh&ocirc;ng qu&aacute; 8 độ</strong></p>\r\n\r\n<p>H&atilde;ng điều h&ograve;a Daikin đưa ra khuyến c&aacute;o rằng bạn chỉ n&ecirc;n đặt nhiệt độ điều h&ograve;a trong khoảng 8 độ C thấp hơn so với nhiệt độ ngo&agrave;i trời. Chẳng hạn như ngo&agrave;i trời 34 độ th&igrave; n&ecirc;n đặt nhiệt độ điều h&ograve;a khoảng 26 độ hoặc cao hơn để tiết kiệm điện năng.</p>\r\n\r\n<p><strong>3. Tr&aacute;nh &aacute;nh s&aacute;ng mặt trời chiếu thẳng v&agrave;o ph&ograve;ng</strong></p>\r\n\r\n<p>&Aacute;nh s&aacute;ng mặt trời mang theo nhiệt độ sẽ khiến cho điều h&ograve;a của bạn phải hoạt động nhiều hơn để l&agrave;m m&aacute;t ph&ograve;ng. Sử dụng r&egrave;m che l&agrave; phương ph&aacute;p đơn giản gi&uacute;p bạn khắc phục điều n&agrave;y.</p>\r\n\r\n<p><strong>4. Xử l&yacute; ph&ograve;ng để kh&ocirc;ng l&agrave;m thất tho&aacute;t hơi lạnh</strong></p>\r\n\r\n<p>Từ tường cho đến trần nh&agrave;, việc xử l&yacute; c&aacute;ch ly nhiệt ph&ograve;ng tốt sẽ gi&uacute;p tr&aacute;nh thất tho&aacute;t hơi lạnh v&agrave; gi&uacute;p điều h&ograve;a hoạt động hiệu quả hơn. Dĩ nhi&ecirc;n l&agrave; &quot;tr&ugrave;ng tu&quot; to&agrave;n bộ kh&ocirc;ng chỉ tốn thời gian m&agrave; c&ograve;n tiền bạc, nếu kh&ocirc;ng qu&aacute; căng thẳng th&igrave; bạn c&oacute; thể đơn giản l&agrave; che lại những khe hở như khe cửa sổ, cửa ra v&agrave;o,...</p>\r\n\r\n<p><strong>5. Kh&ocirc;ng để c&aacute;c thiết bị toả nhiệt gần điều h&ograve;a</strong></p>\r\n\r\n<p>Hệ thống cảm biến trong điều h&ograve;a được thiết kế để duy tr&igrave; mức nhiệt độ m&ocirc;i trường như bạn đ&atilde; thiết lập, tuy nhi&ecirc;n ch&uacute;ng c&oacute; thể đưa ra kết quả kh&ocirc;ng ch&iacute;nh x&aacute;c nếu để gần c&aacute;c thiết bị toả nhiệt nhiều như TV, tủ lạnh,... Khi hoạt động nhiệt lượng toả ra từ c&aacute;c thiết bị n&agrave;y c&oacute; thể khiến điều h&ograve;a của bạn hoạt động nhiều hơn mức cần thiết.</p>\r\n\r\n<p><strong>6. Vệ sinh v&agrave; bảo tr&igrave; định kỳ</strong></p>\r\n\r\n<p>Để điều h&ograve;a hoạt động hiệu quả th&igrave; bạn cần phải vệ sinh v&agrave; bảo tr&igrave; n&oacute; định kỳ. Nếu điều h&ograve;a bị bẩn hoặc trục trặc th&igrave; n&oacute; sẽ sử dụng nhiều điện năng hơn khi hoạt động. Bạn c&oacute; thể tự l&agrave;m hoặc thu&ecirc; dịch vụ, chi ph&iacute; cũng chỉ khoảng 100k-200k m&agrave; th&ocirc;i.&nbsp;</p>\r\n\r\n<p><strong>7. Hẹn giờ tắt mở điều h&ograve;a khi đi ngủ</strong></p>\r\n\r\n<p>Bạn kh&ocirc;ng nhất thiết phải bật điều h&ograve;a li&ecirc;n tục suốt đ&ecirc;m, thay v&agrave;o đ&oacute; c&oacute; thể hẹn giờ để khi ngủ s&acirc;u m&aacute;y tự động tắt để tiết kiệm điện năng. Cũng cần nhắc lại rằng điều h&ograve;a kh&ocirc;ng cung cấp oxy, n&ecirc;n đừng lo rằng tắt m&aacute;y th&igrave; bạn sẽ bị ng&ocirc;p thở... v&igrave; nếu bị ngộp th&igrave; c&oacute; mở điều h&ograve;a vẫn ngộp.</p>\r\n\r\n<p><strong>8. Sử dụng quạt để đối lưu kh&ocirc;ng kh&iacute; lạnh</strong></p>\r\n\r\n<p>Khi kết hợp với quạt, đặc biệt l&agrave; quạt trần, luồng hơi từ điều h&ograve;a sẽ lan toả ra khắp ph&ograve;ng nhanh hơn, từ đ&oacute; tiết kiệm điện năng khi hoạt động.</p>\r\n\r\n<p>So với việc điều h&ograve;a phải hoạt động hết c&ocirc;ng suất th&igrave; sử dụng th&ecirc;m quạt sẽ tốn &iacute;t năng lượng hơn, nhưng cần lưu &yacute; l&agrave; tr&aacute;nh để gi&oacute; quạt thổi trực tiếp v&agrave;o người qu&aacute; l&acirc;u sẽ kh&ocirc;ng tốt cho sức khoẻ bạn nh&eacute;.</p>\r\n\r\n<p><strong>9. Tr&aacute;nh nội thất chắn luồng kh&iacute; lạnh từ m&aacute;y</strong></p>\r\n\r\n<p>Để c&oacute; thể l&agrave;m m&aacute;t ph&ograve;ng một c&aacute;ch nhanh v&agrave; hiệu quả nhất, bạn cần đảm bảo rằng luồng kh&iacute; lạnh ph&aacute;t ra từ m&aacute;y kh&ocirc;ng bị nội thất chắn lại. Trường hợp đơn giản th&igrave; bạn chỉ cần điều chỉnh lại c&aacute;nh gi&oacute;, c&ograve;n phức tạp th&igrave; c&oacute; thể bạn sẽ phải xắn tay &aacute;o v&agrave; di chuyện một số m&oacute;n đồ nội thất trong ph&ograve;ng.</p>\r\n\r\n<p><strong>10. Thay điều h&ograve;a đời mới với c&ocirc;ng nghệ tiết kiệm điện</strong></p>\r\n\r\n<p>điều h&ograve;a l&agrave; thiết bị rất bền, với nhiều m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể hoạt động l&ecirc;n đến 10 năm. Nhưng ngay cả vẫn hoạt động, hiệu suất của điều h&ograve;a cũng sẽ suy giảm theo thời gian v&agrave; điện năng cũng sẽ ti&ecirc;u tốn nhiều hơn c&aacute;c điều h&ograve;a đời mới. Ch&iacute;nh v&igrave; vậy m&agrave; nếu điều h&ograve;a đ&atilde; qu&aacute; cũ th&igrave; bạn n&ecirc;n c&acirc;n nhắc thay thế n&oacute; bằng điều h&ograve;a đời mới t&iacute;ch hợp c&aacute;c t&iacute;nh năng tiết kiệm điện.</p>\r\n', '1608867896106.jpg', 0),
(15, ' NGƯỜI THUÊ TRỌ THAN TRỜI ', '<p>C&aacute;c chủ cho thu&ecirc; trọ cho rằng kh&ocirc;ng nắm được c&aacute;ch t&iacute;nh tiền điện. C&ograve;n c&aacute;c C&ocirc;ng ty điện lực lại cho rằng đ&atilde; tuy&ecirc;n truyền, l&agrave;m hết tr&aacute;ch nhiệm.</p>\r\n', '<p><em>C&aacute;c chủ cho thu&ecirc; trọ cho rằng kh&ocirc;ng nắm được c&aacute;ch t&iacute;nh tiền điện. C&ograve;n c&aacute;c C&ocirc;ng ty điện lực lại cho rằng đ&atilde; tuy&ecirc;n truyền, l&agrave;m hết tr&aacute;ch nhiệm.</em></p>\r\n\r\n<h2><strong>Chủ trọ đổ lỗi cho Cty điện lực</strong></h2>\r\n\r\n<p>Theo phản &aacute;nh, Chị Nguyễn Thị Huyền, th&ocirc;n Giang Liễu, x&atilde; Phương Liễu, (Quế V&otilde;, Bắc Ninh) phản &aacute;nh, gia đ&igrave;nh c&oacute; 10 ph&ograve;ng trọ cho c&ocirc;ng nh&acirc;n khu c&ocirc;ng nghiệp (KCN) thu&ecirc;, ti&ecirc;u thụ từ 400 - 500 kWh/th&aacute;ng. Theo gi&aacute; điện cũ, gia đ&igrave;nh chị thu của c&ocirc;ng nh&acirc;n khoảng 3.000 đồng/ kWh. Từ th&aacute;ng 4/2019, gi&aacute; điện tăng, chị thu của c&ocirc;ng nh&acirc;n 3.500 đồng/ kWh v&agrave; ngay lập tức bị c&ocirc;ng nh&acirc;n phản ứng.</p>\r\n\r\n<p>Theo chị Huyền, với gi&aacute; điện mới, gia đ&igrave;nh chị chịu gi&aacute; lũy tiến, cao nhất 2.927 đồng/ kWh (t&iacute;nh cả thuế xấp xỉ 3.200 đồng/ kWh). &ldquo;Ch&uacute;ng t&ocirc;i buộc phải t&iacute;nh tăng tiền điện của c&ocirc;ng nh&acirc;n l&ecirc;n 3.500 đồng/số, bởi c&ograve;n phải chịu tiền bơm nước, chiếu s&aacute;ng v&agrave; c&aacute;c chi ph&iacute; sửa chữa&rdquo; - chị Huyền n&oacute;i.</p>\r\n\r\n<p>Theo chị Huyền, với c&aacute;ch t&iacute;nh lũy tiến như thế cả chủ trọ v&agrave; c&ocirc;ng nh&acirc;n đều chịu thiệt. Trong khi đ&oacute;, từ trước đến nay, gia đ&igrave;nh chị kh&ocirc;ng nắm được chủ trương k&ecirc; khai, t&iacute;nh gi&aacute; điện cho người thu&ecirc; trọ. &ldquo;Cty điện lực kh&ocirc;ng c&oacute; c&ocirc;ng văn hướng dẫn cho người d&acirc;n triển khai. Trong khi gi&aacute; điện tăng lại c&oacute; th&ocirc;ng b&aacute;o đến từng gia đ&igrave;nh&rdquo;, chị Huyền n&oacute;i.</p>\r\n\r\n<p>Theo chị Huyền, th&ocirc;ng qua b&aacute;o Tiền Phong, gia đ&igrave;nh mới biết c&aacute;ch t&iacute;nh điện gi&aacute; rẻ cho người thu&ecirc; trọ. &ldquo;Ch&uacute;ng t&ocirc;i hỏi nh&acirc;n vi&ecirc;n thu tiền điện, họ n&oacute;i kh&ocirc;ng biết, phải xuống Cty điện để hỏi. B&acirc;y giờ, ch&uacute;ng t&ocirc;i muốn biết để đăng k&yacute; cần phải thực hiện thủ tục thế n&agrave;o? B&ecirc;n điện lực cần th&ocirc;ng b&aacute;o hướng dẫn về đơn từ, mẫu như thế n&agrave;o. Khu vực n&agrave;y c&oacute; đến h&agrave;ng ngh&igrave;n hộ cho thu&ecirc; trọ, nếu ai cũng đi hỏi, giải quyết kiểu thế n&agrave;o?&rdquo;, chị Huyền đề nghị.</p>\r\n\r\n<p>Anh Trần Văn Miện cũng c&oacute; hơn chục ph&ograve;ng cho thu&ecirc; tại th&ocirc;n Giang Liễu (Phương Liễu, Quế V&otilde;) cho biết, gia đ&igrave;nh anh mỗi th&aacute;ng ti&ecirc;u thụ hết khoảng 500 kWh v&agrave; chưa nắm được c&aacute;c ch&iacute;nh s&aacute;ch, chủ trương hay c&aacute;ch t&iacute;nh tiền điện theo c&aacute;c mức kh&aacute;c nhau. &ldquo;Chỉ biết gi&aacute; điện nh&agrave; nước tăng th&igrave; ch&uacute;ng t&ocirc;i cũng phải tăng, m&agrave; tăng sẽ ảnh hưởng đến c&ocirc;ng nh&acirc;n. Họ l&agrave;m xa nh&agrave;, tiền điện nước tăng sẽ t&aacute;c động ngay đến cuộc sống v&agrave; chủ nh&agrave; trọ cũng bị li&ecirc;n đới. Ch&uacute;ng t&ocirc;i kh&ocirc;ng thu l&ecirc;n th&igrave; bị thiệt, m&agrave; thu l&ecirc;n th&igrave; c&ocirc;ng nh&acirc;n c&oacute; &yacute; kiến. Trong khi, điện lực cũng kh&ocirc;ng giải th&iacute;ch l&agrave;m thế n&agrave;o để c&oacute; lợi cho chủ trọ, c&oacute; lợi cho c&ocirc;ng nh&acirc;n&rdquo;, anh Miện n&oacute;i.</p>\r\n\r\n<p>&nbsp;| Đọc th&ecirc;m:&nbsp;<a href=\"https://bandon.vn/kinh-nghiem/gia-dien-nha-nuoc\" target=\"_blank\">Ở THU&Ecirc; NH&Agrave; TRỌ - C&Aacute;CH T&Iacute;NH TIỀN ĐIỆN THEO QUY ĐỊNH MỚI CỦA NH&Agrave; NƯỚC</a></p>\r\n\r\n<h2><strong>Cty điện lực: Đ&atilde; l&agrave;m hết tr&aacute;ch nhiệm?</strong></h2>\r\n\r\n<p>&Ocirc;ng Vũ Sỹ Tư, GĐ Điện lực Quế V&otilde; cho biết: &ldquo;Ở Quế V&otilde;, ngay từ đầu ch&uacute;ng t&ocirc;i đ&atilde; tổ chức họp c&aacute;c b&ecirc;n, c&aacute;c sở ban ng&agrave;nh,UBND huyện, x&atilde;, đại diện chủ trọ, trưởng th&ocirc;n&hellip; Họ kh&ocirc;ng chọn đăng k&yacute; hợp đồng gi&aacute; định mức số hộ để t&iacute;nh gi&aacute; điện theo quy định cho người thu&ecirc;. Ngo&agrave;i ra, kh&ocirc;ng giống với huyện kh&aacute;c, ở Quế V&otilde;, c&ocirc;ng nh&acirc;n họ nhảy việc nhiều, nay ở chỗ n&agrave;y, mai chuyển chỗ kh&aacute;c n&ecirc;n để &aacute;p dụng phương thức t&iacute;nh theo số hộ khiến c&aacute;c chủ trọ thấy phức tạp. Nếu đăng k&yacute; c&ocirc;ng nh&acirc;n chuyển đến, chuyển đi, chủ trọ phải thường xuy&ecirc;n k&yacute; lại hợp đồng thu&ecirc;, hợp đồng điện, khai b&aacute;o tạm tr&uacute;&hellip;&rdquo;. &Ocirc;ng Tư cho biết th&ecirc;m, ngo&agrave;i tuy&ecirc;n truyền vận động của c&aacute;c b&ecirc;n, kh&aacute;ch h&agrave;ng mua điện cần phải t&igrave;m hiểu v&agrave; chủ động ra đăng k&yacute; để đảm bảo quyền lợi.</p>\r\n\r\n<p>&Ocirc;ng Dương Văn Tấn, Ph&oacute; trưởng ph&ograve;ng Kinh doanh (Điện lực Quế V&otilde;) cho biết, từ th&aacute;ng 8/2018, ngo&agrave;i th&ocirc;ng b&aacute;o, Cty c&ograve;n chủ động xuống từng hộ tuy&ecirc;n truyền, c&oacute; nhiều biện ph&aacute;p siết chặt gi&aacute; b&aacute;n đến c&ocirc;ng nh&acirc;n. Số liệu thống k&ecirc; gi&aacute; b&aacute;n điện cho người thu&ecirc; nh&agrave; để ở tới th&aacute;ng 3/2019 tại Quế V&otilde; cho thấy, trong tổng số 1.298 hộ c&oacute; nh&agrave; cho thu&ecirc; tr&ecirc;n địa b&agrave;n c&oacute; tới 1.177 hộ k&yacute; hợp đồng theo gi&aacute; sinh hoạt bậc 3 (c&aacute;ch t&iacute;nh gi&aacute; điện cho người thu&ecirc; trọ - PV).</p>\r\n\r\n<p><em><strong>Thủ tướng y&ecirc;u cầu kiểm tra việc tăng gi&aacute; điện</strong></em></p>\r\n\r\n<p><em>Ng&agrave;y 3/5, Văn ph&ograve;ng Ch&iacute;nh phủ c&oacute; văn bản truyền đạt &yacute; kiến của Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c gửi Thanh Tra Ch&iacute;nh phủ, Bộ T&agrave;i ch&iacute;nh, Bộ C&ocirc;ng Thương y&ecirc;u cầu kiểm tra việc điều chỉnh gi&aacute; điện.Văn bản n&ecirc;u r&otilde;, thời gian qua một số phương tiện th&ocirc;ng tin đưa tin việc điều chỉnh mức gi&aacute; b&aacute;n điện v&agrave; thu tiền điện g&acirc;y nhiều bức x&uacute;c cho người d&acirc;n, Thủ tướng y&ecirc;u cầu Thanh tra Ch&iacute;nh phủ chủ tr&igrave;, phối hợp với Bộ T&agrave;i ch&iacute;nh, Bộ C&ocirc;ng Thương v&agrave; c&aacute;c cơ quan li&ecirc;n quan kiểm tra việc điều chỉnh mức gi&aacute; b&aacute;n điện, phương ph&aacute;p t&iacute;nh gi&aacute; v&agrave; việc thu tiền điện thời gian qua, l&agrave;m r&otilde; đ&uacute;ng, sai v&agrave; b&aacute;o c&aacute;o Thủ tướng trong th&aacute;ng 6-2019.</em></p>\r\n\r\n<p><em>Như Tiền Phong phản &aacute;nh, chỉ mới v&agrave;o đầu cao điểm m&ugrave;a nắng nhưng nhiều hộ d&acirc;n ở ph&aacute;t hoảng khi nhận ho&aacute; đơn tiền điện tăng gấp đ&ocirc;i, thậm ch&iacute; hơn 3 lần so với những th&aacute;ng trước. Về nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave;m cho ho&aacute; đơn tiền điện tăng cao, EVN cho rằng do lượng ti&ecirc;u thụ tăng cộng số ng&agrave;y sử dụng điện trong c&aacute;c kỳ h&oacute;a đơn th&aacute;ng 4 (31 ng&agrave;y) nhiều hơn so với kỳ h&oacute;a đơn th&aacute;ng 3 (chỉ c&oacute; 28 ng&agrave;y). Như vậy số ng&agrave;y sử dụng điện nhiều hơn n&ecirc;n lượng điện năng ti&ecirc;u thụ sẽ nhiều hơn kết hợp c&aacute;c yếu tố điện sử dụng tăng theo quy luật h&agrave;ng năm v&agrave;o những th&aacute;ng h&egrave; cộng với việc gi&aacute; b&aacute;n điện điều chỉnh l&agrave;m tổng số tiền điện của kỳ h&oacute;a đơn th&aacute;ng 4/2019 của c&aacute;c hộ d&acirc;n phải trả tăng hơn so với th&aacute;ng trước&hellip;</em></p>\r\n\r\n<p>&nbsp;</p>\r\n', '1608867903107.jpg', 0),
(16, 'CÒN ĐÓ NHỮNG NGUY CƠ XẤU ĐANG RÌNH RẬP NGƯỜI THUÊ TRỌ', '<p>Với những sinh vi&ecirc;n, học sinh, người lao động ngoại tỉnh về H&agrave; Nội học tập, lao động... việc t&igrave;m kiếm một nơi để y&ecirc;n t&acirc;m ở trọ gần như l&agrave; điều kiện ti&ecirc;n quyết.</p>\r\n', '<p><em>Với những sinh vi&ecirc;n, học sinh, người lao động ngoại tỉnh về H&agrave; Nội học tập, lao động... việc t&igrave;m kiếm một nơi để y&ecirc;n t&acirc;m ở trọ gần như l&agrave; điều kiện ti&ecirc;n quyết. Song, với cung c&aacute;ch quản l&yacute; c&ograve;n nhiều lỏng lẻo, c&ugrave;ng việc thiếu cảnh gi&aacute;c của bản th&acirc;n người thu&ecirc; nh&agrave; đ&atilde; biến nh&agrave; trọ trở th&agrave;nh nơi tiềm ẩn nhiều nguy cơ như trộm cắp, giết người, hiếp d&acirc;m.</em></p>\r\n\r\n<h2><strong>Từ việc bị lừa đảo khi thu&ecirc; ph&ograve;ng...</strong></h2>\r\n\r\n<p>&ldquo;Nếu l&agrave; sinh vi&ecirc;n, người ngoại tỉnh mới về H&agrave; Nội lần đầu m&agrave; thu&ecirc; được nh&agrave; &ldquo;ngon, bổ, rẻ&rdquo; th&igrave; đ&uacute;ng l&agrave; phải quay v&agrave;o &ocirc;... may mắn&rdquo; - Tuấn Long, cựu sinh vi&ecirc;n Đại học Quốc gia H&agrave; Nội ch&eacute;p miệng đ&uacute;c kết, sau những lần bị lừa cay đắng.</p>\r\n\r\n<p>Mấy năm trước, phấn khởi sau khi biết tin đỗ đại học, Long một m&igrave;nh tay x&aacute;ch n&aacute;ch mang nhập trường. Trước đ&oacute; Long đ&atilde; nhờ được người thu&ecirc; gi&uacute;p một căn ph&ograve;ng kh&aacute; &ldquo;tươm&rdquo; song gi&aacute; cũng hơi &ldquo;ch&aacute;t&rdquo; ở quận Cầu Giấy, H&agrave; Nội.</p>\r\n\r\n<p>Sau khoảng 2 th&aacute;ng nhập học, nghe mấy người bạn c&ugrave;ng lớp khuy&ecirc;n, Long v&agrave;o mấy group tr&ecirc;n mạng x&atilde; hội Facebook đăng t&igrave;m người ở gh&eacute;p. Ng&agrave;y trước đăng, ng&agrave;y sau đ&atilde; thấy một nam &ldquo;inbox&rdquo; xin được ở c&ugrave;ng. Nam sinh vi&ecirc;n n&agrave;y n&oacute;i đang học năm cuối trường GTVT, qu&ecirc; ở Th&aacute;i Nguy&ecirc;n.</p>\r\n\r\n<p>&ldquo;Anh chuẩn bị đi thực tập tốt nghiệp n&ecirc;n đồ đạc chỉ gọn nhẹ thế n&agrave;y th&ocirc;i&rdquo; - g&atilde; bảo với Long sau khi x&aacute;ch đ&uacute;ng một vali đến ở c&ugrave;ng. Đồng thời, g&atilde; cũng xin khất tiền ph&ograve;ng đến cuối th&aacute;ng.</p>\r\n\r\n<p>Buổi chiều ngay h&ocirc;m sau đi học về, Long đ&atilde; cảm thấy... l&agrave; lạ khi thấy cửa giả kh&ocirc;ng kh&oacute;a. Bước v&agrave;o ph&ograve;ng th&igrave;... &ocirc;i th&ocirc;i bao nhi&ecirc;u của nả bố mẹ, bạn b&egrave; sắm sửa ch&uacute;c mừng cho Long đều đ&atilde; kh&ocirc;ng c&aacute;nh m&agrave; bay. N&agrave;o l&agrave; m&aacute;y t&iacute;nh x&aacute;ch tay, tivi, tủ lạnh, quạt, đ&agrave;i CD, thậm ch&iacute; cả... điều h&ograve;a nhiệt độ g&atilde; cũng th&aacute;o hết. H&agrave;ng x&oacute;m x&aacute;c nhận người thu&ecirc; ph&ograve;ng h&ocirc;m trước đ&atilde; thu&ecirc; &ocirc; t&ocirc; từ s&aacute;ng đến trưa dọn sạch đồ. &ldquo;Ch&uacute;ng t&ocirc;i lại tưởng hai anh em chuyển đi chỗ kh&aacute;c!&rdquo;.</p>\r\n\r\n<p>Cho&aacute;ng v&aacute;ng trước hiện thực phũ ph&agrave;ng, Long vội bấm điện thoại cho anh sinh vi&ecirc;n năm cuối - chỉ thấy tiếng &ldquo;&ograve; &iacute; e&rdquo;. L&ecirc;n mạng x&atilde; hội t&igrave;m th&igrave; Facebook của Long đ&atilde; bị chặn. Đến Cơ quan c&ocirc;ng an tr&igrave;nh b&aacute;o, Long chỉ c&oacute; v&agrave;i th&ocirc;ng tin về đối tượng (m&agrave; cũng kh&ocirc;ng biết đ&uacute;ng hay sai). Ngay cả ảnh ch&acirc;n dung đối tượng cậu ch&agrave;ng cũng kh&ocirc;ng c&oacute; nốt!</p>\r\n\r\n<p>Đồng cảnh ngộ, B&ugrave;i Thị Phương (sinh vi&ecirc;n Đại học Khoa học x&atilde; hội v&agrave; nh&acirc;n văn) cũng phải nhận tr&aacute;i đắng khi cho người &ldquo;lạ&rdquo; ở c&ugrave;ng. &ldquo;Buổi s&aacute;ng em đăng tin t&igrave;m người ở gh&eacute;p, buổi chiều đ&atilde; c&oacute; 1 bạn nữ xinh xắn đến xin ở c&ugrave;ng. V&igrave; ph&ograve;ng em cũng đủ đồ cả rồi n&ecirc;n bạn ấy chỉ việc mang quần &aacute;o đến ở th&ocirc;i. Hai đứa th&igrave; lịch học kh&ocirc;ng tr&ugrave;ng nhau, bạn kia ở được 2 h&ocirc;m th&igrave; mất h&uacute;t c&ugrave;ng với tiền, một số vật dụng c&aacute; nh&acirc;n v&agrave; chiếc laptop y&ecirc;u qu&yacute; của em.</p>\r\n\r\n<p>Đ&oacute; trở th&agrave;nh b&agrave;i học đau l&ograve;ng của những ng&agrave;y đầu l&agrave;m sinh vi&ecirc;n đại học của em. Chỉ v&igrave; tin người qu&aacute; m&agrave; sau đ&oacute; em phải vay mượn khắp nơi để mua lại phương tiện học tập. Chưa l&agrave;m ra được xu n&agrave;o m&agrave; mất cả&rdquo; - Phương cay đắng thốt l&ecirc;n.</p>\r\n\r\n<h2><strong>...đến những vụ hiếp d&acirc;m, giết người</strong></h2>\r\n\r\n<p>Như B&aacute;o ANTG đ&atilde; th&ocirc;ng tin, tối ng&agrave;y 3-6-2018, Đặng Thị Cẩm T., sinh vi&ecirc;n năm cuối Đại học S&acirc;n khấu điện ảnh đến khu nh&agrave; trọ tại ng&otilde; 12/2 Nguyễn Ph&uacute;c Lai (phường &Ocirc; Chợ Dừa, Đống Đa, H&agrave; Nội) để t&igrave;m ph&ograve;ng. Chị Cẩm T. kh&ocirc;ng thể ngờ được rằng, buổi đi xem ph&ograve;ng trọ để l&agrave;m nh&agrave; kho cho c&ocirc;ng việc kinh doanh sắp tới lại trở th&agrave;nh buổi tối &ldquo;định mệnh&rdquo;.</p>\r\n\r\n<p>Được biết, trước khi đến, Cẩm T. đ&atilde; li&ecirc;n hệ với chủ căn nh&agrave; l&agrave; anh Nguyễn Như T. (29 tuổi, tr&uacute; tại quận Cầu Giấy, H&agrave; Nội) để trao đổi về gi&aacute; tiền cũng như thời gian đến xem nh&agrave;. Anh T. đ&atilde; gọi điện cho Nguyễn Anh T&uacute; (35 tuổi, tr&uacute; tại Quan Thổ 3, Đống Đa, H&agrave; Nội; hiện đang thu&ecirc; một ph&ograve;ng trọ tr&ecirc;n tầng 3 ng&ocirc;i nh&agrave;) nhờ T&uacute; mở cửa ph&ograve;ng cho Cẩm T. xem. Căn ph&ograve;ng n&agrave;y hiện T&uacute; đang thu&ecirc;, nằm trong ng&ocirc;i nh&agrave; 4 tầng, c&oacute; 12 ph&ograve;ng cho thu&ecirc; trọ do anh Như T. quản l&yacute;.</p>\r\n\r\n<p>Biết được th&ocirc;ng tin chị T. đi xem ph&ograve;ng trọ một m&igrave;nh, T&uacute; nảy sinh &yacute; đồ xấu, thủ sẵn một vi&ecirc;n gạch. Khoảng 21 giờ c&ugrave;ng ng&agrave;y, chị T. đến khu trọ. Thừa l&uacute;c chị Cẩm T. đang ng&oacute; nghi&ecirc;ng th&igrave; bất ngờ T&uacute; đập một nh&aacute;t v&agrave;o đầu v&agrave; d&ugrave;ng tay b&oacute;p cổ k&eacute;o chị T. v&agrave;o ph&ograve;ng với &yacute; định hiếp d&acirc;m c&ocirc; g&aacute;i xinh đẹp.</p>\r\n\r\n<p>Thấy chị T. chống cự quyết liệt, g&atilde; &ldquo;y&ecirc;u r&acirc;u xanh&rdquo; đ&atilde; d&ugrave;ng gạch đập th&ecirc;m nhiều nh&aacute;t v&agrave;o đầu khiến chị Cẩm T. ngất đi v&agrave; thực hiện bằng được h&agrave;nh vi hiếp d&acirc;m. Sau khi thỏa m&atilde;n th&uacute; t&iacute;nh, T&uacute; vẫn đ&aacute;nh một giấc ngon l&agrave;nh.</p>\r\n\r\n<p>Chiều h&ocirc;m sau th&igrave; g&atilde; dậy lục so&aacute;t lấy hết tiền trong t&uacute;i chị Cẩm T. rồi lang thang tại khu vực phường Quan Hoa (Cầu Giấy, H&agrave; Nội) th&igrave; bị lực lượng C&ocirc;ng an bắt giữ. Tới đ&acirc;y chắc chắn T&uacute; sẽ phải chịu những h&igrave;nh phạt nghi&ecirc;m khắc của ph&aacute;p luật cho h&agrave;nh vi mất hết nh&acirc;n t&iacute;nh của hắn.</p>\r\n\r\n<p>C&aacute;ch đ&acirc;y &iacute;t năm, tr&ecirc;n địa b&agrave;n phường Nh&acirc;n Ch&iacute;nh (quận Thanh Xu&acirc;n, H&agrave; Nội) cũng xảy ra một vụ hiếp d&acirc;m khi t&igrave;m nh&agrave; trọ. Một tối chị Phạm Thị T. (SN 1989, tr&uacute; tại Vĩnh Ph&uacute;c) đến L&agrave;ng sinh vi&ecirc;n Hacinco (Nh&acirc;n Ch&iacute;nh, Thanh Xu&acirc;n) để hỏi ph&ograve;ng trọ. Khi chị đang ở s&acirc;n, đối tượng Trịnh Đăng Giang (SN 1977, tr&uacute; phường Nh&acirc;n Ch&iacute;nh, quận Thanh Xu&acirc;n, đang thu&ecirc; trọ tại L&agrave;ng sinh vi&ecirc;n Hacinco) đ&atilde; giả vờ l&agrave; nh&acirc;n vi&ecirc;n quản l&yacute; ở đ&acirc;y, bảo chị T. l&ecirc;n ph&ograve;ng n&oacute;i chuyện v&agrave; sẽ gi&uacute;p đỡ chị T. thu&ecirc; ph&ograve;ng thật nhanh, gi&aacute; rẻ.</p>\r\n\r\n<p>Chẳng ngờ chị T. vừa bước v&agrave;o ph&ograve;ng th&igrave; Giang lập tức kh&oacute;a tr&aacute;i cửa, nh&eacute;t giẻ v&agrave;o mồm rồi giở tr&ograve; đồi bại với nạn nh&acirc;n. Sau khi thỏa m&atilde;n th&uacute; t&iacute;nh, chị T. được hắn thả ra. Ng&agrave;y h&ocirc;m sau chị T. đ&atilde; tố c&aacute;o h&agrave;nh vi của Giang l&ecirc;n cơ quan C&ocirc;ng an. Đối tượng bị bắt ngay sau đ&oacute;.</p>\r\n\r\n<p>Thực tế, việc quản l&yacute; người ra người v&agrave;o tại kh&ocirc;ng &iacute;t khu nh&agrave; trọ tr&ecirc;n địa b&agrave;n TP H&agrave; Nội hiện vẫn rất lỏng lẻo. Trừ một số khu c&oacute; bảo vệ th&igrave; đa phần người lạ vẫn c&oacute; thể đến ăn, ngủ, nghỉ nhiều ng&agrave;y tại nh&agrave; trọ của bạn b&egrave;, người th&acirc;n, thậm ch&iacute; người mới quen biết trước đ&oacute; &iacute;t ph&uacute;t. V&agrave;, cũng do nhiều chủ nh&agrave; trọ c&ograve;n thiếu tr&aacute;ch nhiệm trong quản l&yacute; đ&atilde; dẫn đến những hậu quả kh&oacute; lường. Kẻ giết người đ&atilde; tự tiện đột nhập v&agrave;o ph&ograve;ng trọ của nạn nh&acirc;n v&agrave;o nửa đ&ecirc;m, s&aacute;ng sớm để ra tay.</p>\r\n\r\n<p>C&ograve;n nhớ, tại phố Vũ Xu&acirc;n Thiều (S&agrave;i Đồng, Long Bi&ecirc;n, H&agrave; Nội) xảy ra một vụ s&aacute;t hại bạn g&aacute;i d&atilde; man. Rạng s&aacute;ng ng&agrave;y 15-3-2017 đối tượng L&ecirc; Mạnh To&agrave;n (SN 1982, qu&ecirc; qu&aacute;n Tĩnh Gia, Thanh H&oacute;a, tạm tr&uacute; tại tỉnh Hưng Y&ecirc;n) m&ograve; đến ph&ograve;ng trọ của chị Nguyễn Thị T.L. (SN 1993, tạm tr&uacute; tại phố Vũ Xu&acirc;n Thiều) để n&oacute;i chuyện. Trước đ&oacute;, chị T.L. v&agrave; To&agrave;n đ&atilde; c&oacute; một thời gian mặn nồng song gần đ&acirc;y chị L. muốn chấm dứt quan hệ.</p>\r\n\r\n<p>Khoảng 7 giờ 30 ph&uacute;t c&ugrave;ng ng&agrave;y, khi chị L. đưa con g&aacute;i đi gửi trẻ về, giữa chị L. v&agrave; To&agrave;n đ&atilde; xảy ra m&acirc;u thuẫn. To&agrave;n muốn nối lại t&igrave;nh cảm, song chị L. kh&ocirc;ng chấp nhận. To&agrave;n đ&atilde; d&ugrave;ng con dao nhọn đ&acirc;m khiến chị L. tử vong. Sau đ&oacute;, To&agrave;n d&ugrave;ng dao tự đ&acirc;m v&agrave;o ngực tr&aacute;i, bị trọng thương v&agrave; cũng tử vong tại bệnh viện.</p>\r\n\r\n<p>Cuối năm 2012, chị Trần Thị Huyền T. (SN 1993, qu&ecirc; Thanh H&oacute;a) cũng bị người y&ecirc;u cũ đột nhập v&agrave;o ph&ograve;ng trọ l&uacute;c nửa đ&ecirc;m rồi s&aacute;t hại. Theo t&agrave;i liệu từ cơ quan C&ocirc;ng an, nguy&ecirc;n nh&acirc;n của vụ việc l&agrave; do chị T. đ&ograve;i chia tay nhưng Th&agrave;nh kh&ocirc;ng chấp nhận v&agrave; nảy sinh &yacute; định s&aacute;t hại người y&ecirc;u m&igrave;nh.</p>\r\n\r\n<p>Ng&agrave;y 20-11-2012, Th&agrave;nh thu&ecirc; nh&agrave; nghỉ đối diện ph&ograve;ng trọ của người y&ecirc;u m&igrave;nh ở nh&agrave; số 24, ng&otilde; 251 đường Nguyễn Khang (Y&ecirc;n H&ograve;a, Cầu Giấy, H&agrave; Nội). Chờ đến nửa đ&ecirc;m, Th&agrave;nh tr&egrave;o cổng v&agrave;o khu trọ, m&ograve; l&ecirc;n ph&ograve;ng của chị T. thu&ecirc; ở trọ c&ugrave;ng chị họ t&ecirc;n H&ograve;a. Khi đi, Th&agrave;nh mang theo một con dao gọt hoa quả.</p>\r\n\r\n<p>Bị chị T. ph&aacute;t hiện, la h&eacute;t, Th&agrave;nh lao đến, đ&egrave; chị T. xuống giường, d&ugrave;ng dao đ&acirc;m li&ecirc;n tiếp. Do c&uacute; đ&acirc;m mạnh, lưỡi dao văng khỏi c&aacute;n. Lợi dụng cơ hội đ&oacute;, chị T. lao ra ngo&agrave;i k&ecirc;u cứu. Th&agrave;nh quay sang cầm lưỡi dao g&iacute; v&agrave;o cổ người y&ecirc;u, k&eacute;o chị T. v&agrave;o khu vực giường ngủ.</p>\r\n\r\n<p>L&uacute;c n&agrave;y, chủ nh&agrave; trọ c&ugrave;ng một số người d&acirc;n chạy sang ứng cứu. Th&agrave;nh đ&oacute;ng cửa, cố thủ trong ph&ograve;ng, dọa sẽ giết chị T. nếu mọi người x&ocirc;ng v&agrave;o. Tắt đ&egrave;n, nằm &ocirc;m người y&ecirc;u từ 2 giờ đến khoảng 4giờ 20, Th&agrave;nh nhiều lần đ&ograve;i quan hệ t&igrave;nh dục nhưng chị T. kh&ocirc;ng chấp nhận.</p>\r\n\r\n<p>Bực tức, Th&agrave;nh khống chế, h&atilde;m hiếp người y&ecirc;u m&igrave;nh, đồng thời đe dọa: &ldquo;Nếu kh&ocirc;ng y&ecirc;u nữa th&igrave; một trong hai đứa phải chết&rdquo;. Chị T. vẫn nhất quyết kh&ocirc;ng đồng &yacute;, Th&agrave;nh đ&atilde; b&oacute;p cổ người y&ecirc;u đến chết.</p>\r\n\r\n<h2><strong>Cần siết chặt quản l&yacute;</strong></h2>\r\n\r\n<p>Theo một l&atilde;nh đạo Ph&ograve;ng Cảnh s&aacute;t h&igrave;nh sự, C&ocirc;ng an th&agrave;nh phố H&agrave; Nội, theo đ&uacute;ng quy định của ph&aacute;p luật th&igrave; một người đến thu&ecirc; nh&agrave; trọ ở địa phương n&agrave;o đ&oacute; th&igrave; người ấy phải c&oacute; tr&aacute;ch nhiệm khai b&aacute;o nh&acirc;n th&acirc;n v&agrave; đăng k&yacute; tạm tr&uacute;. Người thu&ecirc; nh&agrave; c&oacute; thể tự đến c&ocirc;ng an phường, x&atilde; để đăng k&yacute; tạm tr&uacute; hoặc th&ocirc;ng qua chủ nh&agrave; để họ đi l&agrave;m đăng k&yacute; tạm tr&uacute; cho m&igrave;nh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n c&oacute; một thực tế l&agrave; kh&ocirc;ng phải c&aacute; nh&acirc;n đi thu&ecirc; nh&agrave; trọ hay c&aacute;c chủ c&oacute; nh&agrave; cho thu&ecirc; mướn n&agrave;o cũng l&agrave;m đ&uacute;ng nguy&ecirc;n tắc đơn giản ấy. Vẫn c&oacute; kh&ocirc;ng &iacute;t c&aacute;c chủ nh&agrave; c&ograve;n thờ ơ thực hiện việc đăng k&yacute; tạm tr&uacute; cho kh&aacute;ch trọ.</p>\r\n\r\n<p>Ghi nhận của ch&uacute;ng t&ocirc;i, một chủ nh&agrave; trọ tr&ecirc;n địa b&agrave;n quận Thanh Xu&acirc;n giải th&iacute;ch, họ chỉ thực hiện đăng k&yacute; tạm tr&uacute; cho những người n&agrave;o đ&oacute; c&oacute; thời hạn thu&ecirc; nh&agrave; d&agrave;i hơi, &iacute;t nhất khoảng từ 3 th&aacute;ng đến 1 năm trở l&ecirc;n, c&ograve;n với những đối tượng thu&ecirc; theo kiểu &ldquo;du canh du cư&rdquo; tạm bợ độ một v&agrave;i tuần th&igrave; l&agrave;m rất mất c&ocirc;ng (!?). Thậm ch&iacute; nhiều chủ nh&agrave; trọ c&ograve;n dễ d&atilde;i tới mức... chẳng cần đăng k&yacute; tạm tr&uacute; với ch&iacute;nh quyền, miễn l&agrave; người đi thu&ecirc; trọ khai t&ecirc;n tuổi, qu&ecirc; qu&aacute;n, hoặc tr&igrave;nh ra tấm CMND, hoặc một loại giấy tờ n&agrave;o c&oacute; h&igrave;nh v&agrave; dấu đỏ tr&ecirc;n đ&oacute; l&agrave;... OK!</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, việc quản l&yacute; những người ra v&agrave;o khu nh&agrave; trọ lại c&ograve;n lỏng lẻo hơn. Ngo&agrave;i một số khu trọ &ldquo;cao cấp&rdquo; c&oacute; bảo vệ ki&ecirc;m tr&ocirc;ng xe th&igrave; nhiều khu nh&agrave; trọ ai cũng c&oacute; thể v&agrave;o được, bất kể giờ n&agrave;o. T&igrave;nh trạng chủ nh&agrave; quản l&yacute; qua... điện thoại ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến.</p>\r\n\r\n<p>Ch&iacute;nh sự thờ ơ, dễ d&atilde;i của c&aacute;c chủ nh&agrave; cho thu&ecirc; trọ, cũng như việc quản l&yacute; trong c&ocirc;ng t&aacute;c đăng k&yacute; nh&acirc;n khẩu tạm tr&uacute; của ch&iacute;nh quyền địa phương c&ograve;n lơi lỏng như vậy sẽ l&agrave; &ldquo;lỗ hổng&rdquo; để c&aacute;c đối tượng tội phạm lợi dụng hoạt động. Bọn ch&uacute;ng c&oacute; thể tr&agrave; trộn v&agrave;o để trộm cắp, lừa đảo sinh vi&ecirc;n, người lao động.</p>\r\n\r\n<p>Cũng theo đại diện Ph&ograve;ng Cảnh s&aacute;t h&igrave;nh sự, thời gian vừa qua tại một số phường như phường Minh Khai (quận Bắc Từ Li&ecirc;m); phường Ho&agrave;ng Văn Thụ (quận Ho&agrave;ng Mai)... đ&atilde; triển khai những m&ocirc; h&igrave;nh quản l&yacute; nh&agrave; trọ, giữ g&igrave;n trật tự an ninh tr&ecirc;n địa b&agrave;n mang lại hiệu quả kh&aacute; tốt.</p>\r\n\r\n<p>Một loạt biện ph&aacute;p được triển khai như: c&aacute;c điểm đăng k&yacute; tạm tr&uacute; được bố tr&iacute; ở địa b&agrave;n c&aacute;c tổ d&acirc;n phố, gi&uacute;p người d&acirc;n thuận tiện trong việc đăng k&yacute;. Cảnh s&aacute;t khu vực định kỳ tổ chức kiểm tra lưu tr&uacute; tr&ecirc;n địa b&agrave;n phường. C&ocirc;ng an phường đứng ra tổ chức phối hợp với lực lượng bảo vệ d&acirc;n phố v&agrave; c&aacute;n bộ tổ d&acirc;n phố tiến h&agrave;nh mời to&agrave;n bộ số hộ gia đ&igrave;nh cho thu&ecirc; trọ đến trụ sở tập trung ở hội trường lớn để phổ biến những kỹ năng kiến thức ph&aacute;p luật, những kinh nghiệm trong c&ocirc;ng t&aacute;c quản l&yacute; cho thu&ecirc; trọ, c&ocirc;ng t&aacute;c chấp h&agrave;nh ph&aacute;p luật tại khu nh&agrave; cho thu&ecirc; trọ...</p>\r\n\r\n<p>Theo ch&uacute;ng t&ocirc;i, những m&ocirc; h&igrave;nh tr&ecirc;n cần được nh&acirc;n rộng. V&agrave; để giữ g&igrave;n an ninh trật tự ở những khu nh&agrave; trọ th&igrave; ch&iacute;nh quyền địa phương cần siết chặt c&ocirc;ng t&aacute;c quản l&yacute;, đồng thời &yacute; thức tự gi&aacute;c của chủ nh&agrave; trọ v&agrave; bản th&acirc;n người thu&ecirc; nh&agrave; cần phải được n&acirc;ng l&ecirc;n một bước.</p>\r\n', '1608867911108.jpg', 0);
INSERT INTO `news` (`id`, `name`, `description`, `detail_content`, `photo`, `hot`) VALUES
(17, 'SINH VIÊN THUÊ NHÀ TRỌ GIÁ RẺ HÀ NỘI CẦN LƯU Ý ĐIỀU GÌ?', '<p>Kinh ph&iacute; th&igrave; c&agrave;ng phải n&oacute;i, ở một m&igrave;nh ch&uacute;ng ta sẽ thu&ecirc; những căn ph&ograve;ng trọ ph&ugrave; hợp với bản th&acirc;n v&igrave; vậy thu&ecirc; nh&agrave; trọ gi&aacute; rẻ ở H&agrave; Nội l&agrave; một điều kh&aacute; e ngại.</p>\r\n', '<p>L&agrave; sinh vi&ecirc;n, kh&ocirc;ng phải ai cũng c&oacute; gia đ&igrave;nh ở gần trường hay học c&ugrave;ng trường ở H&agrave; Nội, bản th&acirc;n phải nỗ lực v&agrave; sống một m&igrave;nh tại th&agrave;nh phố xa lạ. Kinh ph&iacute; th&igrave; c&agrave;ng phải n&oacute;i, ở một m&igrave;nh ch&uacute;ng ta sẽ thu&ecirc; những căn ph&ograve;ng trọ ph&ugrave; hợp với bản th&acirc;n v&igrave; vậy&nbsp;<strong>thu&ecirc; nh&agrave; trọ gi&aacute; rẻ ở H&agrave; Nội</strong>&nbsp;l&agrave; một điều kh&aacute; e ngại.</p>\r\n\r\n<p>Vậy ch&uacute;ng ta cần phải ch&uacute; &yacute; những g&igrave;? B&agrave;i viết n&agrave;y sẽ gi&uacute;p c&aacute;c bạn th&ecirc;m những kinh nghiệm khi thu&ecirc; ph&ograve;ng trọ gi&aacute; rẻ H&agrave; Nội nh&eacute;.</p>\r\n\r\n<h2><strong>6 Lưu &yacute; thu&ecirc; nh&agrave; trọ gi&aacute; rẻ tại H&agrave; Nội</strong></h2>\r\n\r\n<h3><strong>1 - Gi&aacute; ph&ograve;ng v&agrave; vị tr&iacute;</strong></h3>\r\n\r\n<p>Khi thu&ecirc; ph&ograve;ng trọ gi&aacute; rẻ ở H&agrave; Nội, khi l&agrave; một sinh vi&ecirc;n th&igrave; ch&uacute;ng ta n&ecirc;n lu&ocirc;n ưu ti&ecirc;n việc t&igrave;m ph&ograve;ng gần trường, c&aacute;c bạn t&acirc;n sinh vi&ecirc;n th&igrave; n&ecirc;n đi khảo s&aacute;t c&aacute;c khu vực c&aacute;c địa h&igrave;nh quanh khu vực trường tr&aacute;nh trường hợp thu&ecirc; c&aacute;c căn ph&ograve;ng trọ ở khu ngập &uacute;ng, đường xấu,...&nbsp;</p>\r\n\r\n<p>Về gi&aacute; cả th&igrave; khi thu&ecirc; ph&ograve;ng, gi&aacute; tiền ph&ograve;ng sẽ tương ứng với nội thất trong ph&ograve;ng. Bạn n&ecirc;n hỏi một số người thu&ecirc; ph&ograve;ng quanh khu vực hoặc những người đang thu&ecirc; ph&ograve;ng tại căn nh&agrave; để được biết mức gi&aacute; thu&ecirc; ph&ograve;ng chung nhất tại khu vực bạn muốn thu&ecirc;.</p>\r\n\r\n<h3><strong>2 - Ph&ograve;ng trọ c&oacute; giao th&ocirc;ng thuận tiện&nbsp;</strong></h3>\r\n\r\n<p>Với sinh vi&ecirc;n ch&uacute;ng ta th&igrave; điều n&agrave;y kh&aacute; quan trọng, ai cũng cần đi học v&agrave; cũng c&oacute; bạn đi l&agrave;m th&ecirc;m. Để tr&aacute;nh mất thời gian trong việc di chuyển th&igrave; d&ugrave; l&agrave; ch&uacute;ng ta&nbsp;<em><strong>t&igrave;m một căn ph&ograve;ng trọ gi&aacute; rẻ hay gi&aacute; cao ở H&agrave; Nội</strong></em>&nbsp;th&igrave; ch&uacute;ng ta vẫn phải ưu ti&ecirc;n việc thuận tiện cho việc di chuyển n&agrave;y v&igrave; n&oacute; sẽ ảnh hưởng đến cuộc sống của ch&uacute;ng ta rất nhiều.</p>\r\n\r\n<p>Nếu c&oacute; thể bạn cũng n&ecirc;n chọn một căn nh&agrave; trọ gần chợ, bến xe bus để thuận tiện sinh hoạt hơn.</p>\r\n\r\n<h3><strong>3 - Tr&aacute;nh những nơi lụp xụp, căn nh&agrave; trọ cũ&nbsp;</strong></h3>\r\n\r\n<p>Ai cũng muốn ở trong căn ph&ograve;ng mới v&agrave; đẹp, nhưng c&oacute; thể điều kiện của mỗi sinh vi&ecirc;n kh&aacute;c nhau, kh&ocirc;ng phải ai cũng c&oacute; đủ t&agrave;i ch&iacute;nh để thu&ecirc; c&aacute;c căn ph&ograve;ng trọ hay chung cư mini đẹp, ch&iacute;nh v&igrave; vậy&nbsp;<strong>nh&agrave; trọ gi&aacute; rẻ ở H&agrave; Nội</strong>rất được nhiều bạn sinh vi&ecirc;n để &yacute;.</p>\r\n\r\n<p>Nhưng d&ugrave; l&agrave; thu&ecirc; với t&agrave;i ch&iacute;nh thấp th&igrave; bạn vẫn kh&ocirc;ng n&ecirc;n thu&ecirc; một căn ph&ograve;ng qu&aacute; ẩm mốc, cũ đ&atilde; x&acirc;y l&acirc;u năm, c&oacute; cảm gi&aacute;c g&acirc;y lụp xụp v&igrave; khi ở kh&aacute; nguy hiểm cho bạn tuy mĩ quan kh&ocirc;ng cần đẹp nhưng kh&ocirc;ng n&ecirc;n qu&aacute; cũ kỹ g&acirc;y cảm gi&aacute;c thiếu an to&agrave;n khi sinh sống.</p>\r\n\r\n<h3><strong>4 - Khảo s&aacute;t nhiều căn ph&ograve;ng, nh&agrave; trọ kh&aacute;c nhau</strong></h3>\r\n\r\n<p>Tuy l&agrave; bạn t&igrave;m ph&ograve;ng trọ gi&aacute; rẻ nhưng ở th&agrave;nh phố c&oacute; rất nhiều căn ph&ograve;ng đ&aacute;p ứng được t&agrave;i ch&iacute;nh của bạn, vậy bạn n&ecirc;n khảo s&aacute;t nhiều căn ph&ograve;ng trọ, kh&ocirc;ng n&ecirc;n gặp một căn ph&ograve;ng ph&ugrave; hợp với t&agrave;i ch&iacute;nh của m&igrave;nh l&agrave; cọc ph&ograve;ng lu&ocirc;n, do cũng c&oacute; thể c&oacute; rất nhiều căn ph&ograve;ng gi&aacute; như vậy nhưng c&oacute; thể c&oacute; kh&ocirc;ng gian sống, tiện nghi,... đầy đủ v&agrave; đẹp hơn.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, bạn n&ecirc;n khảo s&aacute;t nhiều ph&ograve;ng trong khu vực trước khi đưa ra quyết định thu&ecirc; ph&ograve;ng nh&eacute;, tr&aacute;nh trường hợp bỏ lỡ nhiều tiện nghi cho cuộc sống của m&igrave;nh.</p>\r\n\r\n<h3><strong>5 - Ch&uacute; &yacute; an ninh ph&ograve;ng trọ</strong></h3>\r\n\r\n<p>C&oacute; thể khi thu&ecirc; ph&ograve;ng trọ gi&aacute; rẻ th&igrave; gần như ai trong ch&uacute;ng ta sẽ nghĩ rằng căn ph&ograve;ng gi&aacute; rẻ th&igrave; an ninh sẽ kh&ocirc;ng được đảm bảo v&agrave; tốt. Đ&oacute; chỉ đ&uacute;ng một phần, nếu ch&uacute;ng ta thu&ecirc; căn ph&ograve;ng trọ gi&aacute; rẻ chung chủ th&igrave; n&oacute; sẽ kh&aacute; an to&agrave;n đấy.</p>\r\n\r\n<p>Nhưng khi bạn&nbsp;<a href=\"https://bandon.vn/kinh-nghiem/sinh-vien-thue-nha-tro-gia-re-ha-noi\"><strong>thu&ecirc; ph&ograve;ng trọ gi&aacute; rẻ</strong></a>&nbsp;nhưng kh&ocirc;ng chung chủ th&igrave; ch&uacute;ng ta vẫn n&ecirc;n ch&uacute; &yacute; an ninh một ch&uacute;t, d&ugrave; kh&ocirc;ng được tốt như c&aacute;c căn ph&ograve;ng trọ gi&aacute; cao hơn nhưng từ cửa nh&agrave; trọ, cửa ph&ograve;ng trọ, ổ kh&oacute;a,... Bạn trước khi thu&ecirc; trọ phải kiểm tra xem n&oacute; c&oacute; chắc chắn kh&ocirc;ng, để đảm bảo đủ an to&agrave;n cho bạn.</p>\r\n\r\n<h3><strong>6 - N&ecirc;n t&igrave;m ph&ograve;ng trọ trực tiếp</strong></h3>\r\n\r\n<p>Khi&nbsp;<strong>t&igrave;m ph&ograve;ng trọ gi&aacute; rẻ tại H&agrave; Nội</strong>&nbsp;th&igrave; bạn n&ecirc;n ưu ti&ecirc;n việc t&igrave;m ph&ograve;ng trọ trực tiếp, d&ugrave; n&oacute; kh&aacute; tốn thời gian v&agrave; c&ocirc;ng sức, tuy vậy t&igrave;m ph&ograve;ng trực tiếp tr&aacute;nh cho bạn được rất nhiều rủi ro như l&agrave;: lừa đảo, căn ph&ograve;ng kh&ocirc;ng được&nbsp; như lời n&oacute;i của chủ nh&agrave;, qua m&ocirc;i giới, trung gian,...</p>\r\n\r\n<p>Nếu bạn l&agrave; t&acirc;n sinh vi&ecirc;n, c&ograve;n bỡ ngỡ trong việc t&igrave;m được v&agrave; khu vực m&igrave;nh ở, th&igrave; bạn n&ecirc;n tham khảo ở&nbsp;<strong>web cho thu&ecirc; ph&ograve;ng uy t&iacute;n</strong>&nbsp;c&ugrave;ng với xem ph&ograve;ng trực tiếp sẽ tiết kiệm thời gian v&agrave; chi ph&iacute; di chuyển của m&igrave;nh. Bạn c&oacute; thể tham khảo một số trang web cho thu&ecirc; ph&ograve;ng trọ uy t&iacute;n như:&nbsp;<em>bandon.vn; phongtro123.com; chotot.com,...</em></p>\r\n\r\n<p><strong>Một số ti&ecirc;u ch&iacute; cho c&aacute;c bạn khi t&igrave;m ph&ograve;ng trọ gi&aacute; rẻ ở H&agrave; Nội</strong></p>\r\n\r\n<ul>\r\n	<li>Chủ nh&agrave; dễ t&iacute;nh&nbsp;</li>\r\n	<li>H&agrave;ng x&oacute;m vui vẻ th&acirc;n thiện</li>\r\n	<li>C&oacute; nơi để xe an to&agrave;n</li>\r\n	<li>Gi&aacute; thu&ecirc; ph&ograve;ng ph&ugrave; hợp với mức gi&aacute; ph&ograve;ng chung ở khu vực&nbsp;</li>\r\n	<li>Gi&aacute; điện, gi&aacute; nước ph&ugrave; hợp với gi&aacute; chung.</li>\r\n	<li>Nguồn nước đảm bảo sạch sẽ v&igrave; sẽ ảnh hưởng rất lớn đến sinh hoạt của bản th&acirc;n</li>\r\n</ul>\r\n', '1608867918109.jpg', 0),
(18, '21+ MẪU THIẾT KẾ PHÒNG TRỌ - NHÀ TRỌ ĐẸP NHẤT 2020', '<p>Tổng hợp 21+ Mẫu h&igrave;nh ảnh thiết kế ph&ograve;ng trọ - nh&agrave; trọ đẹp nhất 2020 d&agrave;nh cho chủ nh&agrave; trọ.</p>\r\n', '<p><em>&quot;<strong>Nh&agrave; trọ đẹp, ph&ograve;ng đẹp</strong>&quot; - l&agrave; điều m&agrave; cả người đi thu&ecirc; ph&ograve;ng v&agrave; người cho thu&ecirc; đều mong muốn.&nbsp;</em></p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, b&agrave;i viết n&agrave;y sẽ gi&uacute;p c&aacute;c bạn tạo thiết kế&nbsp;<strong>ph&ograve;ng trọ đẹp hay 1 căn ph&ograve;ng lung linh</strong>&nbsp;gi&uacute;p ta c&oacute; một kh&ocirc;ng gian thoải m&aacute;i &amp; tiện nghi.</p>\r\n\r\n<p>Sau khi đ&atilde; t&igrave;m được một căn ph&ograve;ng ưng &yacute;, ch&uacute;ng ta sẽ bắt tay ngay v&agrave;o việc sắp xếp đồ đạc, trang tr&iacute; ph&ograve;ng trọ sao cho ưng &yacute; nhất.</p>\r\n\r\n<h2><strong>1 - Mẹo thiết kế ph&ograve;ng trọ, nh&agrave; trọ đẹp</strong></h2>\r\n\r\n<p>V&igrave; căn ph&ograve;ng trọ kh&ocirc;ng qu&aacute; rộng, dao động chỉ từ 15m2 - 35m2 n&ecirc;n ch&uacute;ng ta cần ch&uacute; &yacute; đến kh&ocirc;ng gian v&agrave; sự tho&aacute;ng m&aacute;t đầu ti&ecirc;n để tr&aacute;nh g&acirc;y b&iacute; b&aacute;ch. Sau đ&acirc;y cũng l&agrave; một số &yacute; tưởng để gi&uacute;p&nbsp;<strong>ph&ograve;ng trọ đẹp</strong>&nbsp;hơn nh&eacute;.</p>\r\n\r\n<p>N&ecirc;n tập chung v&agrave;o c&aacute;c m&agrave;u sắc tươi s&aacute;ng để trang tr&iacute; căn ph&ograve;ng. Bạn c&oacute; thể trang tr&iacute; ph&ograve;ng c&aacute;ch sử dụng giấy d&aacute;n tường do c&oacute; thể căn ph&ograve;ng trọ bạn ở đ&atilde; được x&acirc;y dựng l&acirc;u hay c&oacute; c&aacute;c vết ố v&agrave;ng sử dụng giấy d&aacute;n tường sẽ l&agrave; phương &aacute;n ph&ugrave; hợp với bạn.</p>\r\n\r\n<p>Hay c&oacute; thể trang tr&iacute; căn ph&ograve;ng bằng c&aacute;ch treo 1 v&agrave;i bức tranh tươi s&aacute;ng, ảnh kỷ niệm của m&igrave;nh tạo n&ecirc;n sự &nbsp;h&agrave;i h&ograve;a, ch&uacute;ng ta lựa chọn khung ảnh vừa nhỏ ph&ugrave; hợp với căn ph&ograve;ng.</p>\r\n\r\n<p>C&oacute; thể sử dụng c&acirc;y xanh nhỏ trang tr&iacute; tạo cảm gi&aacute;c &lsquo;hoang d&atilde;&rsquo;. D&ugrave; n&oacute; gi&uacute;p ch&uacute;ng ta thanh lọc kh&ocirc;ng kh&iacute; nhưng chỉ khi v&agrave;o ban ng&agrave;y, ban đ&ecirc;m khi qu&aacute; nhiều c&acirc;y xanh trong ph&ograve;ng sẽ kh&ocirc;ng tốt, bạn c&oacute; thể trang tr&iacute; c&acirc;y xanh b&ecirc;n cửa sổ hoặc cửa ph&ograve;ng, hay bạn c&oacute; thể tham khảo v&agrave; t&igrave;m c&acirc;y xanh hợp phong thủy tuổi mệnh của m&igrave;nh.</p>\r\n\r\n<p>Bạn cũng c&oacute; thể vận dụng nội thất để&nbsp;<strong>trang tr&iacute; ph&ograve;ng trọ</strong>&nbsp;của m&igrave;nh. Bởi c&aacute;c ph&ograve;ng trọ đều c&oacute; diện t&iacute;ch kh&aacute; nhỏ, n&ecirc;n việc sử dụng nội thất thiết kế ph&ograve;ng trọ sẽ kh&aacute; cần thiết, vừa gọn g&agrave;ng, tinh tế v&agrave; tiết kiệm.</p>\r\n\r\n<p>Đối với s&agrave;n nh&agrave;, ch&uacute;ng ta n&ecirc;n sử dụng một tấm thảm hoặc miếng d&aacute;n s&agrave;n nh&agrave; m&agrave;u sắc ph&ugrave; hợp với căn ph&ograve;ng, th&ecirc;m một chiếc giường, sẽ th&uacute; vị hơn khi c&oacute; một th&uacute; cưng b&ecirc;n cạnh v&agrave;o mỗi buổi tối c&ugrave;ng nhau &lsquo;lăn&rsquo; s&agrave;n nh&eacute;.</p>\r\n\r\n<p>Ch&uacute;ng ta c&oacute; thể &ldquo;ăn gian&rdquo; diện t&iacute;ch căn ph&ograve;ng bằng c&aacute;ch th&ecirc;m một v&aacute;ch ngăn. Những v&aacute;ch ngăn ở một căn nh&agrave; trọ d&ugrave;ng để ph&acirc;n chia đồng thời tạo th&agrave;nh m&oacute;n đồ nội thất c&oacute; thể mang t&iacute;nh chất trang tr&iacute;. Ch&uacute;ng ta n&ecirc;n chọn một v&aacute;ch ngăn c&oacute; thiết kế đơn giản m&agrave;u sắc tươi s&aacute;ng ph&ugrave; hợp với căn ph&ograve;ng, tạo cảm hứng tươi mới tr&agrave;n đầy sức sống đồng thời tận dụng được hết diện t&iacute;ch căn ph&ograve;ng.</p>\r\n\r\n<p>Sử dụng c&aacute;c kệ s&aacute;ch nhỏ, đơn giản, tiết kiệm chi ph&iacute; vừa c&oacute; t&aacute;c dụng trang tr&iacute; vừa giảm bớt cảm gi&aacute;c trống trải của căn ph&ograve;ng.&nbsp;</p>\r\n\r\n<p><em>Hay với những bạn c&oacute; b&agrave;n tay kh&eacute;o l&eacute;o th&igrave; ch&uacute;ng ta c&oacute; thể sử dụng đồ handmade như c&aacute;c b&igrave;nh hoa giấy, chu&ocirc;ng gi&oacute;, khung ảnh,....&nbsp;</em></p>\r\n\r\n<p>Với nh&agrave; tắm, vệ sinh th&igrave; thường ch&uacute;ng ta n&ecirc;n để thiết kế đơn giản, m&agrave;u sắc s&aacute;ng để tr&aacute;nh mang cảm gi&aacute;c chật chội, đồ vật trong nh&agrave; tắm th&igrave; chỉ n&ecirc;n để c&aacute;c đồ cần thiết tr&aacute;nh trang tr&iacute; nhiều g&acirc;y tốn diện t&iacute;ch.&nbsp;</p>\r\n\r\n<p>C&oacute; thể t&ugrave;y theo c&aacute;ch x&acirc;y dựng căn ph&ograve;ng m&agrave; ch&uacute;ng ta c&oacute; thể trang tr&iacute; nh&agrave; trọ của m&igrave;nh theo &yacute; th&iacute;ch nh&eacute;!</p>\r\n\r\n<h2><strong>2 - Một số lưu &yacute; khi thiết kế ph&ograve;ng trọ&nbsp;</strong></h2>\r\n\r\n<p>Ch&uacute;ng ta n&ecirc;n chọn c&aacute;c m&agrave;u s&aacute;ng để kh&ocirc;ng gian tươi mới v&agrave; tạo cảm gi&aacute;c rộng cho căn ph&ograve;ng, kh&ocirc;ng n&ecirc;n sử dụng những nội thất c&oacute; m&agrave;u tối g&acirc;y b&iacute; b&aacute;ch v&agrave; kh&ocirc;ng gian căn ph&ograve;ng tối lại.</p>\r\n\r\n<p>Với những căn ph&ograve;ng trọ với thiết kế bị hạn chế về diện t&iacute;ch bạn n&ecirc;n kết hợp c&ugrave;ng với gương trang tr&iacute; nhằm gi&uacute;p mang đến cảm gi&aacute;c kh&ocirc;ng gian nh&acirc;n đ&ocirc;i v&agrave; sẽ rộng hơn nhiều. Nhưng bạn n&ecirc;n ch&uacute; &yacute; đến vị tr&iacute; để gương cho ph&ugrave; hợp với căn ph&ograve;ng.</p>\r\n\r\n<p>Kh&ocirc;ng n&ecirc;n sử dụng qu&aacute; nhiều đồ vật g&acirc;y cảm gi&aacute;c rối cho&nbsp;<strong>căn ph&ograve;ng trọ</strong>, n&ecirc;n tận dụng đồ đạc đa chức năng để ph&ograve;ng trọ gọn g&agrave;ng hơn.&nbsp;</p>\r\n\r\n<p><em>Kh&ocirc;ng n&ecirc;n c&oacute; qu&aacute; nhiều họa tiết tr&ecirc;n đồ vật nội thất, v&igrave; qu&aacute; nhiều họa tiết sẽ g&acirc;y cho ta cảm gi&aacute;c mất c&acirc;n đối, v&agrave; rối mắt.</em></p>\r\n\r\n<p>N&ecirc;n chọn những những đồ vật, nội thất c&oacute; k&iacute;ch thước ph&ugrave; hợp với căn ph&ograve;ng trọ của bạn, kh&ocirc;ng n&ecirc;n chọn đồ vật c&oacute; k&iacute;ch thước qu&aacute; lớn l&agrave;m cho kh&ocirc;ng gian căn ph&ograve;ng của bạn trở n&ecirc;n hẹp v&agrave; chật chội.&nbsp;</p>\r\n\r\n<p>Nếu bạn ở theo hộ gia đ&igrave;nh c&oacute; trẻ nhỏ th&igrave; n&ecirc;n tr&aacute;nh c&aacute;c đồ vật sắc nhọn để tr&aacute;nh g&acirc;y tai nạn cho trẻ nhỏ.&nbsp;</p>\r\n\r\n<h2><strong>3 - Tổng hợp 21+ mẫu thiết kế nh&agrave; trọ đẹp&nbsp;</strong></h2>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; hơn 21 mẫu thiết kế ph&ograve;ng trọ,<strong>&nbsp;nh&agrave; trọ đẹp</strong>&nbsp;năm 2020, bạn c&oacute; thể tham khảo nh&eacute;!</p>\r\n', '1608867925110.jpg', 0);

