INSERT INTO `products` (`id`, `name`, `photo`, `owner_id`, `category_id`, `location_id`, `address`, `title`, `description`, `area`, `discount`, `chung_chu`, `phong_tam`, `phong_bep`, `dieu_hoa`, `ban_cong`, `hot`, `gia_dien_nuoc`, `price`, `quantities`, `date`) VALUES
(28, 'Căn hộ tại An Bình City', '160886299812.jpg', 9, 4, 12, '234, 234 Đường Phạm Văn Đồng, Phường Cổ Nhuế 1', '<p>Căn hộ tại An B&igrave;nh City</p>\r\n', '<p>- Chuyển đổi mục đ&iacute;ch sử dụng chủ nh&agrave; cần cho thu&ecirc; gấp căn hộ 2PN đầy đủ nội thất trong th&aacute;ng n&agrave;y<br />\r\n- Diện t&iacute;ch sử dụng: 74m2, tầng trung đẹp, view nội khu bể bơi<br />\r\n- Cửa hướng Bắc, ban c&ocirc;ng hướng Nam tho&aacute;ng m&aacute;t tiết kiệm điện<br />\r\n- Nội thất đầy đủ đ&atilde; bao gồm tủ bếp, bếp từ h&uacute;t m&ugrave;i, 3 điều h&ograve;a, giưởng tủ, tủ lạnh, r&egrave;m cửa, thiết bị vệ sinh toto, sofa, b&agrave;n ăn, m&aacute;y giặt,... kh&aacute;ch v&agrave;o ở ngay kh&ocirc;ng phải sắm sửa g&igrave;<br />\r\n- Thời gian nhận nh&agrave;: nhận nh&agrave; ở ngay</p>\r\n', 74, 0, 0, '2 phòng khép kín', 'liền với phòng khách', 1, 1, 1, 'điện nước giá dân', 10000000, 1, '2020-12-22'),
(29, 'Nhà đường Hồ Ba Mẫu', '16088627878.jpg', 9, 4, 2, 'Đường Hồ Ba Mẫu, Phường Phương Liên', '<p>Nh&agrave; nguy&ecirc;n căn đường Hồ Ba Mẫu</p>\r\n', '<p>Cho thu&ecirc; nh&agrave; ng&otilde; 77 Hồ Ba Mẫu 40m x 5T<br />\r\n- DT s&agrave;n : 50 m2 x ,5 tầng<br />\r\n- Thiết kế như sau:<br />\r\n+&nbsp;Tầng 1 + 2 + 3 + 4 + 5 : TH&Ocirc;NG S&Agrave;N<br />\r\n+&nbsp;Nh&agrave; c&oacute; 04 ph&ograve;ng ngủ Nh&agrave; c&oacute; đầy , b&igrave;nh n&oacute;ng lạnh, điều h&ograve;a.<br />\r\n-&nbsp;Nh&agrave; vị tr&iacute; mặt ng&otilde; xe ba g&aacute;c v&agrave;o tận cửa, c&aacute;ch mặt đường ch&iacute;nh phố HỒ BA MẪU 15 m; an ninh tốt; điện, nước ri&ecirc;ng biệt<br />\r\n-&nbsp;Ph&ugrave; hợp hộ gia đ&igrave;nh để ở, l&agrave;m văn ph&ograve;ng c&ocirc;ng ty, thuận tiện kinh doanh.</p>\r\n', 40, 0, 0, '4 phòng vệ sinh khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 10000000, 1, '2020-12-22'),
(30, 'CCMN Khương Hạ', '16088627574.jpg', 9, 4, 7, 'Số 5E ngách 68 ngõ 29 Khương Hạ, phường Khương Đình', '<p>CCMN Khương Hạ</p>\r\n', '<p>Căn hộ mới 100% c&oacute; thang m&aacute;y, c&oacute; trang bị đủ đồ:<br />\r\n- Điều h&ograve;a 2 chiều<br />\r\n- N&oacute;ng lạnh<br />\r\n- Giường, tủ -B&agrave;n l&agrave;m việc l&agrave;m việc hoặc b&agrave;n trang điểm<br />\r\n- Smart Tivi<br />\r\n- Tủ Lạnh<br />\r\n- M&aacute;y giặt chung<br />\r\n- R&egrave;m cửa cản nắng<br />\r\n- C&oacute; lồng sắt phơi quần &aacute;o ri&ecirc;ng từng ph&ograve;ng<br />\r\n- Tủ bếp c&oacute; v&aacute;ch k&iacute;n ngăn m&ugrave;i, c&oacute; bồn rửa Ph&ograve;ng tho&aacute;ng c&oacute; đủ nắng v&agrave; gi&oacute;.<br />\r\nT&ograve;a nh&agrave; c&oacute; bảo vệ 24/24 n&ecirc;n an ninh cực tốt, ra v&agrave;o thoải m&aacute;i, nh&agrave; để xe rộng r&atilde;i. Nh&agrave; mặt view hồ m&aacute;t mẻ. Ng&otilde; rộng 6m 2 &ocirc; t&ocirc; tr&aacute;nh nhau.</p>\r\n', 30, 0, 0, 'khép kín', 'khu vực nấu ăn thông với phòng khách', 1, 1, 1, 'điện 3,5k/số, nước 25k/khối', 4000000, 1, '2020-12-22'),
(31, 'Phòng trọ phố Bà Triệu', '16088623742.jpg', 9, 4, 8, 'Phố Bà Triệu, phường Nguyễn Trãi', '<p>Ph&ograve;ng trọ phố B&agrave; Triệu, H&agrave; Đ&ocirc;ng</p>\r\n', '<p>- Nh&agrave; đẹp full nội thất mới, chỉ việc mang vali v&agrave;o ở.<br />\r\n- Gi&aacute; từ 2tr8 - 3tr5<br />\r\n- Ph&ograve;ng đẹp, sạch sẽ tho&aacute;ng m&aacute;t gần: HV Qu&acirc;n Y, Viện 103, BV đa khoa H&agrave; Đ&ocirc;ng, metro H&agrave; Đ&ocirc;ng, Chợ H&agrave; Đ&ocirc;ng, c&aacute;ch cầu trắng 500m,...,<br />\r\n- Ban c&ocirc;ng cực rộng, tho&aacute;ng m&aacute;t.<br />\r\n- Thang m&aacute;y tốc độ cao.<br />\r\nM&ocirc; tả chung:<br />\r\n- Full nội thất điều h&ograve;a, n&oacute;ng lạnh, tủ lạnh, giường, tủ, phơi đồ, b&agrave;n học, để xe miễn ph&iacute;, camera an ninh, m&aacute;y giặt, kệ bếp.<br />\r\n- Cổng kh&oacute;a v&acirc;n tay đảm bảo an to&agrave;n.<br />\r\n- Ch&iacute;nh chủ miễn m&ocirc;i giới.<br />\r\n- H&agrave;ng tuần c&oacute; người qua dọn vệ sinh chung cả nh&agrave; thường xuy&ecirc;n.<br />\r\n-&nbsp;Giờ giấc đi lại thoải m&aacute;i, tiếp bạn b&egrave; v&ocirc; tư miễn l&agrave; lịch sự.<br />\r\n- Hợp đồng l&acirc;u d&agrave;i kh&ocirc;ng tăng gi&aacute;, chu kỳ đ&oacute;ng tiền nh&agrave; 1 th&aacute;ng 1 lần, đặt cọc 1 th&aacute;ng tiền nh&agrave;.</p>\r\n', 20, 0, 0, 'khép kín', 'khu nấu ăn chung', 1, 0, 1, 'điện 3,5k/số, nước 20k/khối', 2800000, 4, '2020-12-22'),
(32, 'Phòng trọ Phạm Văn Đồng', '160886403126.jpg', 7, 4, 1, 'Ngõ 2/12b Phạm Văn Đồng', '<p>Ph&ograve;ng trọ Phạm Văn Đồng</p>\r\n', '<p>Căn nh&agrave; c&oacute; 5 tầng, tầng một để xe, ph&ograve;ng&nbsp;c&oacute; cửa sổ rộng tho&aacute;ng m&aacute;t.<br />\r\nGi&aacute; thu&ecirc; 1tr7 - 2tr - 2tr6, diện t&iacute;ch 15 - 18m2<br />\r\n- Nội thất: ph&ograve;ng c&oacute; phản, giường, tủ quần &aacute;o, kệ bếp nấu ăn, điều h&ograve;a, b&igrave;nh n&oacute;ng lạnh, tuỳ theo ph&ograve;ng.<br />\r\n- Ph&ograve;ng b&eacute; chưa c&oacute; đồ, c&aacute;c bạn tự t&uacute;c.<br />\r\n- Vị tr&iacute;: gần c&aacute;c trường ĐH&nbsp;lớn như ĐH&nbsp;Quốc Gia, ĐH&nbsp;Điện Lực, CD Du Lịch, bến xe Mỹ Đ&igrave;nh.<br />\r\n- An ninh tốt, điện nước gi&aacute; rẻ, giờ giấc đi lại tự do, cửa kho&aacute; v&acirc;n tay.</p>\r\n', 15, 0, 0, 'có, khép kín', 'nấu ăn trong phòng', 1, 0, 1, 'điện 3,5k/số, nước 25k/khối', 1700000, 5, '2020-12-22'),
(33, 'Nhà riêng Ngọc Hồi', '160886398123.jpg', 7, 4, 4, 'Đường Ngọc Hồi, Phường Hoàng Liệt', '<p>Nh&agrave; ri&ecirc;ng Ngọc Hồi</p>\r\n', '<p>- Diện t&iacute;ch: 60 m2x 4 tầng, MT 4m, hướng Nam, c&oacute; 4PN, PK, bếp, 3WC, 1P.thờ, 2 mặt ng&otilde;, tho&aacute;ng trước sau.<br />\r\n- Nội thất: c&oacute; mỗi tủ bếp c&ograve;n lại chưa c&oacute; g&igrave;.<br />\r\n- Vị tr&iacute; đẹp c&aacute;ch BX Nước Ngầm 800m, cạnh trường Việt Ba, &ocirc;t&ocirc; đỗ cửa.<br />\r\n- Tiện l&agrave;m văn ph&ograve;ng, b&aacute;n h&agrave;ng online, kho h&agrave;ng, b&aacute;n dược phẩm hoặc ở.</p>\r\n', 60, 0, 0, '3 phòng khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 8000000, 1, '2020-12-22'),
(34, 'Chung cư Vinhomes Westpoint Phạm Hùng', '160886293820.jpg', 7, 4, 5, 'Đường Phạm Hùng, Phường Mễ Trì', '<p>Chung cư Vinhomes Westpoint Phạm H&ugrave;ng</p>\r\n', '<p>- Nội thất đầy đủ đồ bao gồm giường tủ, b&agrave;n ghế sofa, tivi, tủ lạnh, m&aacute;y giặt, đồ điện tử - đồ gia dụng đầy đủ...kh&aacute;ch thu&ecirc; chỉ việc x&aacute;ch vali v&agrave;o ở ngay.<br />\r\n+ Gi&aacute;&nbsp;thu&ecirc; căn hộ nội thất cơ bản:10 triệu/th&aacute;ng.<br />\r\n+ Gi&aacute; thu&ecirc; căn hộ full nội thất:14 triệu/th&aacute;ng.<br />\r\n- Thanh to&aacute;n linh hoạt 3 - 6 - 12 th&aacute;ng/lần( cọc 1 th&aacute;ng).<br />\r\n- C&oacute; thể cho thu&ecirc; ngắn hạn.<br />\r\n- Hỗ trợ kh&aacute;ch thu&ecirc; l&agrave;m c&aacute;c thủ tục hợp đồng, thẻ cư d&acirc;n, tạm tr&uacute;...<br />\r\n- Tiện &iacute;ch khi sống tại Vinhomes West Point: gym, yoga, bể bơi, khu vui chơi... miễn ph&iacute; 100% cho cư d&acirc;n.</p>\r\n', 72, 0, 0, '2 phòng khép kín', 'thông với phòng khách', 1, 1, 1, 'điện nước giá dân', 10000000, 1, '2020-12-22'),
(35, 'CCMN Hoàng Hoa Thám', '160886292116.jpg', 7, 4, 9, '567 Đường Hoàng Hoa Thám, Phường Vĩnh Phúc', '<p>CCMN Ho&agrave;ng Hoa Th&aacute;m</p>\r\n', '<p>- Diện t&iacute;ch 50m2, c&oacute; ban c&ocirc;ng<br />\r\n-&nbsp;Nội thất đầy đủ: điều h&ograve;a n&oacute;ng lạnh, vệ sinh kh&eacute;p k&iacute;n, giường tủ b&agrave;n bếp&hellip;<br />\r\n-&nbsp;Nh&agrave; si&ecirc;u đẹp, sạch sẽ tho&aacute;ng m&aacute;t, ph&ugrave; hợp ở nh&oacute;m nhỏ, hộ gia đ&igrave;nh.</p>\r\n', 50, 0, 0, 'khép kín', 'thông với phòng ngủ', 1, 1, 1, 'điện 4k/số, nước 30k/khối', 4800000, 1, '2020-12-22'),
(36, 'Chung cư đường Ngọc Khánh', '160886413438.jpg', 10, 4, 9, '15-17 Đường Ngọc Khánh, Phường Giảng Võ', '<p>Chung cư đường Ngọc Kh&aacute;nh</p>\r\n', '<p>- Căn hộ c&oacute; diện t&iacute;ch 136m2 - 3pn - 2vs - 1pk v&agrave; kh&ocirc;ng gian nh&agrave; bếp.<br />\r\n- Thiết kế mới trần cao bao c&ocirc;ng rộng tạo n&ecirc;n kh&ocirc;ng gian sống tho&aacute;ng m&aacute;t cho căn hộ.<br />\r\n- Nh&agrave; sạch - đẹp, thiết kế hiện đại, kh&ocirc;ng gian tho&aacute;ng m&aacute;t, d&acirc;n cư văn minh.<br />\r\n- Tọa lạc vị tr&iacute; giao th&ocirc;ng thuận lợi, gần nhiều trung t&acirc;m văn ph&ograve;ng, chợ, si&ecirc;u thị, trường học, tiện &iacute;ch 24/24.<br />\r\n- Nh&agrave; đ&atilde; trang bị full nội thất: ĐH, NL, b&agrave;n ghế, giường tủ, tivi, tủ lạnh, m&aacute;y giặt ...v...v<br />\r\n- Rất ph&ugrave; hợp hộ gđ ở, kinh doanh online,...</p>\r\n', 136, 2, 0, '2 phòng khép kín', 'thông với phòng khách', 1, 1, 1, 'điện nước giá dân', 16000000, 1, '2020-12-22'),
(37, 'CCMN đường Xuân Đỉnh', '160886411335.jpg', 10, 4, 6, '401, Đường Xuân Đỉnh, Phường Xuân Đỉnh', '<p>CCMN đường Xu&acirc;n Đỉnh</p>\r\n', '<p>CCMN mới 100%.<br />\r\n- Nội thất: Điều h&ograve;a, n&oacute;ng lạnh, kệ bếp, sofa.<br />\r\n- Vị tr&iacute;: Nằm ngay giữa giao Phạm Văn Đồng - Xu&acirc;n Đỉnh, giao th&ocirc;ng thuận lợi.<br />\r\n- Kh&ocirc;ng gian y&ecirc;n tĩnh, tho&aacute;ng m&aacute;t, cực thoải m&aacute;i sau một ng&agrave;y l&agrave;m việc mệt mỏi.<br />\r\n- Khu để xe rộng r&atilde;i free, c&oacute; camera, kh&oacute;a ra v&agrave;o v&acirc;n tay.</p>\r\n', 32, 0, 0, 'khép kín', 'khu bếp trong phòng', 1, 0, 1, 'điện 3,5k/số, nước 25k/khối', 3700000, 1, '2020-12-22'),
(38, 'Nhà riêng phố Lý Nam Đế', '160886409532.jpg', 10, 4, 3, ' 93, Đường Lý Nam Đế, Phường Cửa Đông', '<p>Nh&agrave; ri&ecirc;ng phố L&yacute; Nam Đế</p>\r\n', '<p>1. M&ocirc; tả:<br />\r\n- Diện t&iacute;ch x&acirc;y: 70m2 x 6 tầng.<br />\r\n- Diện t&iacute;ch cho thu&ecirc;: Tầng 1, 2, 3.<br />\r\n- Mặt tiền rộng: 5m.<br />\r\n- Nh&agrave; c&aacute;ch mặt phố chỉ từ 15 m.<br />\r\n2. Nh&agrave; mới, 2 mặt tho&aacute;ng trước v&agrave; sau, đ&atilde; trang bị đầy đủ c&aacute;c thiết bị cơ bản như: Điều h&ograve;a, thiết bị vệ sinh mới, s&agrave;n gỗ, cầu thang gỗ. Điện 3 pha ri&ecirc;ng c&ocirc;ng tơ 3 tầng, nước đầy đủ.<br />\r\n- Mỗi tầng chia 2 ph&ograve;ng, c&oacute; cửa kh&oacute;a ri&ecirc;ng biệt.<br />\r\n- Tầng 2 c&oacute; cầu thang c&oacute; thể l&ecirc;n từ ngo&agrave;i cổng.<br />\r\n3. Lối đi sử dụng chung tầng 1.<br />\r\n- C&aacute;c ph&ograve;ng cửa kh&oacute;a ri&ecirc;ng biệt.<br />\r\n- Nh&agrave; chỉ c&oacute; 2 người, đi l&agrave;m cả ng&agrave;y.<br />\r\n4. Nh&agrave; thuộc khu d&acirc;n cư văn h&oacute;a, d&acirc;n tr&iacute; cao, an ninh cực kỳ tốt, ph&ugrave; hợp cho c&aacute;c đơn vị thu&ecirc; l&agrave;m văn ph&ograve;ng hoặc kinh doanh spa, đồ online...<br />\r\n- Ng&otilde; to rộng gần 5m, &ocirc; t&ocirc; con c&oacute; thể đỗ cửa.<br />\r\n5. Gi&aacute; thu&ecirc;: Hỗ trợ dịch covid 19 triệu/th&aacute;ng.&nbsp;Gi&aacute; thu&ecirc; sau dịch: gặp trực tiếp đ&agrave;m ph&aacute;n</p>\r\n', 70, 0, 0, 'khép kín', 'khu nấu ăn chung', 1, 1, 1, 'điện nước giá dân', 19000000, 1, '2020-12-22'),
(39, 'Phòng trọ đường Trịnh Công Sơn', '160886407129.jpg', 10, 4, 10, ' 7, Đường Trịnh Công Sơn, Phường Nhật Tân', '<p>Ph&ograve;ng trọ đường Trịnh C&ocirc;ng Sơn</p>\r\n', '<p>Th&ocirc;ng tin ph&ograve;ng:<br />\r\n- Ph&ograve;ng đầy đủ đổ đạc n&oacute;ng lạnh,điều h&ograve;a, c&oacute; ban c&ocirc;ng nhỏ v&agrave; nh&agrave; vệ sinh ngay cạnh ph&ograve;ng.<br />\r\n- C&oacute; khu vực nấu ăn v&agrave; tủ lạnh ở tầng 1 - m&aacute;y giặt ở s&acirc;n thượng.<br />\r\n- Chỗ để xe rộng r&atilde;i, nh&agrave; ở khu d&acirc;n tr&iacute; cao ng&otilde; rộng.<br />\r\n- Ph&ograve;ng tho&aacute;ng, kh&ocirc;ng kh&iacute; trong l&agrave;nh ở Hồ T&acirc;y l&agrave; nơi sinh sống l&yacute; tưởng.</p>\r\n', 25, 0, 0, 'khép kín', 'khu vực nấu ăn chung', 1, 1, 1, 'điện nước giá dân', 3200000, 1, '2020-12-22'),
(40, 'Phòng trọ đường Võ Chí Công', '160886474550.jpg', 11, 4, 10, ' 398, Đường Võ Chí Công, Phường Xuân La', '<p>Ph&ograve;ng trọ đường V&otilde; Ch&iacute; C&ocirc;ng</p>\r\n', '<p>- Nội thất: Điều h&ograve;a, N&oacute;ng lạnh, Đệm, Tủ nhựa, M&aacute;y giặt, H&uacute;t m&ugrave;i, Quạt trần, Tủ lạnh.<br />\r\n-&nbsp;Giờ giấc tự do, mỗi người 1 ch&igrave;a kh&oacute;a, c&oacute; chỗ để xe rộng r&atilde;i miễn ph&iacute;.<br />\r\n-&nbsp;Thuận tiện cho c&aacute;c bạn học tập v&agrave; l&agrave;m việc khu Cầu Giấy, Ho&agrave;ng Quốc Việt, Hồ T&acirc;y...<br />\r\n- Vị tr&iacute;: Gần c&aacute;c trường Học viện B&aacute;o Ch&iacute;, đại học Quốc Gia...</p>\r\n', 15, 0, 0, 'chung với 1 phòng khác', 'khu bếp chung', 1, 0, 1, 'điện giá dân, nước 80k/người', 2800000, 1, '2020-12-23'),
(41, 'Nhà riêng ngõ Trại Cá', '160886471647.jpg', 11, 4, 11, '1B/87, Đường Trương Định, Phường Trương Định', '<p>Nh&agrave; ri&ecirc;ng ng&otilde; Trại C&aacute;</p>\r\n', '<p>- DT: 24m2 x&acirc;y 4 tầng.<br />\r\n+ Tầng 1: Ph&ograve;ng kh&aacute;ch + bếp + 1 VS<br />\r\n+ Tầng 2,3: Mỗi tầng 1 ph&ograve;ng ngủ + *1 VS*.<br />\r\n- Điện nước ri&ecirc;ng biệt, t&iacute;nh theo gi&aacute; nh&agrave; nước.<br />\r\n- Nh&agrave; ngay gần chợ, c&aacute;c trường học. Kinh Tế, B&aacute;ch khoa, X&acirc;y dựng.<br />\r\n- Khu vực an ninh tốt, thuận tiện cho sinh hoạt.</p>\r\n', 28, 0, 0, 'khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 6000000, 1, '2020-12-23'),
(42, 'CCMN Đường Láng', '160886465344.jpg', 11, 4, 2, 'Đường Láng, Phường Trung Liệt', '<p>CCMN Đường L&aacute;ng</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p>Nh&agrave; c&aacute;ch chợ chỉ 10m. Mới x&acirc;y c&oacute; nhiều ph&ograve;ng cho thu&ecirc;, rộng 25m2 v&agrave; 30m2.<br />\r\nNgay cạnh đường L&aacute;ng, đi ng&otilde; 562 đường L&aacute;ng v&agrave;o.<br />\r\n- Ph&ograve;ng 30m2: đủ đồ c&oacute; ban c&ocirc;ng<br />\r\n+ c&oacute; tủ lạnh: 5tr<br />\r\n+ kh&ocirc;ng tủ lạnh: 4tr7<br />\r\n- Ph&ograve;ng 25m2: đủ đồ kh&ocirc;ng ban c&ocirc;ng<br />\r\n+ c&oacute; tủ lạnh: 4tr7<br />\r\n+ kh&ocirc;ng tủ lạnh: 4tr5<br />\r\nC&Oacute; THỂ tự mua tự l&aacute;p tủ lạnh của m&igrave;nh để tiết kiệm 200-300k một th&aacute;ng. Đồ đạc c&ograve;n lại đầy đủ như h&igrave;nh, c&oacute; m&aacute;y giặt chung.<br />\r\n-&nbsp;Gi&aacute; dịch vụ: Điện 3,8k/ số; nước 100k/người; vệ sinh, thang m&aacute;y, điện h&agrave;nh lang, m&aacute;y giặt d&ugrave;ng chung 100k/ người; internet 100k/ ph&ograve;ng.</p>\r\n', 30, 0, 0, 'khép kín', 'nấu ăn trong phòng', 1, 1, 1, 'điện 3,8k/số, nước 100k/người', 4500000, 4, '2020-12-23'),
(43, 'Chung cư Văn Phú - Hà Đông', '160886463341.jpg', 11, 4, 8, 'Đường Lê Trọng Tấn, Phường Quang Trung', '<p>Chung cư Văn Ph&uacute; - H&agrave; Đ&ocirc;ng</p>\r\n', '<p>- Diện t&iacute;ch 70m2 thiết kế 2pn 2wc, c&oacute; thể v&agrave;o ở lu&ocirc;n<br />\r\n-&nbsp;Nội thất sắm đầy đủ: s&agrave;n gỗ, tủ bếp, bếp, điều h&ograve;a, n&oacute;ng lạnh, sofa, b&agrave;n ăn, giường, tủ đồ, nội thất liền tường...<br />\r\n-&nbsp;Tiện &iacute;ch đầy đủ dưới ch&acirc;n t&ograve;a chung cư: si&ecirc;u thị, nh&agrave; h&agrave;ng, quầy thuốc, salon, spa, gym, yoga, bể bơi..;<br />\r\n- Vị tr&iacute;: khu đ&ocirc; thị Văn Ph&uacute; - H&agrave; Đ&ocirc;ng; thuận tiện giao th&ocirc;ng, gần trường học c&aacute;c cấp, bệnh viện, trung t&acirc;m thương mại lớn.</p>\r\n', 70, 0, 0, '2 phòng khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 10000000, 1, '2020-12-23'),
(44, 'Nhà nguyên căn Mỗ Lao', '160886559362.jpg', 12, 4, 8, 'Đường Mỗ Lao, Phường Mộ Lao', '<p>Nh&agrave; nguy&ecirc;n căn Mỗ Lao</p>\r\n', '<p>Cho thu&ecirc; nh&agrave; 31m2 4 tầng bao gồm 4PN, 2 nh&agrave; vệ sinh.<br />\r\n- Nội thất: trang bị 3 điều h&ograve;a, 1 tivi, 1 bộ b&agrave;n ghế, tủ bếp v&agrave; 1 số đồ d&ugrave;ng th&ocirc;ng dụng.<br />\r\n-&nbsp;Mặt tiền 4m, &ocirc; t&ocirc; đỗ cửa, c&aacute;ch mặt đường Trần Ph&uacute; 50m.<br />\r\n- Vị tr&iacute;: gần trường Đại học Kiến Tr&uacute;c, ngay &aacute;p lưng si&ecirc;u thị Điện m&aacute;y Media.<br />\r\n-&nbsp;Rất th&iacute;ch hợp cho thu&ecirc; l&agrave;m văn ph&ograve;ng kinh doanh v&agrave; hộ gia đ&igrave;nh ở.</p>\r\n', 30, 0, 0, '3 phòng khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 11000000, 1, '2020-12-23'),
(45, 'Chung cư Cầu Giấy', '160886557659.jpg', 12, 4, 1, 'Đường Nguyễn Khánh Toàn, Phường Quan Hoa', '<p>Chung cư Cầu Giấy</p>\r\n', '<p>- Tiện &iacute;ch gần trường học bệnh viện , gần chợ cầu giấy,c&ocirc;ng vi&ecirc;n cầu giấy, trường đh gi&aacute;o th&ocirc;ng vận tải ,trường cấp 2,3 dịch vọng, trung t&acirc;m si&ecirc;u thị fivimart v&agrave; vinmart , kh&ocirc;ng gian rộng tho&aacute;ng m&aacute;t văn minh lịch sự , xung quanh c&aacute;c t&ograve;a cao ốc , văn ph&ograve;ng sầm uất bậc nhất cầu giấy.<br />\r\n-&nbsp;Nội thất: s&agrave;n gỗ Malaysia ,b&agrave;n ghế sofa, 1 bộ b&agrave;n ghế ăn , b&agrave;n ghế l&agrave;m việc , bếp điện ,tủ bếp , l&ograve; vi s&oacute;ng , điều h&ograve;a 2 chiều , tủ lạnh lớn , tivi 42 inch , k&eacute;t sắt , tủ quần &aacute;o , giường cỡ lớn k&egrave;m chăn ga gối , m&aacute;y giặt sấy ,buồng tắm đứng , m&aacute;y ph&aacute;t điện , thang m&aacute;y ,bồn tắm Toto, b&igrave;nh n&oacute;ng lạnh.full đồ chỉ cần s&aacute;ch vali v&agrave;o ở.<br />\r\n- Miễn ph&iacute; xe m&aacute;y, truyền h&igrave;nh c&aacute;p , internet , dọn ph&ograve;ng 2 lần / tuần , nước uống 2 b&igrave;nh/ th&aacute;ng , thay chăn ga gối h&agrave;ng tuần , bảo vệ mở cửa 24/24</p>\r\n', 70, 0, 0, 'khép kín', 'thông với phòng khách', 1, 1, 1, 'điện nước giá dân', 13000000, 1, '2020-12-23'),
(46, 'CCMN đường Nghĩa Dũng', '160886553056.jpg', 12, 4, 9, 'Số 33, nghách 27, ngõ 12, Đường Nghĩa Dũng, Phường Phúc Xá', '<p>CCMN đường Nghĩa Dũng</p>\r\n', '<p>To&agrave; nh&agrave; chung cư mini mới x&acirc;y cuối năm 2018 c&oacute; c&aacute;c căn ph&ugrave; hợp cho gia đ&igrave;nh với c&aacute;c tiện &iacute;ch:<br />\r\n- Thang m&aacute;y, để xe tại tầng 1 với camera an ninh.<br />\r\n- C&aacute;c ph&ograve;ng đều c&oacute; ban c&ocirc;ng, được trang bị điều ho&agrave; 2 chiều, wifi tốc độ cao, truyền h&igrave;nh c&aacute;p miễn ph&iacute;.<br />\r\n- Bếp c&oacute; kệ, tủ, h&uacute;t m&ugrave;i.<br />\r\n- M&aacute;y giặt v&agrave; s&acirc;n thượng phơi đồ ri&ecirc;ng biệt.<br />\r\n- Vị tr&iacute; rất ph&ugrave; hợp với cuộc sống, c&ocirc;ng việc tại khu phố cổ, trung t&acirc;m HN. Gần chợ d&acirc;n sinh, s&acirc;n b&oacute;ng đ&aacute; Hồng H&agrave;, viện Medlatec, viện Ho&egrave; Nhai. Chỉ 3p xe m&aacute;y l&agrave; ra đương Thanh Ni&ecirc;n hay sang phố cổ An ninh tốt do gần UBND Phường.<br />\r\n-&nbsp;Cộng đồng gồm c&aacute;c gia đ&igrave;nh văn minh, lịch sự - kh&ocirc;ng c&oacute; sinh vi&ecirc;n hay lao động tự do ở chung.</p>\r\n', 25, 0, 0, 'khép kín', 'nấu ăn trong phòng', 1, 1, 1, 'điện 3,5k/số, nước 25k/khối', 3500000, 1, '2020-12-23'),
(47, 'Phòng trọ Định Công', '160886549553.jpg', 12, 4, 4, ' 10, Nguyễn Cảnh Dị, Phường Định Công', '<p>Ph&ograve;ng trọ Định C&ocirc;ng</p>\r\n', '<p>- Ph&ograve;ng trọ ở&nbsp;trong nh&agrave; ri&ecirc;ng đầy đủ nội thất n&oacute;ng lạnh điều h&ograve;a giường tủ, c&oacute; m&aacute;y lọc nước chung, tho&aacute;ng, y&ecirc;n tĩnh, an ninh tốt<br />\r\n-&nbsp;C&oacute; giảm gi&aacute; cho nếu chuyển v&agrave;o ngay.<br />\r\n- Vị tr&iacute;:<br />\r\n+&nbsp;Giao th&ocirc;ng thuận tiện, oto đỗ cửa, gần c&aacute;c tuyến phố lớn GI&aacute;p B&aacute;t, Định C&ocirc;ng, , L&ecirc; Trọng Tấn, Linh Đ&agrave;m, Nguyễn Xiển ...<br />\r\n+&nbsp;Gần nhiều trường : ĐH Y, HV Ng&acirc;n h&agrave;ng, Thủy Lợi, X&acirc;y Dựng, BV Bạch Mai...</p>\r\n', 18, 2, 0, 'khép kín', 'nấu ăn trong phòng', 1, 0, 1, 'điện 3,5k/số, nước 100k/người', 2500000, 1, '2020-12-23'),
(48, 'Phòng trọ đẹp quận Hoàng Mai', '160886616374.jpg', 13, 4, 4, ' 37 ngõ 61, Đường Bằng Liệt, Phường Hoàng Liệt', '<p>Ph&ograve;ng trọ đẹp quận Ho&agrave;ng Mai</p>\r\n', '<p>T&ograve;a nh&agrave; c&oacute; đầy đủ tiện &iacute;ch:<br />\r\n&bull; Thang m&aacute;y 750kg tốc độ cao<br />\r\n&bull; Tự chủ về giờ giấc đi &ndash;về nhờ c&oacute; cổng kh&oacute;a v&acirc;n tay bảo mật.<br />\r\n&bull; An ninh tốt, camera 24/24 từ cổng =&gt;nh&agrave; xe=&gt; h&agrave;nh lang c&aacute;c tầng.<br />\r\n&bull; Wifi tốc độ cao &bull; Nh&agrave; để xe rất rộng r&atilde;i tại tầng 1<br />\r\n&bull; &Ocirc; t&ocirc; đỗ cổng, ng&otilde; rộng 2 &ocirc; t&ocirc; tr&aacute;nh nhau thoải m&aacute;i.<br />\r\n&bull; Gần chợ: c&aacute;ch 200m<br />\r\nPh&ograve;ng ở được trang bị đầy đủ:<br />\r\n&bull; Giường gỗ sồi 1m6<br />\r\n&bull; Tủ 3 buồng<br />\r\n&bull; Điều h&ograve;a sharp 12000 BTU invester tiết kiệm điện<br />\r\n&bull; Quạt trần<br />\r\n&bull; Kệ bếp<br />\r\n&bull; Gi&agrave;n phơi H&ograve;a ph&aacute;t trong ph&ograve;ng<br />\r\n&bull; Cửa sổ tho&aacute;ng m&aacute;t</p>\r\n', 30, 0, 0, 'khép kín', 'nấu ăn trong phòng', 1, 0, 1, 'điện 3,5k/số, nước 25k/khối', 3000000, 1, '2020-12-24'),
(49, 'Chung cư Hà Thành Plaza', '160886614471.jpg', 13, 4, 2, '102 Đường Thái Thịnh, Phường Thịnh Quang', '<p>Chung cư H&agrave; Th&agrave;nh Plaza</p>\r\n', '<p>- Căn hộ 68m2: 1 kh&aacute;ch, 1 bếp, 1 sảnh, 2 ph&ograve;ng ngủ, 1 toilet, 1 khu phơi.<br />\r\n- Nh&agrave; đ&atilde; sửa đẹp, đủ đồ c&oacute; thể ở được lu&ocirc;n.<br />\r\n- Nằm ở khu Th&aacute;i Thịnh, gần trường MN, cấp 1, cấp 2, gần bệnh viện, chợ đối diện nh&agrave;.<br />\r\n- Khu d&acirc;n tr&iacute; cao, t&ograve;a nh&agrave; c&oacute; 2 thang m&aacute;y, an ninh tốt d&ugrave;ng v&acirc;n tay/thẻ từ để bấm thang m&aacute;y. Độ bảo mật, an ninh rất cao.<br />\r\n- Đồ d&ugrave;ng gia đ&igrave;nh đ&atilde; c&oacute; sẵn: đầy đủ nội thất (thiếu tủ lạnh) Tất cả c&aacute;c đồ đều đang d&ugrave;ng tốt.</p>\r\n', 70, 0, 0, 'khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 9000000, 1, '2020-12-24'),
(50, 'Căn hộ 30m Nguyễn Chí Thanh', '160886612768.jpg', 13, 4, 2, 'Ngõ 76, Đường Nguyễn Chí Thanh, Phường Láng Thượng', '<p>Căn hộ 30m Nguyễn Ch&iacute; Thanh</p>\r\n', '<p>Cho thu&ecirc; căn hộ 30m+8m g&aacute;c x&eacute;p kh&eacute;p k&iacute;n đầy đủ tiện nghi điều h&ograve;a , b&igrave;nh n&oacute;ng lạnh , giường , tủ quầ &aacute;o , tủ lạnh.<br />\r\nCăn hộ th&ocirc;ng tho&aacute;ng m&aacute;t v&agrave;o m&ugrave;a h&egrave; ấm v&agrave;o m&ugrave;a đ&ocirc;ng , c&oacute; chỗ phơi quần &aacute;o ri&ecirc;ng biệt.<br />\r\nVị tr&iacute; trung t&acirc;m c&aacute;ch đường Nguyễn Ch&iacute; thanh 20 m tiện đi lại , điện nước gi&aacute; nh&agrave; nước , giờ giấc tự do kh&ocirc;ng chung chủ ưu ti&ecirc;n thu&ecirc; l&acirc;u d&agrave;i</p>\r\n', 30, 0, 0, 'khép kín', 'khu nấu ăn chung', 1, 1, 1, 'điện 3,5k/số, nước 25k/khối', 4000000, 1, '2020-12-24'),
(51, 'Cho thuê nhà riêng Đức Giang, Long Biên', '160886611065.jpg', 13, 4, 12, 'Đường Đức Giang, Phường Đức Giang', '<p>Cho thu&ecirc; nh&agrave; ri&ecirc;ng Đức Giang, Long Bi&ecirc;n</p>\r\n', '<p>Vị tr&iacute;: Nằm mặt phố Đức Giang, c&aacute;ch mặt đường Ng&ocirc; Gia Tự 200m. Ngay gần c&acirc;y xăng Đức Giang. C&aacute;ch cầu Chương Dương 10 - 15 ph&uacute;t đi xe, thuận tiện cho việc đi lại v&agrave; l&agrave;m việc b&ecirc;n phố.<br />\r\n+ DT đất: 110m2, 80m2/s&agrave;n,Ngang: 4.5m &ndash; Dài 24m Đường trước nhà: 8m xe ôtô đỗ trước nhà, view đẹp, thoáng mát<br />\r\n+ Nhà 3 tầng + sân rộng . Phòng khách: 01 phòng + Bếp: 01 phòng bếp + Phòng ngủ: 04 phòng + Phòng tắm - toilet: 03 phòng</p>\r\n', 110, 0, 0, '3 phòng khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 15000000, 1, '2020-12-24'),
(52, 'Cho thuê nhà 5 tầng mới xây Ngọc Thụy, Long Biên', '160886690686.jpg', 14, 4, 12, 'Đường Ngọc Thụy, Phường Ngọc Thụy', '<p>Cho thu&ecirc; nh&agrave; 5 tầng mới x&acirc;y Ngọc Thụy, Long Bi&ecirc;n</p>\r\n', '<p>Vị tr&iacute;: gần trường Việt Ph&aacute;p 500m, c&aacute;ch Cầu Chương Dương 1,5 km, c&aacute;ch học viện hậu cầu 1km, c&aacute;ch cầu Đ&ocirc;ng Tr&ugrave; 1km, nằm c&aacute;ch trường Cấp 1 Ngọc Thụy 1km ... Tiện &iacute;ch: L&agrave; khu d&acirc;n tr&iacute; cao, gần chợ, thuận tiện l&agrave;m viện b&ecirc;n Nội th&agrave;nh ... &Ocirc; t&ocirc; đỗ cửa<br />\r\nDiện t&iacute;ch: 40 m/s&agrave;n. Gồm 5 tầng: 3 ph&ograve;ng ngủ<br />\r\nNội thất đồ cơ bản: S&agrave;n gỗ, Tủ bếp, điều h&ograve;a, n&oacute;ng lạnh, b&agrave;n ghế, giường, tủ quần &aacute;o, quạt trần...</p>\r\n', 40, 0, 0, '3 phòng khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 10000000, 1, '2020-12-24'),
(53, 'Căn hộ tập thể tầng 3 tại Nguyên Hồng', '160886689183.jpg', 14, 4, 9, 'Ngõ 9 Nguyên Hồng nhà D4 Thành Công, phường Thành Công', '<p>Căn hộ tập thể tầng 3 tại Nguy&ecirc;n Hồng</p>\r\n', '<p>Căn hộ tiện sinh hoạt v&agrave; d&acirc;n tr&iacute; cao. L&agrave; căn hộ tập thể của cơ quan Viện Dầu kh&iacute; x&acirc;y v&agrave; ph&acirc;n cho CBCNV n&ecirc;n tho&aacute;ng m&aacute;t.<br />\r\nC&aacute;c ph&ograve;ng đều c&oacute; cửa sổ tổng diện t&iacute;ch 90m2.<br />\r\nNh&agrave; đ&atilde; sửa lại khang trang, cho thu&ecirc; l&acirc;u d&agrave;i thiết kế 7 ph&ograve;ng v&agrave; 1 ph&ograve;ng vệ sinh, 1 bếp, &ocirc; t&ocirc; đỗ cửa.<br />\r\nVị tr&iacute; rất tiện giao th&ocirc;ng, gần trường học tốt, chợ to v&agrave; nhiều đồ ăn ngon, gần c&aacute;c bệnh viện lớn.<br />\r\nRất th&iacute;ch hợp cho hộ gia đ&igrave;nh nhiều thế hệ, hoặc người đi l&agrave;m ở chung, ở gh&eacute;p, điện nước gi&aacute; nh&agrave; nước...</p>\r\n', 90, 0, 0, 'khép kín', 'khép kín', 1, 1, 1, 'điện nước giá dân', 8500000, 1, '2020-12-24'),
(54, 'Phòng Gác Xếp Đủ Đồ Yên Hòa Cầu Giấy', '160886687380.jpg', 14, 4, 1, ' 65B, Đường Yên Hòa, Phường Yên Hoà', '<p>Ph&ograve;ng G&aacute;c Xếp Đủ Đồ Y&ecirc;n H&ograve;a Cầu Giấy</p>\r\n', '<p>- Có đầy đủ: Điều hoà, nóng lạnh, tủ quần áo, bàn ghế, kệ bếp và tủ bếp,...<br />\r\n- Phòng mới đẹp, có ban công phơi phóng thoáng mát<br />\r\n- Chỗ để xe rộng rãi và miễn phí<br />\r\n- Khóa cổng hiện đại bằng vân tay- an ninh tốt<br />\r\n- Khu vực trung tâm, gần các siêu thị, nhà hàng, chợ, ... =&gt; Cạnh các tuyến phố lớn Đường Láng, Nguyễn Khang, Trung Kính Gần các trường đại học GTVT, Ngoại Thương, Luật, Báo Chí, Thanh Thiếu Niên, Quốc Gia, Sư Phạm</p>\r\n', 28, 0, 0, 'khép kín', 'nấu ăn trong phòng', 1, 1, 1, 'điện 3,5k/số, nước 25k/khối', 3400000, 1, '2020-12-24'),
(55, 'CHO THUÊ CHUNG CƯ MINI MỚI XÂY', '160886685677.jpg', 14, 4, 8, 'ngõ 197/77 Trần Phú, phường Văn Quán', '<p>CHO THU&Ecirc; CHUNG CƯ MINI MỚI X&Acirc;Y</p>\r\n', '<p>☘ Diện t&iacute;ch: 20 - 25m2<br />\r\n☘ Gi&aacute; từ 3tr5</p>\r\n\r\n<p>- C&oacute; thang m&aacute;y<br />\r\n- Nội thất: giường, tủ, m&aacute;y giặt, kệ b&agrave;n tủ bếp nấu ăn, điều h&ograve;a, n&oacute;ng lạnh,...<br />\r\n- Thiết kế theo phong c&aacute;ch H&agrave;n Quốc: r&egrave;m cửa, b&agrave;n ghế, tranh ảnh,...<br />\r\n- Gần c&aacute;c trường đại học Kiến Tr&uacute;c, Bưu Ch&iacute;nh, HVAN, YHCT, Big C H&agrave; Đ&ocirc;ng, giao th&ocirc;ng thuận tiện,c&oacute; điểm bus tại đầu ng&otilde;.</p>\r\n', 20, 0, 0, 'khép kín', 'nấu ăn trong phòng', 1, 0, 1, 'điện 3,5k/số, nước 30k/khối', 3500000, 3, '2020-12-24'),
(56, 'Chung cư Hyundai Hillstate', '160886752998.jpg', 15, 4, 8, 'Đường Tô Hiệu, Phường Hà Cầu', '<p>Chung cư Hyundai Hillstate</p>\r\n', '<p>Do chuyển đi nơi kh&aacute;c n&ecirc;n gia đ&igrave;nh t&ocirc;i cần cho thu&ecirc; gấp căn hộ chung cư Hyundai Hillstate, H&agrave; Đ&ocirc;ng, H&agrave; Nội.<br />\r\n- Căn hộ 105. thiết kế 2 ph&ograve;ng ngủ, 2 vệ sinh, ph&ograve;ng kh&aacute;ch v&agrave; bếp rất rộng, ban c&ocirc;ng tho&aacute;ng m&aacute;t nh&igrave;n ra trục đường lớn.<br />\r\n- Nội thất đầy đủ, tiện nghi: N&oacute;ng lạnh, điều h&ograve;a, s&agrave;n gỗ, tủ bếp, giường tủ, b&agrave;n ghế, m&aacute;y giặt, tủ lạnh, r&egrave;m, sofa...<br />\r\nKh&aacute;ch thu&ecirc; chỉ việc x&aacute;ch vali v&agrave;o ở.</p>\r\n', 105, 0, 0, '2 phòng khép kín', 'thông với phòng khách', 1, 1, 1, 'điện nước giá dân', 11000000, 1, '2020-12-24'),
(57, 'Nhà nguyên căn Mễ Trì', '160886751695.jpg', 15, 4, 5, 'ngõ 98 Mễ Trì Thượng, phường Mễ Trì', '<p>Nh&agrave; nguy&ecirc;n căn Mễ Tr&igrave;</p>\r\n', '<p>Nh&agrave; diện t&iacute;ch 45m, 3 tầng, tầng 1 ph&ograve;ng kh&aacute;ch bếp, tầng 2 2 ph&ograve;ng ngủ, tầng 1 ph&ograve;ng v&agrave; s&acirc;n thượng<br />\r\n- Nội thất đầy đủ , tivi, tủ lạnh, bếp từ hồng ngoại, tủ bếp đầy đủ -Giường tủ, điều h&ograve;a, n&oacute;ng lạnh , c&oacute; b&igrave;nh th&aacute;i dương năng rất tiết kiệm chi ph&iacute; n&oacute;ng lạnh<br />\r\n- Ph&ograve;ng tắm n&oacute;ng lạnh nội thất toto, cửa ra v&agrave;o s&agrave;n nh&agrave; cầu thang gỗ lim chắc chắn<br />\r\n- Tiện đi lại gần kh&aacute;ch sạn Marriott, gần BigC Thăng Long&hellip;vv<br />\r\n- Hợp với cho thu&ecirc; hộ gia đ&igrave;nh chỉ cần mang quần &aacute;o đến ở.</p>\r\n', 45, 0, 0, '3 phòng khép kín', 'khu bếp chung với phòng khách', 1, 1, 1, 'điện nước giá dân', 8000000, 1, '2020-12-24'),
(58, 'Phòng trọ tại Lê Trong Tấn', '160886749892.jpg', 15, 4, 7, '192 Lê Trọng Tấn, phường Khương Mai', '<p>Ph&ograve;ng trọ tại L&ecirc; Trong Tấn</p>\r\n', '<p>- Nội thất: C&oacute; điều ho&agrave;, tivi, n&oacute;ng lạnh, m&aacute;y giặt, giường tủ, b&agrave;n ghế, b&agrave;n phấn, quạt treo tường, r&egrave;m cửa...<br />\r\n- Gần bệnh viện Bưu Điện, ĐH Y, c&aacute;ch B&aacute;ch Kinh X&acirc;y 1km, c&aacute;ch ng&atilde; tư sở 1km, bến xe gi&aacute;p b&aacute;t 1,2km</p>\r\n', 22, 0, 0, 'khép kín', 'khu nấu ăn chung', 1, 1, 1, 'điện 3,5k/số, nước 25k/khối', 3200000, 1, '2020-12-24'),
(59, 'CHO THUÊ CCMN PHẠM VĂN ĐỒNG, XUÂN ĐỈNH', '160886747289.jpg', 15, 4, 6, '251 Phạm Văn Đồng, Phường Xuân Đỉnh', '<p>CHO THU&Ecirc; CCMN PHẠM VĂN ĐỒNG, XU&Acirc;N ĐỈNH</p>\r\n', '<p>Diện t&iacute;ch ph&ograve;ng: 40m2, c&oacute; g&aacute;c x&eacute;p, c&oacute; thể bố tr&iacute; 2 giường ngủ, c&oacute; bếp nấu ăn ri&ecirc;ng, cửa sổ , ban c&ocirc;ng tho&aacute;ng.<br />\r\nPh&ograve;ng mới, đ&atilde; trang bị đầy đủ nội thất: tủ quần áo, b&agrave;n ghế, điều hoà, bàn bếp nấu ăn chậu rửa.<br />\r\nTh&iacute;ch hợp cho sinh vi&ecirc;n, hộ gia đ&igrave;nh nhỏ thu&ecirc;, chỉ việc x&aacute;ch vali dọn v&agrave;o sẵn ở.<br />\r\nNằm ở khu tập trung nhiều trường đại học, c&aacute;c tiện &iacute;ch x&atilde; hội, thương mại đầy đủ: si&ecirc;u thị, chợ, c&ocirc;ng vi&ecirc;n H&ograve;a B&igrave;nh&hellip; đảm bảo thuận tiện cho sinh hoạt, học tập, c&ocirc;ng t&aacute;c. Ng&otilde; rộng, &ocirc; t&ocirc; v&agrave;o được.<br />\r\nKhu d&acirc;n cư d&acirc;n tr&iacute; cao, camera an ninh, kho&aacute; v&acirc;n tay đảm bảo an to&agrave;n. Vệ sinh hàng tuần sạch sẽ.<br />\r\nKhông chung chủ giờ giấc tự do, nh&agrave; xe rộng tầng 1 miễn phí.</p>\r\n', 40, 0, 0, 'khép kín', 'khu nấu ăn thông với phòng khách', 1, 1, 1, 'điện nước giá dân', 3500000, 1, '2020-12-24');

